INSERT INTO `tbl_product` (`product_id`, `product_code`, `product_name`, `product_price`, `old_price`, `product_desc`, `content`, `qty`, `slug`, `creator`, `editor`, `product_thumb`, `product_status`, `add_date`, `update_date`, `cat_id`, `child_id`) VALUES
(1, '#IMSS1', 'Samsung Galaxy Note 20', 32999000, 35990000, '<p>Màn hình 6.9\", Chip Exynos 990</p>\r\n\r\n<p>RAM 12 GB, ROM 256 GB</p>\r\n\r\n<p>Camera sau: Chính 108 MP & Phụ 12 MP, 12 MP, cảm biến Laser AF</p>\r\n\r\n<p>Camera trước: 10 MP</p>\r\n\r\n<p>Pin 4500 mAh, Sạc 25 W</p>\r\n', '<h3><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-note-20-Ultra-5g\" target=\"_blank\" title=\"Tham khảo giá Samsung Galaxy Note 20 Ultra 5G\">Galaxy Note 20 Ultra 5G</a>, m&acirc;̃u&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo giá điện thoại chính hãng\">đi&ecirc;̣n thoại</a>&nbsp;flagship cao c&acirc;́p thu&ocirc;̣c dòng Note của&nbsp;<a href=\"https://www.thegioididong.com/samsung\" target=\"_blank\" title=\"Tham khảo sản phẩm Samsung chính hãng tại Thegioididong.com\">Samsung</a>, ra mắt th&aacute;ng 8/2020 với diện mạo thay đổi cùng những n&acirc;ng cấp đột ph&aacute;, đ&acirc;y chắc chắn sẽ là sản ph&acirc;̉m được săn l&ugrave;ng từ những người d&ugrave;ng y&ecirc;u thích c&ocirc;ng nghệ.</h3>\r\n\r\n<h3>Thi&ecirc;́t k&ecirc;́ mạnh mẽ nhưng v&ocirc; c&ugrave;ng sang trọng</h3>\r\n\r\n<p>Thi&ecirc;́t k&ecirc;́ Galaxy Note 20&nbsp;Ultra 5G được hoàn thi&ecirc;̣n từ khung kim loại cao c&acirc;́p với 2 mặt k&iacute;nh cường lực được l&agrave;m cong bóng b&acirc;̉y, mặt k&iacute;nh do Gorilla phát tri&ecirc;̉n mang lại khả năng ch&ocirc;́ng tr&acirc;̀y cực t&ocirc;́t, ch&ocirc;́ng bám bụi b&acirc;̉n và m&ocirc;̀ h&ocirc;i tay hi&ecirc;̣u quả.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/225063/samsung-galaxy-note-20-ultra-5g2-1.jpg\" onclick=\"return false;\"><img alt=\"Cận cảnh mặt lưng chiếc điện thoại Samsung Galaxy Note 20 Ultra 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/225063/samsung-galaxy-note-20-ultra-5g2-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/225063/samsung-galaxy-note-20-ultra-5g2-1.jpg\" title=\"Cận cảnh mặt lưng chiếc điện thoại Samsung Galaxy Note 20 Ultra 5G\" /></a></p>\r\n\r\n<p>M&aacute;y được thi&ecirc;́t k&ecirc;́ khá vu&ocirc;ng vức, các đường vi&ecirc;̀n của máy được vu&ocirc;́t cong m&ocirc;̣t cách nhẹ nhàng đem đ&ecirc;́n sự sang trọng nhưng mạnh mẽ, b&ecirc;n cạnh đó, Samsung đã cắt giảm các chi ti&ecirc;́t kh&ocirc;ng c&acirc;̀n thi&ecirc;́t ở mặt sau giúp làm n&ocirc;̉i b&acirc;̣t th&ecirc;m cụm camera hình chữ nh&acirc;̣t đ&ocirc;̣c đáo.</p>\r\n\r\n<h3>Màn hình Infinity-O với vi&ecirc;̀n cực mỏng</h3>\r\n\r\n<p>Galaxy Note 20 Ultra 5G trang bị t&acirc;́m n&ecirc;̀n Dynamic AMOLED 2X, màn hình v&ocirc; cực 6.9 inch h&ocirc;̃ trợ đ&ocirc;̣ ph&acirc;n giải 2K+ đảm bảo hình ảnh hi&ecirc;̉n thị sắc nét, màu sắc rực rỡ, bắt mắt.</p>\r\n\r\n<p>M&agrave;n h&igrave;nh của m&aacute;y c&ograve;n gi&uacute;p hạn chế mỏi mắt khi sử dụng về đ&ecirc;m, gi&uacute;p bảo vệ mắt tốt hơn nhờ c&ocirc;ng nghệ Dynamic AMOLED 2X. Mặt k&iacute;nh cảm ứng của m&aacute;y l&agrave;&nbsp;Gorilla Glass Victus&nbsp;với khả năng giảm thiểu trầy xước tốt, chắc chắn v&agrave; bền bỉ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/225063/samsung-galaxy-note-20-ultra-5g3-1.jpg\" onclick=\"return false;\"><img alt=\"Màn hình hiển thị siêu rõ nét của điện thoại Samsung Galaxy Note 20 Ultra 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/225063/samsung-galaxy-note-20-ultra-5g3-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/225063/samsung-galaxy-note-20-ultra-5g3-1.jpg\" title=\"Màn hình hiển thị siêu rõ nét của điện thoại Samsung Galaxy Note 20 Ultra 5G\" /></a></p>\r\n\r\n<h3>Cụm camera h&agrave;ng đầu với nhi&ecirc;̀u cải ti&ecirc;́n</h3>\r\n\r\n<p>Ở mặt sau, Note 20 Ultra g&acirc;y &acirc;́n tượng với người dùng với cụm camera khá lớn nằm ở góc tr&ecirc;n cùng b&ecirc;n trái. Tuy nhi&ecirc;n đ&ecirc;̉ th&acirc;̉m mỹ hơn, Samsung đã b&ocirc;̉ sung các vi&ecirc;̀n vòng tròn xung quanh 3 camera chính, giúp cụm camera trở n&ecirc;n tinh t&ecirc;́ hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/225063/samsung-galaxy-note-20-ultra-5g1-1.jpg\" onclick=\"return false;\"><img alt=\"Camera khủng của điện thoại Samsung Galaxy Note 20 Ultra 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/225063/samsung-galaxy-note-20-ultra-5g1-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/225063/samsung-galaxy-note-20-ultra-5g1-1.jpg\" title=\"Camera khủng của điện thoại Samsung Galaxy Note 20 Ultra 5G\" /></a></p>\r\n\r\n<p>Trong đó, cảm bi&ecirc;́n chính của máy có đ&ocirc;̣ ph&acirc;n giải l&ecirc;n đ&ecirc;́n 108 MP, đi cùng là m&ocirc;̣t camera góc cực r&ocirc;̣ng 12 MP giúp thu vào bức ảnh nhi&ecirc;̀u cảnh v&acirc;̣t hơn. Camera tele 12 MP cho khả năng zoom l&ecirc;n đ&ecirc;́n 50x và kèm theo là&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/sieu-on-dinh-super-steady-tren-galaxy-la-gi-1232363\" target=\"_blank\" title=\"Tìm hiểu công nghệ chống rung Super Steady trên điện thoại Samsung\">c&ocirc;ng ngh&ecirc;̣ si&ecirc;u ch&ocirc;́ng rung Super Steady</a>, thích hợp cho những bu&ocirc;̉i du lịch, dạo ph&ocirc;́.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/samsung-galaxy-note-20-ultra-5g-220420-030449.jpg\" />Ảnh chụp&nbsp;g&oacute;c thường 1x tr&ecirc;n Galaxy Note 20 Ultra 5G</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/samsung-galaxy-note-20-ultra-5g-220420-030454.jpg\" />Ảnh chụp&nbsp;zoom 5x tr&ecirc;n Galaxy Note 20 Ultra 5G</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/samsung-galaxy-note-20-ultra-5g-220420-030459.jpg\" />Ảnh chụp&nbsp;zoom 10x tr&ecirc;n Galaxy Note 20 Ultra 5G</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>B&ecirc;n cạnh cụm camera là đèn flash LED, cảm bi&ecirc;́n 3D ToF và micro phụ h&ocirc;̃ trợ quay video giúp m&aacute;y c&oacute; thể quay được những video ch&acirc;́t lượng, ch&acirc;n thực s&ocirc;́ng đ&ocirc;̣ng nh&acirc;́t.</p>\r\n\r\n<p>Camera trước có đ&ocirc;̣ ph&acirc;n giải 10 MP, cung c&acirc;́p đ&acirc;̀y đủ tính năng làm đẹp AI, ch&acirc;n dung,... cho ảnh chụp sắc n&eacute;t, s&ocirc;́ng đ&ocirc;̣ng cũng như mang lại những cuộc gọi video ch&acirc;́t lượng cao.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/samsung-galaxy-note-20-ultra-5g-230520-030525.jpg\" />Ảnh selfie tr&ecirc;n Galaxy Note 20 Ultra</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/225063/samsung-galaxy-note-20-ultra-5g-230520-030538.jpg\" />Ảnh selfie tr&ecirc;n Galaxy Note 20 Ultra</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Hi&ecirc;̣u năng c&acirc;́u hình v&ocirc; cùng mạnh mẽ</h3>\r\n\r\n<p>Cung c&acirc;́p sức mạnh cho máy là chip xử lý Exynos 990 8 nh&acirc;n mới nh&acirc;́t từ Samsung. Chipset này được đánh giá là cho hi&ecirc;̣u năng vượt tr&ocirc;̣i và chỉ được trang bị tr&ecirc;n các m&acirc;̃u flagship cao c&acirc;́p, nhờ v&acirc;̣y n&ecirc;n mọi tác vụ tr&ecirc;n chi&ecirc;́c smartphone này đ&ecirc;̀u được xử lý m&ocirc;̣t cách nhanh chóng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/225063/samsung-galaxy-note-20-ultra-5g-231220-031249.jpg\" onclick=\"return false;\"><img alt=\"Trang bị vi xử lý Exynos 990 | Galaxy Note 20 Ultra 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/225063/samsung-galaxy-note-20-ultra-5g-231220-031249.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/225063/samsung-galaxy-note-20-ultra-5g-231220-031249.jpg\" title=\"Trang bị vi xử lý Exynos 990 | Galaxy Note 20 Ultra 5G\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đó, Galaxy Note n&agrave;y còn đi cùng với RAM 12 GB cùng b&ocirc;̣ nhớ trong 256 GB, mang lại khả năng đa nhiệm v&agrave; lưu trữ h&agrave;ng đầu hiện nay.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/225063/samsung-galaxy-note-20-ultra-5g4-1.jpg\" onclick=\"return false;\"><img alt=\"Test bút S Pen trên điện thoại Samsung Galaxy Note 20 Ultra 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/225063/samsung-galaxy-note-20-ultra-5g4-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/225063/samsung-galaxy-note-20-ultra-5g4-1.jpg\" title=\"Test bút S Pen trên điện thoại Samsung Galaxy Note 20 Ultra 5G\" /></a></p>\r\n\r\n<p>M&aacute;y đáp ứng t&ocirc;́t nhu c&acirc;̀u trải nghi&ecirc;̣m xem phim, lướt web, chơi các tựa game ph&ocirc;̉ bi&ecirc;́n hi&ecirc;̣n nay như&nbsp;<a href=\"https://www.thegioididong.com/game-app/garena-lien-quan-mobile-thang-bai-tai-ky-nang-219924\" target=\"_blank\" title=\"Garena Liên Quân Mobile - Thắng Bại Tại Kỹ Năng\">Li&ecirc;n Qu&acirc;n Mobile</a>,&nbsp;<a href=\"https://www.thegioididong.com/game-app/pubg-mobile-vn-winner-winner-chicken-dinner-219952\" target=\"_blank\" title=\"PUBG MOBILE VN - Winner Winner Chicken Dinner\">PUBG Mobile</a>&nbsp;h&ecirc;́t sức d&ecirc;̃ dàng, thậm ch&iacute; với cấu h&igrave;nh như n&agrave;y th&igrave; c&ograve;n gi&uacute;p&nbsp;<a href=\"https://www.thegioididong.com/dtdd-choi-game\" target=\"_blank\" title=\"Tham khảo điện thoại chơi game - cấu hình cao tại Thegioididong.com \">điện thoại chơi game</a>&nbsp;nặng hơn c&oacute; mức đồ hoạ cao cũng kh&ocirc;ng thể l&agrave;m kh&oacute; si&ecirc;u phẩm n&agrave;y.</p>\r\n\r\n<h3>B&uacute;t S Pen được cải tiến nhiều t&iacute;nh năng, giảm thiểu độ trễ</h3>\r\n\r\n<p>Bút S-Pen tr&ecirc;n Note 20 Ultra 5G kh&ocirc;ng thay đ&ocirc;̉i kích thước, tuy nhi&ecirc;n vị trí bút S-Pen được thay đổi vị tr&iacute; đặt ở b&ecirc;n trái của c&ocirc;̉ng sạc, ngo&agrave;i c&aacute;c chức năng cũ th&igrave; b&uacute;t c&ograve;n trang bị th&ecirc;m c&aacute;c t&iacute;nh năng điều khiển bằng cử chỉ th&ocirc;ng minh nhờ v&agrave;o loạt cảm biến mới được trang bị ở mặt trước.</p>\r\n\r\n<p>Ngo&agrave;i ra, độ trễ của b&uacute;t cũng được giảm thiểu đến mức ấn tượng chỉ c&ograve;n 9 ms, mang đến trải nghiệm viết mượt m&agrave; như viết tr&ecirc;n giấy, đ&acirc;y l&agrave; một trong những cải tiến mạnh mẽ tr&ecirc;n Note 20 Ultra 5G.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/225063/samsung-galaxy-note-20-ultra-5g6-1.jpg\" onclick=\"return false;\"><img alt=\"Bút S Pen đi kèm với điện thoại Samsung Galaxy Note 20 Ultra 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/225063/samsung-galaxy-note-20-ultra-5g6-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/225063/samsung-galaxy-note-20-ultra-5g6-1.jpg\" title=\"Bút S Pen đi kèm với điện thoại Samsung Galaxy Note 20 Ultra 5G\" /></a></p>\r\n\r\n<p>Cung c&acirc;́p năng lượng cho máy là vi&ecirc;n pin 4500 mAh đ&aacute;p ứng thoải mái nhu cầu sử dụng trong ngày, b&ecirc;n cạnh đó Galaxy Note 20 Ultra 5G c&ograve;n đi kèm với t&iacute;nh năng sạc nhanh qua c&ocirc;̉ng USB Type-C giúp rút ngắn đ&aacute;ng kể thời gian sạc.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/225063/samsung-galaxy-note-20-ultra-5g-214720-114746.jpg\" onclick=\"return false;\"><img alt=\"Sở hữu pin lớn, hỗ trợ sạc nhanh - Samsung Galaxy Note 20 Ultra\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/225063/samsung-galaxy-note-20-ultra-5g-214720-114746.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/225063/samsung-galaxy-note-20-ultra-5g-214720-114746.jpg\" title=\"Sở hữu pin lớn, hỗ trợ sạc nhanh - Samsung Galaxy Note 20 Ultra\" /></a></p>\r\n\r\n<p>Nh&igrave;n chung, Galaxy Note 20 Ultra 5G nổi bật với camera được thi&ecirc;́t k&ecirc;́ &acirc;́n tượng, m&agrave;u sắc mới sang trọng, đẹp mắt, theo đ&oacute; l&agrave; m&agrave;n h&igrave;nh màu sắc hi&ecirc;̉n thị chất lượng, hơn nữa, với b&uacute;t S Pen cũng được thay đổi hỗ trợ nhiều t&iacute;nh năng điều khiển th&ocirc;ng minh, sẵn s&agrave;ng mang đến trải nghiệm cao cấp v&agrave; tiện &iacute;ch.</p>\r\n', 2, 'dien-thoai-samsung-galaxy-note-20-ultra-5g-1.html', 'Đàm Tống', '', 'https://cdn.tgdd.vn/Products/Images/42/225063/sam-sung-note-20-ultra-vang-dong-600x600.jpg', '2', 1625580029, 1642752655, 1, 2),
(2, '#IMIP2', 'iPhone SE 128GB (2020) ', 11990000, NULL, '<p>M&agrave;n h&igrave;nh 4.7&quot;, Chip Apple A13 Bionic</p>\r\n\r\n<p>RAM 3 GB, ROM 128 GB</p>\r\n\r\n<p>Camera sau: 12 MP</p>\r\n\r\n<p>Camera trước: 7 MP</p>\r\n\r\n<p>Pin 1821 mAh, Sạc 18 W</p>\r\n', '<h3><a href=\"https://www.thegioididong.com/dtdd/iphone-se-128gb-2020\" target=\"_blank\" title=\"Tham khảo giá iPhone SE 2020 128 GB chính hãng tại Thegioididong.com\" type=\"Tham khảo giá iPhone SE 2020 128 GB chính hãng tại Thegioididong.com\">iPhone SE 128GB (2020)</a>&nbsp;cũng đ&atilde; được cho ra mắt l&agrave;m thỏa m&atilde;n triệu t&iacute;n đồ T&aacute;o khuyết. M&aacute;y sở hữu thiết kế si&ecirc;u nhỏ gọn như&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-8-64gb\" target=\"_blank\" title=\"Tham khảo giá điện thoại iPhone 8 chính hãng tại Thegioididong.com\" type=\"Tham khảo giá điện thoại iPhone 8 chính hãng tại Thegioididong.com\">iPhone 8</a>, chip A13 Bionic cho hiệu năng khủng như&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-11-128gb\" target=\"_blank\" title=\"Tham khảo giá điện thoại iPhone 11 chính hãng tại Thegioididong.com\" type=\"Tham khảo giá điện thoại iPhone 11 chính hãng tại Thegioididong.com\">iPhone 11</a>, nhưng iPhone SE 2020 lại c&oacute; một mức gi&aacute; tốt đến bất ngờ.</h3>\r\n\r\n<h3>Thiết kế nhỏ gọn trong l&ograve;ng b&agrave;n tay.</h3>\r\n\r\n<p>Kh&ocirc;ng nằm ngo&agrave;i mọi đồn đo&aacute;n, iPhone SE 2020 mang tr&ecirc;n m&igrave;nh h&igrave;nh d&aacute;ng của iPhone 8 c&aacute;ch đ&acirc;y 3 năm, vẫn mặt lưng bằng&nbsp;<a href=\"https://www.thegioididong.com/dtdd?g=kim-loai-va-kinh\" target=\"_blank\" title=\"Tham khảo các mẫu điện thoại có chất liệu kính và kim loại tại Thegioididong.com\" type=\"Tham khảo các mẫu điện thoại có chất liệu kính và kim loại tại Thegioididong.com\">k&iacute;nh v&agrave; khung kim loại</a>,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-bao-mat-van-tay\" target=\"_blank\" title=\"Tham khảo các mẫu điện thoại có hỗ trợ mở khóa cảm biến vân tay tại Thegioididong.com\" type=\"Tham khảo các mẫu điện thoại có hỗ trợ mở khóa cảm biến vân tay tại Thegioididong.com\">cảm biến v&acirc;n tay</a>&nbsp;touch ID huyền thoại đ&atilde; quay trở lại v&agrave; nằm ở vị tr&iacute; quen thuộc.</p>\r\n\r\n<p>Đối với những ai ưa th&iacute;ch sự nhỏ gọn, đặc biệt l&agrave; c&aacute;c bạn nữ th&igrave; iPhone SE l&agrave; một sự lựa chọn l&yacute; tưởng. K&iacute;ch thước nhỏ gọn kh&ocirc;ng qu&aacute; to, gi&uacute;p cho iPhone SE 2020 dễ d&agrave;ng cầm nắm v&agrave; sử dụng bằng 1 tay hay nh&eacute;t v&agrave;o t&uacute;i quần dễ d&agrave;ng.</p>\r\n\r\n<p>Giống với iPhone 8 đến 99%, nhưng bạn vẫn c&oacute; thể ph&acirc;n biệt 2 model n&agrave;y th&ocirc;ng qua logo t&aacute;o khuyết ở mặt lưng, nếu ở thiết bị tiền nhiệm tr&aacute;i t&aacute;o đặt hơi lệch ở ph&iacute;a tr&ecirc;n th&igrave; đến iPhone SE 2020 biểu tượng n&agrave;y đ&atilde; dời ngay ch&iacute;nh giữa.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/222629/iphone-se-128gb-20201.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Apple iPhone SE 2020 | Thiết kế nhỏ gọn đi ngược xu hướng \" data-src=\"https://cdn.tgdd.vn/Products/Images/42/222629/iphone-se-128gb-20201.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/222629/iphone-se-128gb-20201.jpg\" title=\"Điện thoại Apple iPhone SE 2020 | Thiết kế nhỏ gọn đi ngược xu hướng \" /></a></p>\r\n\r\n<p>Cũng giống như c&aacute;c thế hệ iPhone gần đ&acirc;y, iPhone SE được trang bị khả năng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-chong-nuoc-bui\" target=\"_blank\" title=\"Tham khảo các mẫu điện thoại chống nước chống bụi tại Thegioididong.com\" type=\"Tham khảo các mẫu điện thoại chống nước chống bụi tại Thegioididong.com\">kh&aacute;ng nước bụi</a>&nbsp;IP67 c&ugrave;ng t&iacute;nh năng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-khong-day\" target=\"_blank\" title=\"Tham khảo điện thoại sạc không dây tại Thegioididong.com\">sạc kh&ocirc;ng d&acirc;y</a>&nbsp;kh&aacute; hữu &iacute;ch trong nhiều điều kiện sử dụng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/222629/iphone-se-128gb-2020-205920-125935.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Apple iPhone SE 2020 | Chống nước, kháng bụi IP67\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/222629/iphone-se-128gb-2020-205920-125935.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/222629/iphone-se-128gb-2020-205920-125935.jpg\" title=\"Điện thoại Apple iPhone SE 2020 | Chống nước, kháng bụi IP67\" /></a></p>\r\n\r\n<p>Ở mặt trước, m&aacute;y trang bị một m&agrave;n h&igrave;nh Retina 4.7 inch, tấm nền IPS cho m&agrave;u sắc ch&iacute;nh x&aacute;c, tươi tắn ở nhiều g&oacute;c độ, d&ugrave; độ ph&acirc;n giải chỉ đạt HD nhưng vẫn cho ra h&igrave;nh ảnh r&otilde; n&eacute;t với chi tiết mịn m&agrave;ng m&agrave; kh&ocirc;ng giảm đi chất lượng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/222629/iphone-se-128gb-20206.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Apple iPhone SE 2020 | Chất lượng hiển thị rõ nét trung thực dù độ phân giải chỉ HD\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/222629/iphone-se-128gb-20206.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/222629/iphone-se-128gb-20206.jpg\" title=\"Điện thoại Apple iPhone SE 2020 | Chất lượng hiển thị rõ nét trung thực dù độ phân giải chỉ HD\" /></a></p>\r\n\r\n<h3>Cấu h&igrave;nh &ldquo;kh&ocirc;ng đối thủ&rdquo; trong tầm gi&aacute;</h3>\r\n\r\n<p>Tr&aacute;i ngược với vẻ ngo&agrave;i nhỏ gọn, th&igrave; trang bị phần cứng b&ecirc;n trong iPhone SE 2020 cực kỳ mạnh mẽ với chip A13 Bionic cho hiệu năng v&ocirc; c&ugrave;ng ấn tượng. Với tầm gi&aacute; n&agrave;y, kh&oacute; một chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd?g=android\" target=\"_blank\" title=\"Tham khảo điện thoại Android chính hãng, giá tốt tại Thegioididong.com\" type=\"Tham khảo điện thoại Android chính hãng, giá tốt tại Thegioididong.com\">điện thoại Android</a>&nbsp;hiện nay c&oacute; thể đ&aacute;nh bại.</p>\r\n\r\n<p>Sở hữu vi xử l&yacute; c&oacute; tr&ecirc;n bộ 3 iPhone 11,&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-11-pro\" target=\"_blank\" title=\"Tham khảo điện thoại iPhone 11 Pro chính hãng tại Thegioididong.com\" type=\"Tham khảo điện thoại iPhone 11 Pro chính hãng tại Thegioididong.com\">iPhone 11 Pro</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-11-pro-max\" target=\"_blank\" title=\"Tham khảo giá điện thoại iPhone 11 Pro Max chính hãng tại Thegioididong.com\" type=\"Tham khảo giá điện thoại iPhone 11 Pro Max chính hãng tại Thegioididong.com\">iPhone 11 Pro Max</a>, gi&uacute;p cho iPhone SE 2020 c&oacute; một tốc độ xử l&yacute; kinh ngạc từ những t&aacute;c vụ nhẹ nh&agrave;ng h&agrave;ng ng&agrave;y cho đến t&aacute;c vụ nặng chơi game hay thậm ch&iacute; chỉnh sửa đồ họa đều mượt m&agrave; nhanh ch&oacute;ng.</p>\r\n\r\n<p>Sau khi đo bằng ứng dụng AnTuTu th&igrave; điểm nhận về l&agrave; 364.049, ở mức n&agrave;y th&igrave; chỉnh sửa h&igrave;nh ảnh bằng Light Room Mobile cũng mượt nữa chứ kh&ocirc;ng kể g&igrave; đến việc chơi game nhẹ Sudway Sufers hay Temple Run,...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/222629/iphone-se-128gb-2020-203520-043535.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Apple iPhone SE 2020 | Đo điểm AnTuTu\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/222629/iphone-se-128gb-2020-203520-043535.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/222629/iphone-se-128gb-2020-203520-043535.jpg\" title=\"Điện thoại Apple iPhone SE 2020 | Đo điểm AnTuTu\" /></a></p>\r\n\r\n<p>iPhone SE 2020 với t&ugrave;y chọn bộ nhớ 128 GB, gi&uacute;p bạn thoải m&aacute;i hơn khi lưu trữ nhạc phim ưa th&iacute;ch, trong khi bộ nhớ 64 GB ng&agrave;y c&agrave;ng tỏ ra hạn chế khi chạy những phi&ecirc;n bản iOS mới.</p>\r\n\r\n<h3>Camera đơn - đa t&iacute;nh năng</h3>\r\n\r\n<p>Tuy chỉ 1 camera duy nhất, nhưng iPhone SE 2020 vẫn xuất sắc cho ra những tấm h&igrave;nh r&otilde; n&eacute;t, dải nhạy s&aacute;ng rộng,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-xoa-phong\" target=\"_blank\" title=\"Tham khảo các mẫu điện thoại có camera xóa phông tại Thegioididong.com\" type=\"Tham khảo các mẫu điện thoại có camera xóa phông tại Thegioididong.com\">x&oacute;a ph&ocirc;ng</a>&nbsp;bằng AI cực ch&iacute;nh x&aacute;c, tự nhi&ecirc;n, thậm ch&iacute; bạn c&ograve;n c&oacute; thể chỉnh độ x&oacute;a ph&ocirc;ng sau khi chụp, t&iacute;nh năng t&ugrave;y chỉnh n&agrave;y chỉ xuất hiện một v&agrave;i mẫu smartphone cao cấp.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/222629/iphone-se-128gb-2020-230720-110704.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Apple iPhone SE 2020 | Camera đơn những vẫn xóa phông cực đẹp\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/222629/iphone-se-128gb-2020-230720-110704.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/222629/iphone-se-128gb-2020-230720-110704.jpg\" title=\"Điện thoại Apple iPhone SE 2020 | Camera đơn những vẫn xóa phông cực đẹp\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh chụp h&igrave;nh, th&igrave; khả năng quay phim tr&ecirc;n iPhone SE 2020 vẫn rất ấn tượng, m&aacute;y c&oacute; thể quay phim l&ecirc;n đến 4K si&ecirc;u n&eacute;t, dải m&agrave;u tuyệt vời. Nhấn giữ n&uacute;t camera để quay phim nhanh gi&uacute;p bạn kh&ocirc;ng bỏ qua khoảnh khắc th&uacute; vị n&agrave;o.</p>\r\n\r\n<p>Ở mặt trước, camera selfie vẫn l&agrave;m kh&aacute; tốt nhiệm vụ của m&igrave;nh, cho ph&eacute;p bạn chụp h&igrave;nh selfie x&oacute;a ph&ocirc;ng hay chụp với nhiều hiệu ứng AI kh&aacute;c nhau, mang đến những tấm h&igrave;nh đẹp đạt chất lượng Studio.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/222629/iphone-se-128gb-2020-230820-110820.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Apple iPhone SE 2020 | Camera selfie chất lượng ảnh vô cùng ấn tượng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/222629/iphone-se-128gb-2020-230820-110820.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/222629/iphone-se-128gb-2020-230820-110820.jpg\" title=\"Điện thoại Apple iPhone SE 2020 | Camera selfie chất lượng ảnh vô cùng ấn tượng\" /></a></p>\r\n\r\n<h3>Sở hữu c&ocirc;ng nghệ đỉnh cao với mức gi&aacute; phải chăng</h3>\r\n\r\n<p>Tuy chỉ trang bị vi&ecirc;n pin khi&ecirc;m tốn 1821 mAh, nhưng nhờ sự tối ưu cực kỳ tốt phần cứng của iOS, cho ph&eacute;p thiết bị mới của Apple c&oacute; thể trụ được đến 13 giờ sử dụng li&ecirc;n tục, gi&uacute;p bạn y&ecirc;n t&acirc;m trải nghiệm thiết bị trong ng&agrave;y d&agrave;i.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/222629/iphone-se-128gb-2020-135920-095934.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone SE 2020 | Sạc pin nhanh 18W\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/222629/iphone-se-128gb-2020-135920-095934.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/222629/iphone-se-128gb-2020-135920-095934.jpg\" title=\"Điện thoại iPhone SE 2020 | Sạc pin nhanh 18W\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, trang bị t&iacute;nh năng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo các mẫu điện thoại có hỗ trợ sạc pin nhanh tại Thegioididong.com\" type=\"Tham khảo các mẫu điện thoại có hỗ trợ sạc pin nhanh tại Thegioididong.com\">sạc nhanh</a>&nbsp;18 W gi&uacute;p cho iPhone SE mới c&oacute; thể sạc đầy 50% pin chỉ mất 30 ph&uacute;t với bộ sạc nhanh của h&atilde;ng. Rất tiếc, trong hộp chỉ c&oacute; bộ sạc ti&ecirc;u chuẩn, muốn sạc nhanh người d&ugrave;ng cần mua th&ecirc;m phụ kiện sạc ri&ecirc;ng.</p>\r\n\r\n<p>M&aacute;y cũng sẽ hỗ trợ 2 sim 2 s&oacute;ng tiện dụng, gi&uacute;p bạn duy tr&igrave; li&ecirc;n lạc giữa c&ocirc;ng việc v&agrave; c&aacute; nh&acirc;n trong c&ugrave;ng 1 thiết bị, so với những model iPhone chỉ hỗ trợ 1 sim.</p>\r\n\r\n<p>T&oacute;m lại, iPhone SE 2020 l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-choi-game\" target=\"_blank\" title=\"Tham khảo điện thoại chơi game - cấu hình cao tại Thegioididong.com \">điện thoại chơi game</a>&nbsp;tốt khi mang trong m&igrave;nh một cấu h&igrave;nh mạnh mẽ cũng như những t&iacute;nh năng mới nhất của nh&agrave; T&aacute;o. iPhone SE 2020 mang đến cho bạn sự trải nghiệm ho&agrave;n hảo với mức gi&aacute; kh&ocirc;ng thể hấp dẫn hơn.</p>\r\n', 10, 'dien-thoai-iphone-se-128gb-2020-2.html', 'Hạo Thiên', '', 'https://cdn.tgdd.vn/Products/Images/42/230412/iphone-se-2020-trang-600x600-600x600.jpg', '2', 1625573820, 0, 1, 1),
(3, '#IMMB3', 'Apple MacBook Air M1', 26960000, NULL, '<p>13.3&quot;, Retina</p>\r\n\r\n<p>CPU: Apple M1</p>\r\n\r\n<p>Card: 7 nh&acirc;n GPU</p>\r\n\r\n<p>Nặng: 1.29 kg, Khoảng 10 tiếng</p>\r\n', '<h2><a href=\"https://www.thegioididong.com/laptop/apple-macbook-air-2020-mgn93saa\" target=\"_blank\" title=\"Laptop Apple MacBook (MGN93SA/A)\">Laptop Apple MacBook Air M1 2020 (MGN93SA/A)&nbsp;</a>c&oacute; thiết kế nhỏ gọn thuận tiện mang theo b&ecirc;n m&igrave;nh, chip M1 cho cấu h&igrave;nh mạnh mẽ tốc độ xử l&yacute; nhanh sẽ l&agrave; trợ thủ đắc lực cho bạn trong c&ocirc;ng việc.</h2>\r\n\r\n<h3>Thiết kế mỏng nhẹ, m&agrave;u sắc sang trọng</h3>\r\n\r\n<p>Th&acirc;n m&aacute;y của&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook-m1\" target=\"_blank\" title=\"Macbook M1 đang bán tại Thế Giới Di Động\">Macbook M1</a>&nbsp;c&oacute; độ<strong>&nbsp;mỏng từ 4.1 mm đến 16.1 mm&nbsp;</strong>thuộc v&agrave;o h&agrave;ng ngũ những chiếc laptop mỏng nhất, vỏ m&aacute;y được l&agrave;m từ&nbsp;<strong>kim loại nguy&ecirc;n khối&nbsp;</strong>nhưng lại c&oacute; khối cực nhẹ chỉ&nbsp;<strong>1.29 kg</strong>. Tuy mỏng nhẹ l&agrave; thế nhưng chiếc laptop n&agrave;y vẫn c&oacute; độ bền vượt trội, bảo vệ tốt c&aacute;c linh kiện b&ecirc;n trong khi kh&ocirc;ng may xảy ra va đập. To&agrave;n th&acirc;n m&aacute;y được bao phủ với gam m&agrave;u bạc s&aacute;ng lo&aacute;ng c&agrave;ng t&ocirc;n l&ecirc;n n&eacute;t sang trọng của sản phẩm.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/231245/apple-macbook-air-2020-mgn93saa-073720-083748.jpg\" onclick=\"return false;\"><img alt=\"Apple MacBook Air 2020 (MGN93SA/A) - Trọng lượng\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/231245/apple-macbook-air-2020-mgn93saa-073720-083748.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/231245/apple-macbook-air-2020-mgn93saa-073720-083748.jpg\" title=\"trọng lượng\" /></a></p>\r\n\r\n<p>Magic Keyboard được thiết kế tr&ecirc;n tiến tr&igrave;nh&nbsp;<strong>1 mm&nbsp;</strong>cho thao t&aacute;c g&otilde; ch&iacute;nh x&aacute;c v&agrave; &ecirc;m &aacute;i, c&aacute;c ph&iacute;m tắt được lập tr&igrave;nh sẵn gi&uacute;p bạn dễ d&agrave;ng truy cập hơn. B&agrave;n ph&iacute;m c&oacute;&nbsp;<strong>đ&egrave;n nền cảm biến &aacute;nh s&aacute;ng&nbsp;</strong>xung quanh gi&uacute;p bạn nhập liệu ch&iacute;nh x&aacute;c hơn ở nơi thiếu s&aacute;ng. B&agrave;n di chuột Force Touch c&oacute; k&iacute;ch thước lớn, c&oacute; khả năng cảm biến lực nhấn v&agrave; rung gi&uacute;p bạn kiểm so&aacute;t ch&iacute;nh x&aacute;c thao t&aacute;c vuốt, chụm hoặc thu ph&oacute;ng.</p>\r\n\r\n<p>Chỉ cần một c&aacute;i chạm nhẹ để mở kh&oacute;a cho chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\" title=\"Xem thêm một số mẫu Macbook đang kinh doanh tại Thế Giới Di Động\">MacBook&nbsp;</a>n&agrave;y th&ocirc;ng qua&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/fingerprint-sensor-la-gi-va-co-tac-dung-gi-904389\" target=\"_blank\" title=\"Cảm biến vân tay (Fingerprint Sensor) là gì và có tác dụng gì?\">cảm biến v&acirc;n tay</a>&nbsp;t&iacute;ch hợp tr&ecirc;n n&uacute;t nguồn, sử dụng v&acirc;n tay như một chiếc ch&igrave;a kh&oacute;a để mua ứng dụng hoặc truy cập v&agrave;o những t&agrave;i liệu bảo mật m&agrave; kh&ocirc;ng cần nhập mật khẩu rắc rối.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/231245/apple-macbook-air-2020-mgn93saa-075520-085553.jpg\" onclick=\"return false;\"><img alt=\"Apple MacBook Air 2020 (MGN93SA/A) - Bảo mật\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/231245/apple-macbook-air-2020-mgn93saa-075520-085553.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/231245/apple-macbook-air-2020-mgn93saa-075520-085553.jpg\" title=\"bảo mật\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/hoi-dap/cong-thunderbolt-la-gi-uu-va-nhuoc-diem-cua-thunde-1180891\" target=\"_blank\" title=\"Cổng Thunderbolt 3 là gì? \"><strong>Thunderbolt 3</strong></a>&nbsp;cho ph&eacute;p truyền dữ liệu, sạc v&agrave; xuất video th&ocirc;ng qua một cổng duy nhất cực gọn g&agrave;ng. Kết nối kh&ocirc;ng d&acirc;y gồm&nbsp;<strong><a href=\"https://www.thegioididong.com/laptop-wifi-6\" target=\"_blank\" title=\"Xem thêm một số mẫu laptop có Wi-Fi 6 tại thegioididong.com\">Wi-Fi 6</a>&nbsp;</strong>v&agrave; Bluetooth v5.0 cho tốc độ kết nối nhanh hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/231245/apple-macbook-air-2020-mgn93saa-073720-083738.jpg\" onclick=\"return false;\"><img alt=\"Apple MacBook Air 2020 (MGN93SA/A) - Cổng kết nối\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/231245/apple-macbook-air-2020-mgn93saa-073720-083738.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/231245/apple-macbook-air-2020-mgn93saa-073720-083738.jpg\" title=\"cổng kết nối\" /></a></p>\r\n\r\n<h3>Tốc độ xử nhanh, thao t&aacute;c mượt m&agrave;</h3>\r\n\r\n<p>M&aacute;y sở hữu chip&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/apple-m1-la-gi-1305904\" target=\"_blank\" title=\"Tìm hiểu thêm về chip Apple M1\"><strong>Apple M1</strong></a>&nbsp;độc quyền đầu ti&ecirc;n của Apple thiết kế d&agrave;nh cho Mac dựa tr&ecirc;n cấu tr&uacute;c ARM, CPU 8 nh&acirc;n gồm 4 nh&acirc;n cho hiệu năng cao v&agrave; 4 nh&acirc;n tiết kiệm năng lượng nhờ vậy thời lượng pin được k&eacute;o khoảng 10 giờ sử dụng li&ecirc;n tục vượt trội hơn so với c&aacute;c thế hệ&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook-air\" target=\"_blank\" title=\"Xem thêm các mẫu laptop MacBook Air tại Thế Giới Di Động\">MacBook Air</a>&nbsp;trước. Ngo&agrave;i khả năng tiết kiệm năng lượng con chip n&agrave;y c&ograve;n c&oacute; tốc độ xử l&yacute; cực nhanh gấp&nbsp;<strong>3.5 lần</strong>&nbsp;thế hệ trước.</p>\r\n\r\n<p>GPU 7 nh&acirc;n cho tốc độ xử l&yacute; đồ họa nhanh hơn&nbsp;<strong>5 lần</strong>&nbsp;so với c&aacute;c phi&ecirc;n bản trước, thực hiện trơn tru c&aacute;c c&ocirc;ng việc đồ họa chuy&ecirc;n s&acirc;u, render video mượt m&agrave;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/231245/apple-macbook-air-2020-mgn93saa-075120-035131.jpg\" onclick=\"return false;\"><img alt=\"Apple MacBook Air 2020 (MGN93SA/A) - M1\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/231245/apple-macbook-air-2020-mgn93saa-075120-035131.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/231245/apple-macbook-air-2020-mgn93saa-075120-035131.jpg\" title=\"Apple MacBook Air 2020 (MGN93SA/A) - M1\" /></a></p>\r\n\r\n<p>M&aacute;y sở hữu&nbsp;<a href=\"https://www.thegioididong.com/laptop-8-gb\" target=\"_blank\" title=\"Xem laptop RAM 8 GB Tại Thế Giới Di Động\"><strong>RAM 8 GB</strong></a>&nbsp; cho khả năng đa nhiệm tốt, bạn c&oacute; thể vừa mở YouTube nghe nhạc vừa mở Photoshop l&agrave;m việc m&agrave; m&aacute;y vẫn hoạt động mượt m&agrave; kh&ocirc;ng c&oacute; hiện tượng giật lag.</p>\r\n\r\n<p>Ngo&agrave;i ra, ổ cứng&nbsp;<strong><a href=\"https://www.thegioididong.com/laptop?g=ssd-256-gb\" target=\"_blank\" title=\"Xem thêm laptop có SSD 256 GB tại Thế Giới Di Động\">SSD 256 GB</a>&nbsp;</strong>mang đến cho&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm một số mẫu laptop đang kinh doanh tại Thế Giới Di Động\">laptop</a>&nbsp;tốc độ phản hồi nhanh ch&oacute;ng, bạn chỉ mất v&agrave;i gi&acirc;y để v&agrave;o ứng dụng. C&aacute;c thao t&aacute;c cuộn, k&eacute;o, thả được thực hiện trơn tru nhờ đ&oacute; tốc độ l&agrave;m việc được cải thiện đ&aacute;ng kể. B&ecirc;n cạnh tốc độ phản hồi th&igrave; ổ cứng c&ograve;n mang đến cho bạn kh&ocirc;ng gian lưu trữ lớn bạn c&oacute; thể thoải m&aacute;i lưu những b&agrave;i h&aacute;t hay những bộ phim y&ecirc;u th&iacute;ch m&agrave; kh&ocirc;ng lo n&oacute; sẽ chiếm mất kh&ocirc;ng gian lưu trữ của c&aacute;c t&agrave;i liệu c&ocirc;ng việc.</p>\r\n\r\n<p><br />\r\n<a href=\"https://www.thegioididong.com/images/44/231245/apple-macbook-air-2020-mgn93saa-075720-085747.jpg\" onclick=\"return false;\"><img alt=\"Apple MacBook Air 2020 (MGN93SA/A) - SSD\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/231245/apple-macbook-air-2020-mgn93saa-075720-085747.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/231245/apple-macbook-air-2020-mgn93saa-075720-085747.jpg\" title=\"Apple MacBook Air 2020 (MGN93SA/A) - ssd\" /></a></p>\r\n\r\n<p><strong>Hệ điều h&agrave;nh macOS Big&nbsp;Sur</strong><strong>&nbsp;</strong>được chạy tr&ecirc;n chip M1 với một thiết kế ho&agrave;n to&agrave;n mới, khả năng cập nhật ứng dụng nhanh ch&oacute;ng v&agrave; kho ứng dụng si&ecirc;u to khổng lồ sẽ đ&aacute;p ứng tất tần tật mọi nhu cầu của bạn.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://Danh%20s%C3%A1ch%20%E1%BB%A9ng%20d%E1%BB%A5ng%20ch%E1%BA%A1y%20%C4%91%C6%B0%E1%BB%A3c%20tr%C3%AAn%20Macbook%20chip%20Apple%20M1/\" target=\"_blank\" title=\"Danh sách ứng dụng chạy được trên Macbook chip Apple M1\">Danh s&aacute;ch ứng dụng chạy được tr&ecirc;n Macbook chip Apple M1</a>.</p>\r\n\r\n<h3>H&igrave;nh ảnh sắc n&eacute;t, m&agrave;u sắc sống động</h3>\r\n\r\n<p>Laptop với&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-retina-la-gi-905780\" target=\"_blank\" title=\"Màn hình Retina là gì?\">m&agrave;</a><a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-retina-la-gi-905780\" target=\"_blank\" title=\"Màn hình Retina là gì? \">n h&igrave;nh Retina</a>&nbsp;k&iacute;ch thước&nbsp;<strong><a href=\"https://www.thegioididong.com/laptop-13-inch\" target=\"_blank\" title=\"Xem thêm các mẫu laptop 13 inch tại Thế Giới Di Động\">13.3 inch</a>&nbsp;</strong>c&oacute; độ s&aacute;ng&nbsp;<strong>400 nits</strong>&nbsp;sẽ đem đến cho bạn những h&igrave;nh ảnh chi tiết nhất v&agrave; c&oacute; độ tương phản tuyệt vời. C&ocirc;ng nghệ&nbsp;<strong><a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-cong-nghe-man-hinh-true-tone-992705\" target=\"_blank\" title=\"Tìm hiểu công nghệ màn hình True Tone\">True Tone</a></strong>&nbsp;kết hợp c&ugrave;ng dải m&agrave;u rộng gi&uacute;p m&agrave;u sắc h&igrave;nh ảnh đạt được ch&iacute;nh x&aacute;c v&agrave; tự nhi&ecirc;n, c&ugrave;ng viền m&agrave;n h&igrave;nh mỏng gi&uacute;p ph&aacute; bỏ c&aacute;c giới hạn khi thưởng thức phim ảnh.</p>\r\n\r\n<p>C&ocirc;ng nghệ&nbsp;<strong>IPS</strong>&nbsp;cho g&oacute;c nh&igrave;n rộng hơn, bạn c&oacute; thể thoải m&aacute;i quan s&aacute;t m&agrave;n h&igrave;nh kể cả khi kh&ocirc;ng ở đối diện m&aacute;y t&iacute;nh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/hoi-dap/huong-dan-su-dung-facetime-tren-macbook-958617\" target=\"_blank\" title=\"FaceTime Camera là gì\">FaceTime Camera&nbsp;</a>c&oacute; khả năng ghi lại h&igrave;nh ảnh chất lượng cao kết hợp c&ugrave;ng dải 3 micro với chất lượng ph&ograve;ng thu gi&uacute;p bạn thực hiện tốt c&aacute;c cuộc gọi video hay hội nghị trực tuyến.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/231245/apple-macbook-air-2020-mgn93saa-075720-085704.jpg\" onclick=\"return false;\"><img alt=\"Apple MacBook Air 2020 (MGN93SA/A) - Màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/231245/apple-macbook-air-2020-mgn93saa-075720-085704.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/231245/apple-macbook-air-2020-mgn93saa-075720-085704.jpg\" title=\"màn hình\" /></a></p>\r\n\r\n<p>Laptop Apple MacBook Air M1 2020 (MGN93SA/A) với cấu h&igrave;nh mạnh mẽ, thiết kế mỏng nhẹ sẽ l&agrave; chiếc m&aacute;y t&iacute;nh ph&ugrave; hợp d&agrave;nh cho bạn khi đang muốn t&igrave;m một chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=cao-cap-sang-trong\" target=\"_blank\" title=\"Xem thêm các mẫu laptop cao cấp tại Thế Giới DI Động\">laptop cao cấp</a>&nbsp;c&oacute; hiệu suất l&agrave;m việc cao.</p>\r\n', 10, 'laptop-apple-macbook-air-m1-2020-8gb-256GB-silver-mgn93sa-a-3.html ', 'Văn Phước', '', 'https://cdn.tgdd.vn/Products/Images/44/231245/apple-macbook-air-2020-mgn93saa-1-600x600.jpg', '2', 1625573825, 0, 2, 5);
INSERT INTO `tbl_product` (`product_id`, `product_code`, `product_name`, `product_price`, `old_price`, `product_desc`, `content`, `qty`, `slug`, `creator`, `editor`, `product_thumb`, `product_status`, `add_date`, `update_date`, `cat_id`, `child_id`) VALUES
(4, '#IMSS4', 'Samsung Galaxy A52', 9790000, 9800000, '<p>Màn hình 6.5\", Chip Snapdragon 720G</p>\r\n\r\n<p>RAM 8 GB, ROM 256 GB</p>\r\n\r\n<p>Camera sau: Chính 64 MP & Phụ 12 MP, 5 MP, 5 MP</p>\r\n\r\n<p>Camera trước: 32 MP</p>', '<h3><a href=\"https://www.thegioididong.com/samsung\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo sản phẩm Samsung kinh doanh tại thegioididong.com\">Samsung</a>&nbsp;cho ra mắt sản phẩm thuộc d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung-galaxy-a\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo Samsung Galaxy A tại thegioididong.com\">Galaxy A</a>&nbsp;mang t&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-a52-8gb-256gb\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo thông tin sản phẩm tại thegioididong.com\">Galaxy A52 (8GB/256GB)</a>. Sở hữu vi xử l&yacute; Snapdragon 720G mạnh mẽ bậc nhất, cụm 4 camera c&oacute; độ ph&acirc;n giải l&ecirc;n đến 64 MP v&agrave; một vẻ ngo&agrave;i hiện đại, trẻ trung tr&agrave;n đầy sức sống.</h3>\r\n\r\n<h3>Thiết kế đặc trưng d&ograve;ng Galaxy A</h3>\r\n\r\n<p>Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo các sản phẩm điện thoại tại Thế Giới Di Động\">điện thoại</a>&nbsp;sở hữu m&agrave;n h&igrave;nh Infinity-O đầy hiện đại, 4 m&aacute;y ảnh mặt sau được sắp xếp gọn g&agrave;ng b&ecirc;n trong m&ocirc;-đun h&igrave;nh chữ nhật th&acirc;n thuộc v&agrave; c&aacute;c cạnh viền bo cong nhẹ nh&agrave;ng, uyển chuyển quanh th&acirc;n m&aacute;y, tạo cảm gi&aacute;c dễ chịu khi cầm nắm sử dụng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/236085/amsung-galaxy-a52-02.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy A52 | Thiết kế mặt sau\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/236085/amsung-galaxy-a52-02.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/236085/amsung-galaxy-a52-02.jpg\" title=\"Samsung Galaxy A52 | Thiết kế mặt sau\" /></a></p>\r\n\r\n<p>Series Samsung Galaxy A lần n&agrave;y đ&atilde; được Samsung thiết kế cho bộ m&agrave;u sắc cực kỳ tinh tế l&agrave;: M&agrave;u xanh, t&iacute;m, đen v&agrave; trắng. Bạn c&oacute; thể h&oacute;a th&acirc;n đầy năng động, nhẹ nh&agrave;ng, lịch l&atilde;m hay sang trọng một c&aacute;ch đa dạng bằng c&aacute;ch lựa chọn cho m&igrave;nh một trong những m&agrave;u sắc ưng &yacute; nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/236085/amsung-galaxy-a52-13.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy A52 | Đạt chuẩn kháng nước IP67\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/236085/amsung-galaxy-a52-13.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/236085/amsung-galaxy-a52-13.jpg\" title=\"Samsung Galaxy A52 | Đạt chuẩn kháng nước IP67\" /></a></p>\r\n\r\n<p>Smartphone bền bỉ theo thời gian khi trang bị mặt k&iacute;nh cường lực rắn chắc, bảo vệ m&aacute;y khỏi c&aacute;c va đập, trầy xước. Ngo&agrave;i ra, người d&ugrave;ng c&oacute; thể thoải m&aacute;i tham gia một số hoạt động c&oacute; li&ecirc;n quan tới nước dưới độ s&acirc;u 1 m&eacute;t khoảng 30 ph&uacute;t trong sự bảo vệ của chuẩn kh&aacute;ng nước v&agrave; bụi IP67.</p>\r\n\r\n<h3>Hiển thị sắc n&eacute;t sắc, sống động</h3>\r\n\r\n<p>Tấm nền&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-super-amoled-la-gi-905770\" rel=\"noopener\" target=\"_blank\" title=\"Màn hình Super AMOLED là gì?\">Super AMOLED</a>&nbsp;độ ph&acirc;n giải&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/do-phan-giai-man-hinh-qhd-hd-fullhd-2k-4k-la-gi--592178#fullhd\" rel=\"noopener\" target=\"_blank\" title=\"Độ phân giải màn hình qHD, HD, FullHD, 2K, 4K là gì?\">Full HD+</a>&nbsp;được trang bị tr&ecirc;n Galaxy A52 c&oacute; khả năng tiết kiệm điện, cung cấp h&igrave;nh ảnh với m&agrave;u sắc rực rỡ, độ tương phản m&agrave;u sắc cao sẽ mang đến cho bạn những ph&uacute;t gi&acirc;y giải tr&iacute; đ&uacute;ng chất, khiến bạn ch&igrave;m đắm bất tận bằng c&aacute;c c&ocirc;ng nghệ ti&ecirc;n tiến nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/236085/amsung-galaxy-a52-01.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy A52 | Tấm nền Super AMOLED cho sắc màu rực rỡ\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/236085/amsung-galaxy-a52-01.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/236085/amsung-galaxy-a52-01.jpg\" title=\"Samsung Galaxy A52 | Tấm nền Super AMOLED cho sắc màu rực rỡ\" /></a></p>\r\n\r\n<p>Để c&oacute; những trải nghiệm tuyệt vời th&igrave; ngo&agrave;i khả năng hiển thị h&igrave;nh ảnh đẹp ra c&ograve;n phải c&oacute; một kh&ocirc;ng gian lớn, Galaxy A52 sở hữu m&agrave;n h&igrave;nh c&oacute; k&iacute;ch thước 6.5 inch kh&aacute; rộng r&atilde;i cho người d&ugrave;ng những trải nghiệm bắt mắt, r&otilde; r&agrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/236085/amsung-galaxy-a52-08.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy A52 | Hỗ trợ tần số quét cao 90 Hz\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/236085/amsung-galaxy-a52-08.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/236085/amsung-galaxy-a52-08.jpg\" title=\"Samsung Galaxy A52 | Hỗ trợ tần số quét cao 90 Hz\" /></a></p>\r\n\r\n<p>Tần số qu&eacute;t của A52 cũng đ&atilde; được n&acirc;ng l&ecirc;n 90 Hz, gi&uacute;p cho những chuyển động được mượt m&agrave; hơn, giờ đ&acirc;y bạn chỉ cần chạm nhẹ v&agrave; Galaxy A52 lướt uyển chuyển theo tay bạn.</p>\r\n\r\n<h3>Hiệu năng mạnh mẽ với con chip chuy&ecirc;n game</h3>\r\n\r\n<p>Galaxy A52 mang trong m&igrave;nh con chip&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/snapdragon-720g-1246562\" rel=\"noopener\" target=\"_blank\" title=\"Khám phá Snapdragon 720G, vi xử lý chuyên game cho smartphone\">Snapdragon 720G 8 nh&acirc;n</a>&nbsp;sản xuất dựa tr&ecirc;n quy tr&igrave;nh 8 nm, chipset được cải tiến c&aacute;c th&ocirc;ng số cao cấp, để so s&aacute;nh th&igrave; Snapdragon 720G sẽ l&agrave; mạnh hơn Exynos 9611 ở phi&ecirc;n bản tiền nhiệm cả CPU lẫn GPU. Hiệu năng sức mạnh, mượt m&agrave; của chiếc điện thoại l&agrave; điều m&agrave; kh&ocirc;ng một người d&ugrave;ng chuy&ecirc;n hoạt động gaming n&agrave;o c&oacute; thể bỏ qua.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/236085/amsung-galaxy-a52-14.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy A52 | Trang bị chip Snapdragon 720G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/236085/amsung-galaxy-a52-14.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/236085/amsung-galaxy-a52-14.jpg\" title=\"Samsung Galaxy A52 | Trang bị chip Snapdragon 720G\" /></a></p>\r\n\r\n<p>L&agrave; một trong những d&ograve;ng chip chuy&ecirc;n game, Snapdragon 720G c&oacute; khả năng xử l&yacute; đồ họa trơn tru, đi k&egrave;m c&aacute;c t&iacute;nh năng Elite Gaming của Qualcomm như HDR gaming,... Tốc độ xử l&yacute; đạt 2.3 GHz, mọi thứ đều ho&agrave;n hảo cho một gaming ch&iacute;nh hiệu tự tin thể hiện.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/236085/amsung-galaxy-a52-12.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy A52 | Trải nghiệm gaming mạnh mẽ\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/236085/amsung-galaxy-a52-12.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/236085/amsung-galaxy-a52-12.jpg\" title=\"Samsung Galaxy A52 | Trải nghiệm gaming mạnh mẽ\" /></a></p>\r\n\r\n<p>M&aacute;y sở hữu dung lượng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-8gb\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo điện thoại có RAM 8 GB tại Thegioididong.com\">RAM 8 GB</a>, số RAM tương đối cao ở thời điểm hiện tại. Đi k&egrave;m&nbsp;<a href=\"https://www.thegioididong.com/dtdd-rom-256gb\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo điện thoại có bộ nhớ 256 GB tại Thegioididong.com\">bộ nhớ trong 256 GB</a>&nbsp;sẽ gi&uacute;p c&oacute; kh&ocirc;ng gian lưu trữ rộng mở, lưu ứng dụng v&agrave; h&igrave;nh ảnh thả ga, giải quyết một c&aacute;ch dễ d&agrave;ng c&aacute;c vấn đề về đầy bộ nhớ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/236085/amsung-galaxy-a52-07.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy A52 | Hỗ trợ thẻ nhớ ngoài\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/236085/amsung-galaxy-a52-07.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/236085/amsung-galaxy-a52-07.jpg\" title=\"Samsung Galaxy A52 | Hỗ trợ thẻ nhớ ngoài\" /></a></p>\r\n\r\n<h3>Tận hưởng những bức ảnh cực chất</h3>\r\n\r\n<p>Samsung Galaxy A52 trang bị 4 ống k&iacute;nh, sẵn s&agrave;ng thực thi mọi t&iacute;nh năng hỗ trợ m&agrave; m&aacute;y c&oacute;. Camera ch&iacute;nh 64 MP với khẩu độ f/1.8,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-goc-rong\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo điện thoại có camera góc rộng tại Thegioididong.com\">camera g&oacute;c rộng</a>&nbsp;123 độ 12 MP, 2 camera một macro v&agrave; một đo độ s&acirc;u c&ugrave;ng c&oacute; độ ph&acirc;n giải 5 MP với khẩu độ f/2.4.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/236085/amsung-galaxy-a52-03.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy A52 | Cụm camera sau\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/236085/amsung-galaxy-a52-03.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/236085/amsung-galaxy-a52-03.jpg\" title=\"Samsung Galaxy A52 | Cụm camera sau\" /></a></p>\r\n\r\n<p>Th&ocirc;ng số tr&ecirc;n Galaxy A52 ho&agrave;n to&agrave;n ngang t&agrave;i ngang sức&nbsp;so với Galaxy A72, chỉ c&oacute; kh&aacute;c biệt l&agrave; A52 kh&ocirc;ng sở hữu ống k&iacute;nh tele 8 MP m&agrave; thay v&agrave;o đ&oacute; l&agrave; ống k&iacute;nh đo độ s&acirc;u 5 MP, gi&uacute;p người d&ugrave;ng thoả sức s&aacute;ng tạo những bức ảnh như chiếc m&aacute;y ảnh thực sự.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/236085/samsung-galaxy-a52-5g-018.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy A52 | Ảnh chụp trên camera\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/236085/samsung-galaxy-a52-5g-018.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/236085/samsung-galaxy-a52-5g-018.jpg\" title=\"Samsung Galaxy A52 | Ảnh chụp trên camera\" /></a></p>\r\n\r\n<p>C&aacute;c t&iacute;nh năng quay phim sắc n&eacute;t, quay từng chi tiết từ b&igrave;nh thường đến những chi tiết đặc biệt đều rất chất lượng, b&ecirc;n cạnh đ&oacute; c&ograve;n hỗ trợ chống rung quang học, đem đến cho bạn những thước phim ổn định, đường n&eacute;t chuy&ecirc;n nghiệp hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/236085/amsung-galaxy-a52-16.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy A52 | Quay phim ổn định, sắc nét\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/236085/amsung-galaxy-a52-16.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/236085/amsung-galaxy-a52-16.jpg\" title=\"Samsung Galaxy A52 | Quay phim ổn định, sắc nét\" /></a></p>\r\n\r\n<p>M&aacute;y ảnh mặt trước c&oacute; độ ph&acirc;n giải l&ecirc;n đến 32 MP, cung cấp những t&ugrave;y chọn l&agrave;m đẹp ảnh th&ocirc;ng dụng, camera selfie th&ecirc;m lung linh m&agrave; kh&ocirc;ng qu&ecirc;n ghi nhớ mọi chi tiết của vẻ đẹp tự nhi&ecirc;n, ch&acirc;n thật nơi người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/236085/samsung-galaxy-a52-5g-019.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy A52 | Ảnh chụp selfie\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/236085/samsung-galaxy-a52-5g-019.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/236085/samsung-galaxy-a52-5g-019.jpg\" title=\"Samsung Galaxy A52 | Ảnh chụp selfie\" /></a></p>\r\n\r\n<h3>Pin dung lượng lớn, thoải m&aacute;i sử dụng cả ng&agrave;y d&agrave;i</h3>\r\n\r\n<p>Cung cấp năng lượng cho A52 l&agrave; vi&ecirc;n pin dung lượng 4500 mAh.&nbsp;Pin kh&aacute; thoải m&aacute;i để người d&ugrave;ng chạy những t&aacute;c vụ cơ bản tr&ecirc;n m&aacute;y cả ng&agrave;y d&agrave;i, tuy nhi&ecirc;n để chơi game xuy&ecirc;n suốt th&igrave; bạn cần phải sạc pin nhiều lần trong ng&agrave;y.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/236085/amsung-galaxy-a52-11.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy A52 | Dung lượng pin lớn 4500 mAh\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/236085/amsung-galaxy-a52-11.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/236085/amsung-galaxy-a52-11.jpg\" title=\"Samsung Galaxy A52 | Dung lượng pin lớn 4500 mAh\" /></a></p>\r\n\r\n<p>Y&ecirc;n t&acirc;m hơn trong mỗi lần sạc pin, v&igrave; Galaxy A52 đ&atilde; c&oacute; c&ocirc;ng nghệ&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo các sản phẩm điện thoại có sạc pin nhanh tại Thế Giới Di Động\">sạc pin nhanh</a>&nbsp;hỗ trợ 25 W. Chỉ cần cắm sạc v&agrave; smartphone sẽ đầy pin nhanh ch&oacute;ng gi&uacute;p bạn. Kh&ocirc;ng l&agrave;m gi&aacute;n đoạn qu&aacute; l&acirc;u c&ocirc;ng việc hay giải tr&iacute; của bạn, tiết kiệm tối đa thời gian sạc hiệu quả.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/236085/amsung-galaxy-a52-05.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy A52 | Hỗ trợ sạc nhanh 25 W\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/236085/amsung-galaxy-a52-05.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/236085/amsung-galaxy-a52-05.jpg\" title=\"Samsung Galaxy A52 | Hỗ trợ sạc nhanh 25 W\" /></a></p>\r\n\r\n<p>Giữa mu&ocirc;n v&agrave;ng sản phẩm đang c&oacute; tr&ecirc;n thị trường th&igrave; Samsung Galaxy A52 với những n&acirc;ng cấp về camera, m&agrave;n h&igrave;nh v&agrave; hiệu năng,... như tr&ecirc;n đ&acirc;y cũng sẽ l&agrave; một lựa chọn đ&aacute;ng để người ti&ecirc;u d&ugrave;ng c&acirc;n nhắc. Chiếc điện thoại hứa hẹn sẽ khuấy động ph&acirc;n kh&uacute;c tầm trung trong thời gian tới.</p>\r\n', 2, 'dien-thoai-samsung-galaxy-a52-8gb-256gb-3.html', 'Văn Nhã', '', 'https://cdn.tgdd.vn/Products/Images/42/236085/TimerThumb/samsung-galaxy-a52-8gb-256gb.jpg', '2', 1625573825, 1642752614, 1, 2),
(5, '#IMMSI5', 'MSI GF63 10SC i5 ', 19520000, NULL, '<p>15.6\", Full HD</p>\r\n\r\n<p>CPU: Intel Core i5 Comet Lake, 2.50 GHz</p>\r\n\r\n<p>Card: NVIDIA GeForce GTX 1650 4 GB</p>\r\n\r\n<p>Nặng: 1.86 kg, 3-cell Li-ion, 51 Wh</p>', '<h3><a href=\"https://www.thegioididong.com/laptop/msi-gf63-10sc-i5-255vn\" target=\"_blank\" title=\"MSI GF63 10SC i5 (255VN) đang bán tại thegioididong.com\">MSI GF63 10SC i5 (255VN)</a>&nbsp;mang vẻ ngo&agrave;i mạnh mẽ, đậm chất gaming sở hữu chip i5 thế hệ 10 c&ugrave;ng card đồ họa NVIDIA GeForce GTX 1650 mang lại hiệu năng vượt trội, thoải m&aacute;i chiến c&aacute;c tựa game y&ecirc;u th&iacute;ch.</h3>\r\n\r\n<h3>Chiến c&aacute;c game thịnh h&agrave;nh với bộ CPU mạnh mẽ</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm các mẫu laptop đang bán tại thegioididong.com\">Laptop</a>&nbsp;sở hữu bộ vi xử l&yacute;&nbsp;<strong><a href=\"https://www.thegioididong.com/laptop?g=core-i5\" target=\"_blank\" title=\"Xem thêm các mẫu laptop trang bị Intel Core i5 đang bán tại thegioididong.com\">Intel Core i5</a>&nbsp;10300H Gen 10</strong>&nbsp;c&oacute; sức mạnh hiệu năng vượt trội tăng khả năng xử l&yacute; nhanh ch&oacute;ng v&agrave; tiết kiệm năng lượng với tốc độ xung nhịp trung b&igrave;nh&nbsp;<strong>2.50 GHz</strong>&nbsp;đạt cao nhất&nbsp;<strong>Turbo Boost 4.5 GHz</strong>.</p>\r\n\r\n<p><strong><a href=\"https://www.thegioididong.com/laptop-8-gb\" target=\"_blank\" title=\"Xem các mẫu laptp 8 GB đang bán tại thegioididong.com\">RAM 8 GB</a></strong>&nbsp;với tốc độ Bus RAM<strong>&nbsp;2666 MHz</strong>&nbsp;cho khả năng xử l&yacute; đa nhiệm, đ&aacute;p ứng được hầu hết c&aacute;c nhu cầu giải tr&iacute; l&agrave;m việc của bạn, d&ugrave; bạn mở nhiều ứng dụng c&ugrave;ng l&uacute;c cũng kh&ocirc;ng lo bị đơ m&aacute;y, đứng m&aacute;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/238608/msi-gf63-10sc-i5-255vn-4.jpg\" onclick=\"return false;\"><img alt=\"MSI GF63 10SC i5 10300H (255VN) - Cấu hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-4.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-4.jpg\" title=\"MSI GF63 10SC i5 10300H (255VN) - Cấu hình\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop-card-do-hoa-roi\" target=\"_blank\" title=\"Xem các mẫu laptop trang bị card đồ họa rời đang bán tại thegioididong.com\">Card đồ họa rời</a>&nbsp;<a href=\"https://www.thegioididong.com/laptop-geforce-gtx\" target=\"_blank\" title=\"Xem thêm laptop trang bị GeForce GTX đang bán tại thegioididong.com\">NVIDIA GeForce GTX 1650 4 GB</a>&nbsp;mang lại trải nghiệm chiến c&aacute;c tựa game hot như Far Cry 5, GTA 5, Li&ecirc;n Minh Huyền Thoại, FIFA Online 4,... một c&aacute;ch mượt m&agrave;, trơn tru ở mức c&agrave;i đặt trung b&igrave;nh - cao m&agrave; kh&ocirc;ng lo xảy ra hiện tượng b&oacute;ng ma hay giật lag h&igrave;nh ảnh. Đồng thời, bạn c&ograve;n c&oacute; thể sử dụng chiếc m&aacute;y n&agrave;y để thực hiện thiết kế đồ họa như chỉnh sửa h&igrave;nh ảnh, video bằng Photoshop, Ai, Pr,...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/238608/msi-gf63-10sc-i5-255vn-5.jpg\" onclick=\"return false;\"><img alt=\"MSI GF63 10SC i5 10300H (255VN) - Card đồ họa \" data-src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-5.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-5.jpg\" title=\"MSI GF63 10SC i5 10300H (255VN) - Card đồ họa \" /></a></p>\r\n\r\n<p><strong>Ổ cứng&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=ssd-512-gb\" target=\"_blank\" title=\"Xem thêm các sản phẩm laptop SSD 512 GB đang kinh doanh tại thegioididong.com\">SSD 512 GB</a>&nbsp;NVMe PCIe</strong>&nbsp;gi&uacute;p khởi động m&aacute;y, ứng dụng nhanh ch&oacute;ng v&agrave; cho kh&ocirc;ng gian lưu trữ vừa đủ nhiều tựa game y&ecirc;u th&iacute;ch, file h&igrave;nh ảnh, &acirc;m thanh,... Hơn nữa, MSI cung cấp khe cắm HDD SATA thoả sức n&acirc;ng cấp ổ cứng tuỳ theo nhu cầu của bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/238608/msi-gf63-10sc-i5-255vn-6.jpg\" onclick=\"return false;\"><img alt=\"MSI GF63 10SC i5 10300H (255VN) - SSD\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-6.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-6.jpg\" title=\"MSI GF63 10SC i5 10300H (255VN) - SSD\" /></a></p>\r\n\r\n<h3>Thiết kế phong c&aacute;ch, thể hiện c&aacute; t&iacute;nh&nbsp;</h3>\r\n\r\n<p>MSI GF63 thể hiện sự mạnh mẽ đặc trưng của d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=laptop-gaming\" target=\"_blank\" title=\"Xem thêm laptop gaming đang bán tại thegioididong.com\">laptop gaming</a>&nbsp;với vỏ mặt lưng được l&agrave;m từ kim loại bền bỉ kết hợp logo c&agrave;ng tăng th&ecirc;m vẻ huyền b&iacute; cho chiếc laptop n&agrave;y. M&aacute;y c&oacute; trọng lượng&nbsp;<strong>1.86 kg</strong>, độ d&agrave;y&nbsp;<strong>21.7 mm</strong>&nbsp;cũng c&oacute; thể được xem l&agrave; gọn nhẹ so với c&aacute;c laptop c&ugrave;ng ph&acirc;n kh&uacute;c.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/238608/msi-gf63-10sc-i5-255vn-2.jpg\" onclick=\"return false;\"><img alt=\"MSI GF63 10SC i5 10300H (255VN) - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-2.jpg\" title=\"MSI GF63 10SC i5 10300H (255VN) - Thiết kế\" /></a></p>\r\n\r\n<p>B&agrave;n ph&iacute;m c&oacute; k&iacute;ch thước hợp l&yacute;, h&agrave;nh tr&igrave;nh ph&iacute;m s&acirc;u, cảm gi&aacute;c g&otilde; ph&iacute;m tốt v&agrave; được trang bị&nbsp;<strong>đ&egrave;n nền</strong>&nbsp;m&agrave;u đỏ gi&uacute;p tăng th&ecirc;m sự th&iacute;ch th&uacute; hơn khi chơi game. B&ecirc;n cạnh đ&oacute;&nbsp;<strong>chiếu nghỉ tay</strong>&nbsp;rất thoải m&aacute;i v&agrave; chắc chắn, bạn sẽ cảm thấy sự m&aacute;t lạnh khi đặt tay l&ecirc;n khu vực n&agrave;y ngay sau khi mở m&aacute;y l&ecirc;n.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/238608/msi-gf63-10sc-i5-255vn-7.jpg\" onclick=\"return false;\"><img alt=\"MSI GF63 10SC i5 10300H (255VN) - Bàn phím\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-7.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-7.jpg\" title=\"MSI GF63 10SC i5 10300H (255VN) - Bàn phím\" /></a></p>\r\n\r\n<p>Hệ thống tản nhiệt&nbsp;<strong>CoolerBoost</strong>&nbsp;độc quyền hoạt động &ecirc;m &aacute;i gi&uacute;p giảm thiểu hơi n&oacute;ng nhanh ch&oacute;ng, tối ưu luồng gi&oacute; để đảm bảo mang lại hiệu năng game cao trong một th&acirc;n m&aacute;y gọn g&agrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/238608/msi-gf63-10sc-i5-255vn-8.jpg\" onclick=\"return false;\"><img alt=\"MSI GF63 10SC i5 10300H (255VN) - Tản nhiệt\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-8.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-8.jpg\" title=\"MSI GF63 10SC i5 10300H (255VN) - Tản nhiệt\" /></a></p>\r\n\r\n<p>Ngo&agrave;i ra,&nbsp;<a href=\"https://www.thegioididong.com/laptop-msi\" target=\"_blank\" title=\"Xem các mẫu laptop MSI đag bán tại thegioididong.com\">laptop MSI</a>&nbsp;c&ograve;n được trang bị đa dạng c&aacute;c cổng kết nối như HDMI, LAN (RJ45), USB 3.2, USB Type-C kết nối tiện lợi v&agrave; nhanh ch&oacute;ng với chuột, b&agrave;n ph&iacute;m rời, m&agrave;n h&igrave;nh rời, m&aacute;y chiếu,... Chuẩn kết nối kh&ocirc;ng d&acirc;y Bluetooth 5.1,&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/wi-fi-chuan-80211ax-la-gi-tim-hieu-ve-wi-fi-the-he-thu-6-1187524\" target=\"_blank\" title=\"Xem thêm về chuẩn Wi-Fi 6 AX201\">Wi-Fi 6 AX201</a>&nbsp;cho đường truyền mạng lu&ocirc;n ổn định, kh&ocirc;ng lo bị mất kết nối khi chơi game, giải tr&iacute;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/238608/msi-gf63-10sc-i5-255vn-12.jpg\" onclick=\"return false;\"><img alt=\"MSI GF63 10SC i5 10300H (255VN) - Cổng kết nối\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-12.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-12.jpg\" title=\"MSI GF63 10SC i5 10300H (255VN) - Cổng kết nối\" /></a></p>\r\n\r\n<h3>Trải nghiệm nhập vai qua&nbsp;<a href=\"https://www.thegioididong.com/laptop-tren-15-inch\" target=\"_blank\" title=\"Xem thêm các mẫu laptop trên 15 inch đang bán tại thegioididong.com\">m&agrave;n h&igrave;nh 15.6 inch</a>&nbsp;hiển thị ch&acirc;n thật</h3>\r\n\r\n<p>M&agrave;n h&igrave;nh c&oacute; độ ph&acirc;n giải&nbsp;<strong>Full HD (1920 x 1080)</strong>&nbsp;với tần số qu&eacute;t&nbsp;<strong>144 Hz</strong>&nbsp;mang lại h&igrave;nh ảnh sống động, m&agrave;u sắc được t&aacute;i tạo ch&iacute;nh x&aacute;c v&agrave; gi&uacute;p nắm bắt kịp thời từng chuyển động trong game, tốc độ chuyển đổi giữa c&aacute;c khung h&igrave;nh nhanh hơn, tăng khả năng chiến thắng.</p>\r\n\r\n<p>G&oacute;c nh&igrave;n rộng hơn đến&nbsp;<strong>178 độ</strong>&nbsp;cho h&igrave;nh ảnh chi tiết sắc n&eacute;t với&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-ips-lcd-la-gi-905752\" target=\"_blank\" title=\"Tìm hiểu thêm về tấm nền IPS\">tấm nền IPS</a>&nbsp;c&ugrave;ng c&ocirc;ng nghệ chống ch&oacute;i&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-man-hinh-chong-choi-anti-glare-1182688\" target=\"_blank\" title=\"Tìm hiểu thêm về công nghệ chống chói Anti Glare\">Anti Glare</a>&nbsp;giảm t&igrave;nh trạng ch&oacute;i, nh&ograve;e h&igrave;nh hay mỏi mắt, ch&oacute;ng mặt khi sử dụng m&aacute;y l&acirc;u hay trong điều kiện &aacute;nh s&aacute;ng mạnh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/238608/msi-gf63-10sc-i5-255vn-9.jpg\" onclick=\"return false;\"><img alt=\"MSI GF63 10SC i5 10300H (255VN) - Hình ảnh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-9.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-9.jpg\" title=\"MSI GF63 10SC i5 10300H (255VN) - Hình ảnh\" /></a></p>\r\n\r\n<p>Đắm ch&igrave;m trong c&aacute;c tr&ograve; chơi hay nghe nhạc, xem phim giải tr&iacute; qua kh&ocirc;ng gian &acirc;m thanh ph&aacute;t ra to r&otilde;, ch&acirc;n thật từ c&ocirc;ng nghệ&nbsp;<strong>Nahimic Audio</strong>.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/238608/msi-gf63-10sc-i5-255vn-11.jpg\" onclick=\"return false;\"><img alt=\"MSI GF63 10SC i5 10300H (255VN) - Âm thanh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-11.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-11.jpg\" title=\"MSI GF63 10SC i5 10300H (255VN) - Âm thanh\" /></a></p>\r\n\r\n<p>H&atilde;y sở hữu ngay chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-msi?g=laptop-gaming\" target=\"_blank\" title=\"Xem thêm laptop gaming MSI đang bán tại thegioididong.com\">laptop MSI Gaming</a>&nbsp;n&agrave;y để thỏa sức c&ugrave;ng chiến c&aacute;c tựa game y&ecirc;u th&iacute;ch nh&eacute;.</p>\r\n', 1, 'laptop-msi-gf63-10sc-i5-10300h-8gb-512gb-4gb-gtx1650-144hz-win10-255vn-5.html', 'Huỳnh Phương', '', 'public/uploads/msi-gf63-10sc-i5-255v-600x600.jpg', '2', 1625576990, 1625615368, 2, 8),
(6, '#IMAS6', 'Asus Rog Zephyrus G14 ', 28990000, NULL, '<p>14\", Full HD</p>\r\n\r\n<p>CPU: AMD Ryzen 7, 3.00 GHz</p>\r\n\r\n<p>Card: NVIDIA GeForce GTX 1650 4 GB</p>\r\n\r\n<p>Nặng: 1.6 Kg, 4-cell Li-ion, 76 Wh</p>', '<h3>Mạnh mẽ v&agrave; di động l&agrave; những đặc t&iacute;nh nổi bật của&nbsp;<a href=\"https://www.thegioididong.com/laptop/asus-rog-zephyrus-g14-ga401qh-r7-hz035t\" target=\"_blank\" title=\"Laptop Asus Rog Zephyrus G14 GA401QH R7 5800HS/8GB/512GB/4GB GTX1650/144Hz/Túi/Win10 (HZ035T) \">Asus Rog Zephyrus G14 GA401QH R7 (HZ035T)</a>, đ&acirc;y ch&iacute;nh l&agrave; chiếc laptop gaming xứng đ&aacute;ng được bạn sở hữu với CPU AMD Ryzen 7 c&ugrave;ng GPU d&ograve;ng GeForce RTX, mang đến cho người d&ugrave;ng những trải nghiệm l&agrave;m việc cũng như giải tr&iacute; ho&agrave;n hảo.</h3>\r\n\r\n<h3>Sức mạnh vượt trội</h3>\r\n\r\n<p>Laptop&nbsp;<a href=\"https://www.thegioididong.com/laptop-asus-rog\" target=\"_blank\" title=\"Một số laptop Asus ROG đang kinh doanh tại thegioididong.com\">Asus ROG</a>&nbsp;được trang bị bộ vi xử l&yacute;&nbsp;<a href=\"https://www.thegioididong.com/laptop-cpu-amd\" target=\"_blank\" title=\"Một số laptop sở hữu chip AMD đang kinh doanh tại thegioididong.com\">AMD</a>&nbsp;<strong>Ryzen 7</strong>&nbsp;được sản xuất tr&ecirc;n tiến tr&igrave;nh&nbsp;<strong>7 nm</strong>&nbsp;với&nbsp;<strong>8 nh&acirc;n 16 luồng</strong>&nbsp;c&oacute; tốc độ cơ bản&nbsp;<strong>3.00 GHz&nbsp;</strong>v&agrave; đạt tối đa đến&nbsp;<strong>4.3 GHz</strong>&nbsp;nhờ&nbsp;<strong>Turbo Boost</strong>, hỗ trợ bạn xử l&yacute; nhanh gọn mọi t&aacute;c vụ từ cơ bản đến thiết kế chuy&ecirc;n nghiệp cũng như chơi tốt c&aacute;c tựa game hạng nặng như Final Fantasy XV, Forza Motorsport 7, LOL, CS:GO,... nhưng vẫn tiết kiệm điện năng tối ưu.</p>\r\n\r\n<p>Đa nhiệm hơn nhờ<a href=\"https://www.thegioididong.com/laptop-8-gb\" target=\"_blank\" title=\"Một số laptop sở hữu RAM 8 GB đang kinh doanh tại thegioididong.com\">&nbsp;RAM 8 GB</a>&nbsp;chuẩn&nbsp;<strong>DDR4 2 khe&nbsp;(1 khe 8 GB onboard + 1 khe trống)&nbsp;</strong>với tốc độ Bus RAM&nbsp;<strong>3200 MHz</strong>, cho ph&eacute;p người d&ugrave;ng mở nhiều ứng dụng c&ugrave;ng l&uacute;c kh&ocirc;ng lo giật hay lag m&aacute;y, gi&uacute;p n&acirc;ng cao năng suất c&ocirc;ng việc. Đồng thời, m&aacute;y cũng được hỗ trợ n&acirc;ng cấp RAM l&ecirc;n đến&nbsp;<strong>24 GB</strong>, đ&aacute;p ứng tối đa cho nhu cầu sử dụng của bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-4.jpg\" onclick=\"return false;\"><img alt=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - Cấu hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-4.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-4.jpg\" title=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - Cấu hình\" /></a></p>\r\n\r\n<p>Xử l&yacute; c&aacute;c t&aacute;c vụ đồ họa mượt m&agrave; hơn bao giờ hết nhờ&nbsp;<a href=\"https://www.thegioididong.com/laptop-card-do-hoa-roi\" target=\"_blank\" title=\"Xem thêm laptop trang bị card đồ họa rời đang bán tại dienmayxanh.com\">card đồ họa rời</a><strong>&nbsp;NVIDIA</strong>&nbsp;<a href=\"https://www.thegioididong.com/laptop-geforce-gtx\" target=\"_blank\" title=\"Một số laptop sở hữu card đồ họa GeForce GTX đang kinh doanh tại thegioididong.com\">GeForce GTX</a>&nbsp;<strong>1650 4 GB</strong>&nbsp;cho bạn thỏa sức s&aacute;ng tạo v&agrave; l&agrave;m việc tr&ecirc;n c&aacute;c ứng dụng chuy&ecirc;n nghiệp như Photoshop, Illustrator, Premiere,...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-5.jpg\" onclick=\"return false;\"><img alt=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - Card đồ họa\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-5.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-5.jpg\" title=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - Card đồ họa\" /></a></p>\r\n\r\n<p>Kh&ocirc;ng gian lưu trữ rộng lớn với ổ cứng&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=ssd-512-gb\" target=\"_blank\" title=\"Một số laptop có ổ cứng SSD 512 GB đang kinh doanh tại thegioididong.com\">SSD 512 GB</a>&nbsp;<strong>NVMe PCIe</strong>, vừa cho ph&eacute;p bạn thoải m&aacute;i lưu giữ nhiều file hay ứng dụng y&ecirc;u th&iacute;ch, vừa tắng tốc độ mở v&agrave; vận h&agrave;nh m&aacute;y nhanh ch&oacute;ng chỉ trong v&agrave;i gi&acirc;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-6.jpg\" onclick=\"return false;\"><img alt=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - SSD\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-6.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-6.jpg\" title=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - SSD\" /></a></p>\r\n\r\n<h3>Ấn tượng với thiết kế độc đ&aacute;o, khẳng định c&aacute; t&iacute;nh ri&ecirc;ng</h3>\r\n\r\n<p>Phi&ecirc;n bản&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Một số laptop đang kinh doanh tại thegioididong.com\">laptop</a>&nbsp;n&agrave;y được chế t&aacute;c từ vỏ nhựa bền chắc với nắp lưng v&agrave; chiếu nghỉ tay được l&agrave;m từ kim loại, sở hữu sự tinh tế đến từng chi tiết đem đến một thế hệ đặc biệt nổi bật so với c&aacute;c laptop gaming c&ugrave;ng d&ograve;ng. Khung m&aacute;y gọn nhẹ chỉ&nbsp;<strong>1.6 kg</strong>, độ d&agrave;y&nbsp;<strong>17.9 mm</strong>&nbsp;sẵn s&agrave;ng đồng h&agrave;nh c&ugrave;ng bạn đến bất kỳ nơi n&agrave;o.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-2.jpg\" onclick=\"return false;\"><img alt=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-2.jpg\" title=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - Thiết kế\" /></a></p>\r\n\r\n<p>Mở m&aacute;y nhanh ch&oacute;ng với&nbsp;<strong>bảo mật v&acirc;n tay</strong>&nbsp;được t&iacute;ch hợp tr&ecirc;n n&uacute;t nguồn, gi&uacute;p đơn giản h&oacute;a việc đăng nhập của bạn, cho ph&eacute;p bạn truy cập v&agrave;o m&aacute;y một c&aacute;ch nhanh ch&oacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-9.jpg\" onclick=\"return false;\"><img alt=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - Vân tay\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-9.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-9.jpg\" title=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - Vân tay\" /></a></p>\r\n\r\n<p>B&agrave;n ph&iacute;m được&nbsp;thiết kế kh&ocirc;ng viền với c&aacute;c ph&iacute;m n&oacute;ng được đặt ri&ecirc;ng ở ph&iacute;a tr&ecirc;n b&ecirc;n tr&aacute;i gi&uacute;p bạn tăng giảm &acirc;m lượng, tắt micro hay khởi động phần mềm ROG Armoury Crate dễ d&agrave;ng hơn bao giờ hết.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-8.jpg\" onclick=\"return false;\"><img alt=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - Bàn phím\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-8.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-8.jpg\" title=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - Bàn phím\" /></a></p>\r\n\r\n<p>Đặc biệt,&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cach-bat-den-ban-phim-laptop-956550\" target=\"_blank\" title=\"Đèn nền bàn phím laptop là gì\">đ&egrave;n nền b&agrave;n ph&iacute;m</a>&nbsp;được trang bị gi&uacute;p game thủ dễ d&agrave;ng triển khai kỹ năng trong cả điều kiện thiếu s&aacute;ng. Bạn c&ograve;n c&oacute; thể trải nghiệm mọi thể loại game hấp dẫn với c&aacute;c ph&iacute;m bấm cao v&agrave; nhạy hơn nhờ c&ocirc;ng nghệ&nbsp;<strong>ROG Overstroke</strong>.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/245023/asus-rog-zephyrus-g14-ga401qh-r7-hz035t-08.jpg\" onclick=\"return false;\"><img alt=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - Đèn nền bàn phím\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/245023/asus-rog-zephyrus-g14-ga401qh-r7-hz035t-08.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/245023/asus-rog-zephyrus-g14-ga401qh-r7-hz035t-08.jpg\" title=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - Đèn nền bàn phím\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;,&nbsp;<a href=\"https://www.thegioididong.com/laptop-asus\" target=\"_blank\" title=\"Một số laptop Asus đang kinh doanh tại thegioididong.com\">Laptop Asus</a>&nbsp;c&oacute; thiết kế bản lề&nbsp;<strong>ErgoLift</strong>&nbsp;n&acirc;ng th&acirc;n m&aacute;y l&ecirc;n th&ecirc;m một g&oacute;c, đem đến cho người d&ugrave;ng cảm gi&aacute;c thoải m&aacute;i hơn khi g&otilde; ph&iacute;m.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-10.jpg\" onclick=\"return false;\"><img alt=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - Bản lề\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-10.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-10.jpg\" title=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - Bản lề\" /></a></p>\r\n\r\n<p>Dễ d&agrave;ng kết nối với nhiều thiết bị ngoại vi kh&aacute;c nhau với c&aacute;c cổng kết nối phổ biến được trang bị ở chiếc laptop n&agrave;y như: USB 3.2 Gen 2 Type-C support DisplayPort / power delivery / G-SYNC, 2 cổng USB 3.2, HDMI v&agrave; USB Type-C, cho bạn truyền xuất dữ liệu nhanh ch&oacute;ng kh&ocirc;ng cần chuyển đổi rườm r&agrave;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-14.jpg\" onclick=\"return false;\"><img alt=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - Cổng kết nối\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-14.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-14.jpg\" title=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - Cổng kết nối\" /></a></p>\r\n\r\n<p>Đảm bảo đường truyền ổn định với&nbsp;<strong>Wi-Fi 6 AX201</strong>&nbsp;v&agrave;&nbsp;<strong>Bluetooth 5.1&nbsp;</strong>cho bạn truyền tải dữ liệu dễ d&agrave;ng, kh&ocirc;ng bị gi&aacute;n đoạn d&ugrave; ở trong nh&agrave; hay ngo&agrave;i trời.</p>\r\n\r\n<h3>Giải tr&iacute; bất tận với h&igrave;nh ảnh c&ugrave;ng &acirc;m thanh chuy&ecirc;n nghiệp</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop-14-inch\" target=\"_blank\" title=\"Một số laptop 14 inch đang kinh doanh tại thegioididong.com\">M&agrave;n h&igrave;nh 14 inch</a>&nbsp;với độ ph&acirc;n giải&nbsp;<strong>Full HD&nbsp;(1920 x 1080)</strong>&nbsp;c&ugrave;ng&nbsp;độ s&aacute;ng m&agrave;n h&igrave;nh&nbsp;<strong>300 nits&nbsp;</strong>v&agrave;<strong>&nbsp;</strong>tần số qu&eacute;t&nbsp;<strong>144 Hz&nbsp;</strong>bắt trọn mọi hoặt cảnh game, đem đến h&igrave;nh ảnh r&otilde; n&eacute;t m&agrave; kh&ocirc;ng bị hiện tượng x&eacute; h&igrave;nh, cho trải nghiệm chơi game thuận lợi, đầy kịch t&iacute;nh trong điều kiện &aacute;nh s&aacute;ng kh&ocirc;ng qu&aacute; cao.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-12.jpg\" onclick=\"return false;\"><img alt=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - Tần số quét\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-12.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-12.jpg\" title=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - Tần số quét\" /></a></p>\r\n\r\n<p>Thiết kế h&igrave;nh ảnh chuy&ecirc;n nghiệp hơn nhờ m&agrave;u sắc được t&aacute;i tạo ch&iacute;nh x&aacute;c với&nbsp;<strong>100% sRGB</strong>.</p>\r\n\r\n<p>Tầm nh&igrave;n rộng mở đến&nbsp;<strong>178 độ</strong>&nbsp;nhờ tấm nền&nbsp;<strong>IPS&nbsp;</strong>c&ugrave;ng c&ocirc;ng nghệ&nbsp;<a href=\"https://www.thegioididong.com/laptop-man-hinh-chong-choi\" target=\"_blank\" title=\"Một số laptop có công nghệ Anti Glare đang kinh doanh tại thegioididong.com\">Anti Glare</a>, đem đến những h&igrave;nh ảnh sắc n&eacute;t, cho bạn đắm ch&igrave;m trong kh&ocirc;ng gian tr&ograve; chơi sống động như thật trong bất kỳ điều kiện &aacute;nh s&aacute;ng n&agrave;o m&agrave; kh&ocirc;ng lo ảnh hưởng ti&ecirc;u cực đến mắt.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t11.jpg\" onclick=\"return false;\"><img alt=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - Hình ảnh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t11.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t11.jpg\" title=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - Hình ảnh\" /></a></p>\r\n\r\n<p>C&ocirc;ng nghệ &acirc;m thanh&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-dolby-atmos-tren-smartphone-772074\" target=\"_blank\" title=\"Tìm hiểu Dolby Atmos là gì? Làm sao để trải nghiệm công nghệ âm thanh đỉnh cao?\">Dolby Atmos</a>&nbsp;đem đến khả năng t&aacute;i tạo &acirc;m thanh v&ograve;m sống động c&ugrave;ng bộ lọc &acirc;m thanh, cho bạn những trải nghiệm giải tr&iacute; thư gi&atilde;n hơn bao giờ hết.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-13.jpg\" onclick=\"return false;\"><img alt=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - Âm thanh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-13.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/245023/laptopasus-rog-zephyrus-g14-ga401qh-r7-hz035t-13.jpg\" title=\"Asus Rog Zephyrus G14 GA401QH R7 5800HS (HZ035T) - Âm thanh\" /></a></p>\r\n\r\n<p>Hệ thống l&agrave;m m&aacute;t th&ocirc;ng minh với&nbsp;<strong>81 c&aacute;nh quạt k&eacute;p n-Blade&nbsp;</strong>kết hợp với c&aacute;c t&iacute;nh năng v&agrave; c&agrave;i đặt th&iacute;ch hợp đem đến trải nghiệm tốt nhất cho người d&ugrave;ng khi đang đắm ch&igrave;m trong kh&ocirc;ng gian tr&ograve; chơi.</p>\r\n\r\n<p>Laptop&nbsp;Asus Rog Zephyrus G14 GA401QH R7 (HZ035T) sở hữu thiết kế ấn tượng đầy nổi bật c&ugrave;ng hiệu năng vượt trội, l&agrave; sự lựa chọn l&iacute; tưởng cho những ai đang muốn sở hữu một chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=laptop-gaming\" target=\"_blank\" title=\"Một số laptop gaming đang kinh doanh tại thegioididong.com\">laptop gaming</a>&nbsp;nổi trội.</p>\r\n', 4, 'asus-rog-zephyrus-g14-ga401qh-r7-5800hs-hz035t-6.html', 'Thu Thủy', '', 'public/uploads/asus-rog-zephyrus-g14-ga401qh-r7-5800hs-8gb-512gb-600x600.jpg', '2', 1625615167, 1625619829, 2, 6);
INSERT INTO `tbl_product` (`product_id`, `product_code`, `product_name`, `product_price`, `old_price`, `product_desc`, `content`, `qty`, `slug`, `creator`, `editor`, `product_thumb`, `product_status`, `add_date`, `update_date`, `cat_id`, `child_id`) VALUES
(7, '#IMXM7', 'Xiaomi Mi 11 5G', 18990000, 19990000, '<p>M&agrave;n h&igrave;nh 6.81&quot;, Chip Snapdragon 888</p>\r\n\r\n<p>RAM 8 GB, ROM 256 GB</p>\r\n\r\n<p>Camera sau: Ch&iacute;nh 108 MP &amp; Phụ 13 MP, 5 MP</p>\r\n\r\n<p>Camera trước: 20 MP</p>\r\n\r\n<p>Pin 4600 mAh, Sạc 55 W</p>\r\n', '<h3><a href=\"https://www.thegioididong.com/dtdd/xiaomi-mi-11\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo điện thoại Xiaomi Mi 11 chính hãng tại Thegioididong.com\" type=\"Tham khảo điện thoại Xiaomi Mi 11 chính hãng tại Thegioididong.com\">Xiaomi Mi 11</a>&nbsp;một si&ecirc;u phẩm đến từ&nbsp;<a href=\"https://www.thegioididong.com/dtdd-xiaomi\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo giá smartphone Xiaomi chính hãng đang kinh doanh tại Thegioididong.com\" type=\"Tham khảo giá smartphone Xiaomi chính hãng đang kinh doanh tại Thegioididong.com\">Xiaomi</a>, m&aacute;y cho trải nghiệm hiệu năng h&agrave;ng đầu với vi xử l&yacute; Qualcomm Snapdragon 888, c&ugrave;ng loạt c&ocirc;ng nghệ đỉnh cao, khiến bất kỳ ai cũng sẽ cho&aacute;ng ngợp về&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo các mẫu điện thoại, smartphone giá tốt, chính hãng tại Thegioididong.com\" type=\"Tham khảo các mẫu điện thoại, smartphone giá tốt, chính hãng tại Thegioididong.com\">smartphone</a>&nbsp;n&agrave;y.</h3>\r\n\r\n<h3>Thiết kế nổi bật với cụm camera độc nhất</h3>\r\n\r\n<p>C&oacute; thể thấy, điểm s&aacute;ng trong thiết kế của Mi 11 đến từ cụm 3 camera mặt sau được đặt trong m&ocirc;-đun h&igrave;nh vu&ocirc;ng, được ph&acirc;n tầng với 2 lớp k&iacute;nh tạo n&ecirc;n sự kh&aacute;c biệt v&agrave; nổi bật ngay từ c&aacute;i nh&igrave;n đầu ti&ecirc;n.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/226264/xiaomi-mi-11-002.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 11 | Thiết kế với cụm camera độc đáo\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-002.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-002.jpg\" title=\"Xiaomi Mi 11 | Thiết kế với cụm camera độc đáo\" /></a></p>\r\n\r\n<p>Mi 11 sở hữu th&acirc;n m&aacute;y thanh mảnh với độ mỏng ấn tượng 8.06 mm, được vuốt cong dần về cạnh 2 b&ecirc;n, nối liền ho&agrave;n hảo với phần khung kim loại si&ecirc;u mỏng v&agrave; m&agrave;n h&igrave;nh ở mặt trước tạo n&ecirc;n một khối liền mạch, cho bạn trải nghiệm cầm nắm tốt nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/226264/xiaomi-mi-11-121621-031604.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 11 | Độ mỏng ấn tượng 8.06 mm\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-121621-031604.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-121621-031604.jpg\" title=\"Xiaomi Mi 11 | Độ mỏng ấn tượng 8.06 mm\" /></a></p>\r\n\r\n<h3>L&agrave;m chủ mọi khung h&igrave;nh với bộ 3 camera 108 MP</h3>\r\n\r\n<p>Với tổ hợp 3 cảm biến gồm c&oacute; camera ch&iacute;nh 108 MP,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-goc-rong\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại có camera góc rộng. góc siêu rộng tại Thgioididong.com\" type=\"Tham khảo các dòng điện thoại có camera góc rộng. góc siêu rộng tại Thgioididong.com\">camera g&oacute;c si&ecirc;u rộng</a>&nbsp;13 MP v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-macro\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo các mẫu điện thoại có camera chụp cận macro chính hãng tại Thegioididong.com\" type=\"Tham khảo các mẫu điện thoại có camera chụp cận macro chính hãng tại Thegioididong.com\">camera chụp cận macro</a>&nbsp;5 MP, c&ugrave;ng hệ thống phần cứng tối t&acirc;n được trang bị b&ecirc;n trong. Tất cả đ&atilde; l&agrave;m n&ecirc;n đẳng cấp nhiếp ảnh di động chuy&ecirc;n nghiệp tr&ecirc;n Mi 11.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/226264/xiaomi-mi-11-003.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 11 | Cụm 3 camera độ phân giải lên đến 108 MP\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-003.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-003.jpg\" title=\"Xiaomi Mi 11 | Cụm 3 camera độ phân giải lên đến 108 MP\" /></a></p>\r\n\r\n<p>T&iacute;ch hợp b&ecirc;n trong camera ch&iacute;nh 108 MP l&agrave; cảm biến h&igrave;nh ảnh lớn 1/1.33 c&oacute; hỗ trợ chống rung quang học OIS, với c&ocirc;ng nghệ pixel-binning cho ph&eacute;p gộp 4 điểm ảnh th&agrave;nh 1 điểm ảnh si&ecirc;u s&aacute;ng, Mi 11 cho ph&eacute;p người d&ugrave;ng chụp những bức ảnh trong điều kiện thiếu s&aacute;ng m&agrave; vẫn c&oacute; độ n&eacute;t ho&agrave;n hảo.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/226264/xiaomi-mi-11-011.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 11 | Cảm biến chính chống rung quang học, chụp thiếu sáng sắc nét\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-011.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-011.jpg\" title=\"Xiaomi Mi 11 | Cảm biến chính chống rung quang học, chụp thiếu sáng sắc nét\" /></a></p>\r\n\r\n<p>Chụp phong cảnh, ảnh nh&oacute;m dễ d&agrave;ng từ camera g&oacute;c si&ecirc;u rộng 123 độ 13 MP hay chụp ảnh ch&acirc;n dung ảo diệu với hiệu ứng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-xoa-phong\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại có camera chụp chân dung xóa phông tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại có camera chụp chân dung xóa phông tại Thegioididong.com\">x&oacute;a ph&ocirc;ng</a>&nbsp;h&uacute;t mắt.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/226264/xiaomi-mi-11-012.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 11 | Ảnh chụp trên camera\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-012.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-012.jpg\" title=\"Xiaomi Mi 11 | Ảnh chụp trên camera\" /></a></p>\r\n\r\n<p>Cuối c&ugrave;ng, camera macro sẽ gi&uacute;p bạn chụp si&ecirc;u cận ở khoảng c&aacute;ch gần, mang đến những t&aacute;c phẩm macro ấn tượng, độc lạ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/226264/xiaomi-mi-11-121121-031142.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 11 | Ảnh chụp trên camera macro\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-121121-031142.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-121121-031142.jpg\" title=\"Xiaomi Mi 11 | Ảnh chụp trên camera macro\" /></a></p>\r\n\r\n<p>Kh&ocirc;ng chỉ chụp ảnh, Mi 11 c&ograve;n sở hữu khả năng quay video nổi trội với chất lượng 8K, k&egrave;m theo đ&oacute; l&agrave; c&aacute;c thuật to&aacute;n AI v&agrave; giảm nhiễu RAW gi&uacute;p tăng cường &aacute;nh s&aacute;ng khi quay video trong điều kiện &aacute;nh s&aacute;ng yếu.</p>\r\n\r\n<h3>Hiệu năng h&agrave;ng khủng với Snapdragon 888</h3>\r\n\r\n<p>Được trang bị vi xử l&yacute; Qualcomm Snapdragon 888 cao cấp nhất, vừa được ra mắt gần đ&acirc;y với tiến tr&igrave;nh 5 nm. Trang bị l&otilde;i Arm Cortex-X1 với xung nhịp khủng 2.84 GHz gi&uacute;p tốc độ xử l&yacute; CPU tăng 25%, đồng thời tiết kiệm pin hơn so với Snapdragon 865.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/226264/xiaomi-mi-11-122221-032200.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 11 | Trang bị vi xử lý Qualcomm Snapdragon 888\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-122221-032200.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-122221-032200.jpg\" title=\"Xiaomi Mi 11 | Trang bị vi xử lý Qualcomm Snapdragon 888\" /></a></p>\r\n\r\n<p>C&ugrave;ng sự nhảy vọt đ&aacute;ng kể về khả năng đồ họa với GPU Adreno 660 tăng 35%, Mi 11 ho&agrave;n to&agrave;n c&oacute; thể chạy mượt bất kỳ tựa game mobile &ldquo;hạng nặng&rdquo; n&agrave;o.</p>\r\n\r\n<p>Mi 11 c&ograve;n dẫn đầu về tốc độ xử l&yacute; AI khi trang bị bộ xử l&yacute; AI Engine thế hệ thứ 6 Hexagon 780 gi&uacute;p cho m&aacute;y c&oacute; thể xử l&yacute; h&igrave;nh ảnh, video v&agrave; c&aacute;c t&aacute;c vụ AI, m&aacute;y học diễn ra nhanh ch&oacute;ng hơn.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/226264/xiaomi-mi-11-114921-114947.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 11 | Trang bị bộ xử lý AI Engine thế hệ thứ 6 Hexagon 780 xử lý ảnh video nhanh hơn\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-114921-114947.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-114921-114947.jpg\" title=\"Xiaomi Mi 11 | Trang bị bộ xử lý AI Engine thế hệ thứ 6 Hexagon 780 xử lý ảnh video nhanh hơn\" /></a></p>\r\n\r\n<p>Kết hợp với&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-8gb\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo điện thoại có RAM 8 GB tại Thegioididong.com\" type=\"Tham khảo điện thoại có RAM 8 GB tại Thegioididong.com\">8 GB RAM</a>&nbsp;LPDDR5 v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd-rom-256gb\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo điện thoại có bộ nhớ 256 GB tại Thegioididong.com\" type=\"Tham khảo điện thoại có bộ nhớ 256 GB tại Thegioididong.com\">256 GB bộ nhớ trong</a>&nbsp;chuẩn UFS 3.1 với tốc độ h&agrave;ng đầu, c&ugrave;ng c&ocirc;ng nghệ tản nhiệt chất lỏng VC, gi&uacute;p cho Mi 11 ph&aacute;t huy hết sức mạnh của vi xử l&yacute; Snapdragon 888 khi thực hiện c&aacute;c t&aacute;c vụ nặng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/226264/xiaomi-mi-11-010.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 11 | Khay sim\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-010.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-010.jpg\" title=\"Xiaomi Mi 11 | Khay sim\" /></a></p>\r\n\r\n<p>Mi 11 được t&iacute;ch hợp MIUI 12.5 mới nhất hoạt động tr&ecirc;n nền Android 11, được xem l&agrave; bản cập nhật c&oacute; nhiều cải tiến về bảo mật, tối ưu hiệu năng tốt hơn nhờ sử dụng bộ nhớ nền &iacute;t hơn 35% v&agrave; giảm 25% mức ti&ecirc;u thụ điện năng nhưng kh&ocirc;ng l&agrave;m giảm trải nghiệm v&agrave; sự mượt m&agrave; trong mọi t&aacute;c vụ.</p>\r\n\r\n<h3>Sạc nhanh c&ocirc;ng suất cao 55 W</h3>\r\n\r\n<p>Với thiết kế mỏng, nhưng Mi 11 vẫn sở hữu vi&ecirc;n pin với dung lượng ấn tượng 4.600 mAh, đảm bảo thoải m&aacute;i cho một ng&agrave;y sử dụng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/226264/xiaomi-mi-11-115021-115040.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 11 | Pin với dung lượng 4.600 mAh ấn tượng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-115021-115040.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-115021-115040.jpg\" title=\"Xiaomi Mi 11 | Pin với dung lượng 4.600 mAh ấn tượng\" /></a></p>\r\n\r\n<p>Trang bị&nbsp;c&ocirc;ng nghệ&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo các mẫu điện thoại có công nghệ sạc pin nhanh tại Thegioididong.com\" type=\"Tham khảo các mẫu điện thoại có công nghệ sạc pin nhanh tại Thegioididong.com\">sạc pin nhanh</a>&nbsp;Mi TurboCharge với c&ocirc;ng suất 55 W, gi&uacute;p cho Mi 11 được sạc đầy chỉ trong 45 ph&uacute;t, thậm ch&iacute;&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-khong-day\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại có tích hợp sạc không dây tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại có tích hợp sạc không dây tại Thegioididong.com\">sạc kh&ocirc;ng d&acirc;y</a>&nbsp;50 W cũng nhanh kh&ocirc;ng k&eacute;m chỉ với 53 ph&uacute;t.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/226264/xiaomi-mi-11-009.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 11 | Trang bị công nghệ sạc pin nhanh Mi TurboCharge với công suất 55 W\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-009.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-009.jpg\" title=\"Xiaomi Mi 11 | Trang bị công nghệ sạc pin nhanh Mi TurboCharge với công suất 55 W\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, t&iacute;nh năng sạc ngược kh&ocirc;ng d&acirc;y 10 W gi&uacute;p bạn chia sẻ năng lượng cho những thiết bị kh&aacute;c v&ocirc; c&ugrave;ng tiện lợi.</p>\r\n\r\n<h3>Trải nghiệm đỉnh cao với m&agrave;n h&igrave;nh 120 Hz</h3>\r\n\r\n<p>Trang bị&nbsp;<a href=\"https://www.thegioididong.com/dtdd-man-hinh-sieu-tran-vien\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo các dòng điện thoại siêu tràn viền tại Thegioididong.com\" type=\"Tham khảo các dòng điện thoại siêu tràn viền tại Thegioididong.com\">m&agrave;n h&igrave;nh si&ecirc;u tr&agrave;n viền</a>&nbsp;được uốn cong th&aacute;c nước tuyệt đẹp với camera &ldquo;đục lỗ&rdquo;, sử dụng tấm nền AMOLED, độ ph&acirc;n giải 2K+, đảm bảo cho mọi chi tiết, đồ họa, h&igrave;nh ảnh đều hiển thị với chất lượng cao nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/226264/xiaomi-mi-11-001.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 11 | Trang bị màn hình siêu tràn viền với camera đục lỗ\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-001.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-001.jpg\" title=\"Xiaomi Mi 11 | Trang bị màn hình siêu tràn viền với camera đục lỗ\" /></a></p>\r\n\r\n<p>Sử dụng vật liệu ph&aacute;t s&aacute;ng E4 mới cung cấp độ s&aacute;ng 1.500 nits, việc d&ugrave;ng ngo&agrave;i trời nắng kh&ocirc;ng c&ograve;n l&agrave; trở ngại với Mi 11.</p>\r\n\r\n<p>Mi 11 hỗ trợ tốc độ l&agrave;m tươi 120 Hz v&agrave; tốc độ lấy mẫu cảm ứng cực cao l&agrave; 480 Hz mang đến sự mượt m&agrave; mỗi khi lướt cảm ứng v&agrave; l&agrave;m tăng trải nghiệm đặc biệt l&agrave; khi bạn chơi game.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/226264/xiaomi-mi-11-006.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 11 | Kính cường lực Corning Gorilla Glass Victus bảo vệ tuyệt đối\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-006.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-006.jpg\" title=\"Xiaomi Mi 11 | Kính cường lực Corning Gorilla Glass Victus bảo vệ tuyệt đối\" /></a></p>\r\n\r\n<p>M&agrave;n h&igrave;nh của Mi 11 cũng được phủ l&ecirc;n mặt k&iacute;nh cường lực Corning Gorilla Glass Victus gi&uacute;p bảo vệ m&agrave;n h&igrave;nh khỏi c&aacute;c sự cố nứt vỡ trong khi sử dụng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/226264/xiaomi-mi-11-121121-031158.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Mi 11 | Trang bị kính cường lực Corning Gorilla Glass Victus\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-121121-031158.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-121121-031158.jpg\" title=\"Xiaomi Mi 11 | Trang bị kính cường lực Corning Gorilla Glass Victus\" /></a></p>\r\n\r\n<p>Sở hữu vi xử l&yacute; Snapdragon 888, Mi 11 được dự đo&aacute;n sẽ l&agrave; một t&acirc;n binh &quot;đ&aacute;ng gờm&quot; mở đầu cuộc đua hiệu năng đầy s&ocirc;i động trong 2021. Tuy l&agrave; một thiết bị cao cấp nhưng Mi 11 vẫn sẽ c&oacute; mức gi&aacute; cực kỳ hợp l&yacute;, cho ph&eacute;p tiếp cận đến nhiều đối tượng kh&aacute;ch h&agrave;ng hơn.</p>\r\n', 2, 'xiaomi-mi11-5g-7.html', 'Minh Tuấn', '', 'public/uploads/xiaomi-mi-11.jpg', '2', 1625730026, NULL, 1, 4),
(8, '#IMXM8', 'Xiaomi Redmi Note 10 5G', 17990000, NULL, '<p>M&agrave;n h&igrave;nh 6.2&quot;, Chip Exynos 2100</p>\r\n\r\n<p>RAM 8 GB, ROM 128 GB</p>\r\n\r\n<p>Camera sau: Ch&iacute;nh 12 MP &amp; Phụ 64 MP, 12 MP</p>\r\n\r\n<p>Camera trước: 10 MP</p>\r\n\r\n<p>Pin 4000 mAh, Sạc 25 W</p>\r\n', '<h3><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s21\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo điện thoại Galaxy S21 tại thegioididong.com\">Galaxy S21 5G</a>&nbsp;nằm trong series S21 đến từ&nbsp;<a href=\"https://www.thegioididong.com/samsung\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo sản phẩm Samsung kinh doanh tại thegioididong.com\">Samsung</a>&nbsp;nổi bật với thiết kế tr&agrave;n viền, cụm camera ấn tượng cho đến hiệu năng mạnh mẽ h&agrave;ng đầu.</h3>\r\n\r\n<h3>&ldquo;Bộ c&aacute;nh&rdquo; mới nổi bật v&agrave; sang trọng</h3>\r\n\r\n<p>Nổi bật với cụm camera sau được thiết kế đầy mới lạ, phần khu&ocirc;n h&igrave;nh chữ nhật chứa bộ 3 camera &ocirc;m trọn cạnh tr&aacute;i của chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo điện thoại kinh doanh tại thegioididong.com\">smartphone</a>, viền khu&ocirc;n cong uyển chuyển, hạn chế tối đa độ nh&ocirc; ra so với mặt lưng, mang lại c&aacute;i nh&igrave;n tổng thể h&agrave;i h&ograve;a, độc đ&aacute;o.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220833/samsung-galaxy-s21-002.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 5G | Thiết kế nổi bật với cụm camera chữ nhật\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/220833/samsung-galaxy-s21-002.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/220833/samsung-galaxy-s21-002.jpg\" title=\"Samsung Galaxy S21 5G | Thiết kế nổi bật với cụm camera chữ nhật\" /></a></p>\r\n\r\n<p>Galaxy S21 c&oacute; 3 m&agrave;u sắc ch&iacute;nh l&agrave;: T&iacute;m, trắng v&agrave; x&aacute;m. Cho người d&ugrave;ng thoải m&aacute;i lựa chọn để ph&ugrave; hợp với phong c&aacute;ch của bản th&acirc;n nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220833/galaxy_s21_5g_20.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 5G | Với 3 màu sắc chính là: Tím, trắng và xám\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_20.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_20.jpg\" title=\"Samsung Galaxy S21 5G | Với 3 màu sắc chính là: Tím, trắng và xám\" /></a></p>\r\n\r\n<p>Samsung Galaxy S21 hỗ trợ đầy đủ t&iacute;nh năng bảo mật sinh trắc học, bảo mật tối đa điện thoại của bạn bằng c&ocirc;ng nghệ&nbsp;<a href=\"https://www.thegioididong.com/dtdd-bao-mat-van-tay\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo sản phẩm điện thoại bảo mật vân tay tại Thế Giới Di Động\">mở kh&oacute;a v&acirc;n tay</a>&nbsp;si&ecirc;u &acirc;m đặt dưới m&agrave;n h&igrave;nh&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd-bao-mat-khuon-mat\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo các sản phẩm điện thoại bảo mật khuôn mặt tại Thế Giới Di Động\">bảo mật khu&ocirc;n mặt</a>.&nbsp;Kết hợp với t&iacute;nh năng chạm 2 lần s&aacute;ng m&agrave;n h&igrave;nh giờ đ&acirc;y bạn c&oacute; thể bật, mở kh&oacute;a điện thoại cực k&igrave; nhanh ch&oacute;ng, tiện lợi v&agrave; an to&agrave;n.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220833/samsung-galaxy-s21-007.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 5G | Trang bị mở khóa vân tay siêu âm đặt dưới màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/220833/samsung-galaxy-s21-007.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/220833/samsung-galaxy-s21-007.jpg\" title=\"Samsung Galaxy S21 5G | Trang bị mở khóa vân tay siêu âm đặt dưới màn hình\" /></a></p>\r\n\r\n<p>Galaxy S21 l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-chong-nuoc-bui\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo điện thoại kháng nước, bụi kinh doanh tại thegioididong.com\">điện thoại kh&aacute;ng nước, bụi</a>&nbsp;đạt chuẩn IP68, chuẩn cao nhất hiện tại. M&aacute;y c&oacute; thể sống s&oacute;t ở độ s&acirc;u 1.5m l&ecirc;n đến 30 ph&uacute;t, gi&uacute;p hạn chế được khả năng hư hỏng thiết bị khi bị nước đổ v&agrave;o, trời mưa hoặc kh&ocirc;ng may l&agrave;m rơi xuống nước.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220833/galaxy_s21_5g_17.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 5G | Kháng nước, bụi đạt chuẩn IP68\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_17.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_17.jpg\" title=\"Samsung Galaxy S21 5G | Kháng nước, bụi đạt chuẩn IP68\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh v&ocirc; cực Infinity-O si&ecirc;u mượt</h3>\r\n\r\n<p>Galaxy S21 được trang bị m&agrave;n h&igrave;nh&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-man-hinh-dynamic-amoled-2x-tren-smartphone-1245213\" rel=\"noopener\" target=\"_blank\" title=\"Tìm hiểu màn hình Dynamic AMOLED 2X trên smartphone\">Dynamic AMOLED 2X</a>, hỗ trợ HDR10+ ti&ecirc;n tiến do Samsung ph&aacute;t triển, c&oacute; thể đạt được độ s&aacute;ng cao nhất l&ecirc;n đến&nbsp;1300 nits, ngay cả khi dưới &aacute;nh s&aacute;ng mặt trời Galaxy S21 vẫn cho h&igrave;nh ảnh sống động, r&otilde; r&agrave;ng đến kh&ocirc;ng ngờ, sẽ n&acirc;ng trải nghiệm thị gi&aacute;c của bạn l&ecirc;n một tầm cao mới.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220833/samsung-galaxy-s21-001.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 5G | Trang bị màn hình Dynamic AMOLED 2X, hỗ trợ HDR10+ tiên tiến\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/220833/samsung-galaxy-s21-001.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/220833/samsung-galaxy-s21-001.jpg\" title=\"Samsung Galaxy S21 5G | Trang bị màn hình Dynamic AMOLED 2X, hỗ trợ HDR10+ tiên tiến\" /></a></p>\r\n\r\n<p>Galaxy S21 sở hữu m&agrave;n h&igrave;nh k&iacute;ch thước 6.2 inch độ ph&acirc;n giải&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/do-phan-giai-man-hinh-qhd-hd-fullhd-2k-4k-la-gi--592178#fullhd\" rel=\"noopener\" target=\"_blank\" title=\"Độ phân giải màn hình qHD, HD, FullHD, 2K, 4K là gì?\">Full HD+</a>&nbsp;c&oacute; tần số qu&eacute;t 120 Hz, gấp đ&ocirc;i so với thế hệ trước. Kh&ocirc;ng chỉ phản hồi cực nhạy, m&agrave; m&agrave;n h&igrave;nh c&ograve;n tối ưu tần số qu&eacute;t trong mỗi lần bạn thực hiện thao t&aacute;c chạm, cho mọi thao t&aacute;c chạm của bạn đều mịn m&agrave;ng, nhanh nhạy v&agrave; c&oacute; độ ch&iacute;nh x&aacute;c cao nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220833/galaxy_s21_5g_11.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 5G | Màn hình kích thước 6.2 inch độ phân giải Full HD+ có tần số quét 120 Hz\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_11.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_11.jpg\" title=\"Samsung Galaxy S21 5G | Màn hình kích thước 6.2 inch độ phân giải Full HD+ có tần số quét 120 Hz\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, Galaxy S21 đ&atilde; nhận được chứng nhận cho dải m&agrave;u sắc tr&ecirc;n di động, 100% phạm vi bao phủ DCI-P3 (chuẩn điện ảnh) cho h&igrave;nh ảnh kh&ocirc;ng bị nhạt m&agrave;u m&agrave; được hiển thị sống động, bất chấp mọi điều kiện &aacute;nh s&aacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220833/samsung-galaxy-s21-006.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 5G | Dải màu sắc trên di động chuẩn điện ảnh\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/220833/samsung-galaxy-s21-006.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/220833/samsung-galaxy-s21-006.jpg\" title=\"Samsung Galaxy S21 5G | Dải màu sắc trên di động chuẩn điện ảnh\" /></a><br />\r\nHơn thế nữa, chiếc điện thoại n&agrave;y c&oacute; khả năng giảm c&aacute;c t&aacute;c động của &aacute;nh s&aacute;ng xanh, gi&uacute;p bảo vệ sức khỏe đ&ocirc;i mắt của bạn, hạn chế t&igrave;nh trạng nhức, mỏi mắt khi sử dụng thiết bị trong thời gian d&agrave;i. Cho bạn thỏa sức tận hưởng những gi&acirc;y ph&uacute;t giải tr&iacute; đỉnh cao, bất kể ng&agrave;y đ&ecirc;m.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220833/galaxy_s21_5g_9.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 5G | Trang bị kính cường lực Corning Gorilla Glass Victus\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_9.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_9.jpg\" title=\"Samsung Galaxy S21 5G | Trang bị kính cường lực Corning Gorilla Glass Victus\" /></a></p>\r\n\r\n<p>Galaxy S21 được bảo vệ bởi&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-kinh-cuong-luc-corning-gorilla-glass-victus-1284586\" rel=\"noopener\" target=\"_blank\" title=\"Tìm hiểu kính cường lực Corning Gorilla Glass Victus smartphone\">k&iacute;nh cường lực Corning Gorilla Glass Victus</a>&nbsp;với khả năng chịu lực cực kỳ ấn tượng, hạn chế tối đa trầy xước v&agrave; hư hỏng. Đ&acirc;y l&agrave; loại k&iacute;nh cứng nhất tr&ecirc;n điện thoại th&ocirc;ng minh&nbsp;Samsung kể từ đầu năm 2021.</p>\r\n\r\n<h3>Camera cho h&igrave;nh ảnh ho&agrave;n hảo đến từng chi tiết</h3>\r\n\r\n<p>Th&ocirc;ng số m&aacute;y ảnh lu&ocirc;n l&agrave; niềm tự h&agrave;o của Samsung. Galaxy S21 chiếc điện thoại sở hữu 3 camera sau trong đ&oacute;&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/camera-goc-rong-goc-sieu-rong-tren-smartphone-la-1172240\" rel=\"noopener\" target=\"_blank\" title=\"Camera góc rộng, góc siêu rộng trên smartphone là gì?\">camera g&oacute;c rộng</a>,&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/camera-goc-rong-goc-sieu-rong-tren-smartphone-la-1172240\" rel=\"noopener\" target=\"_blank\" title=\"Camera góc rộng, góc siêu rộng trên smartphone là gì?\">g&oacute;c si&ecirc;u rộng</a>&nbsp;c&oacute; c&ugrave;ng độ ph&acirc;n giải l&agrave; 12 MP,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-zoom\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo sản phẩm điện thoại có camera tele tại Thế Giới Di Động\">camera tele</a>&nbsp;c&oacute; độ ph&acirc;n giải l&ecirc;n đến 64 MP. Ở mặt trước, camera selfie c&oacute; độ ph&acirc;n giải 10 MP.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220833/samsung-galaxy-s21-003.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 5G | Cụm camera sau\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/220833/samsung-galaxy-s21-003.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/220833/samsung-galaxy-s21-003.jpg\" title=\"Samsung Galaxy S21 5G | Cụm camera sau\" /></a></p>\r\n\r\n<p>Cụm camera n&agrave;y hỗ trợ đầy đủ c&aacute;c t&iacute;nh năng m&agrave; một người d&ugrave;ng cần. Chế độ chụp ch&acirc;n dung, g&oacute;c rộng, AI l&agrave;m đẹp,... lu&ocirc;n sẵn s&agrave;ng mang đến cho người d&ugrave;ng những bức ảnh xuất sắc m&agrave; kh&ocirc;ng cần chỉnh sửa qu&aacute; nhiều.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220833/galaxy_s21_5g_16.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 5G | Camera góc rộng, góc siêu rộng có cùng độ phân giải là12 MP\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_16.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_16.jpg\" title=\"Samsung Galaxy S21 5G | Camera góc rộng, góc siêu rộng có cùng độ phân giải là12 MP\" /></a></p>\r\n\r\n<p>Chế độ chụp đ&ecirc;m (<a href=\"https://www.thegioididong.com/hoi-dap/che-do-chup-dem-night-mode-la-gi-907873\" rel=\"noopener\" target=\"_blank\" title=\"Chế độ chụp đêm - Night mode là gì?\">Night Mode</a>)&nbsp;sẽ gi&uacute;p c&aacute;c cuộc vui trong đ&ecirc;m của bạn được ghi lại một c&aacute;ch r&otilde; r&agrave;ng.&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/zoom-quang-hoc-va-zoom-ky-thuat-so-la-gi-co-gi-khac-nhau-1296828#zoom-ky-thuat-so\" rel=\"noopener\" target=\"_blank\" title=\"Zoom quang học và zoom kỹ thuật số là gì? Có gì khác nhau?\">Zoom kỹ thuật số</a>&nbsp;l&ecirc;n đến 30x c&oacute; thể gi&uacute;p bạn tiếp cận được những vật thể ở xa với độ chi tiết cao nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220833/galaxy_s21_5g_18.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 5G | Chế độ chụp đêm (Night Mode), Zoom kỹ thuật số lên đến 30x \" data-src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_18.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_18.jpg\" title=\"Samsung Galaxy S21 5G | Chế độ chụp đêm (Night Mode), Zoom kỹ thuật số lên đến 30x \" /></a></p>\r\n\r\n<p>Samsung Galaxy S21 cho ph&eacute;p quay video chất lượng 8K si&ecirc;u cao. Đ&acirc;y l&agrave; độ ph&acirc;n giải m&agrave; &iacute;t c&oacute; d&ograve;ng smartphone n&agrave;o sở hữu được. Nhờ sự hỗ trợ của c&aacute;c c&ocirc;ng nghệ chống rung si&ecirc;u ổn định, c&ugrave;ng với một &iacute;t bộ lọc m&agrave;u người d&ugrave;ng c&oacute; thể s&aacute;ng tạo những thước phim h&agrave;ng ng&agrave;y, khi đi du lịch hay v&agrave;o dịp đặc biệt,... tất cả đều tr&ocirc;ng như những bộ phim chất lượng cao ngo&agrave;i rạp.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220833/samsung-galaxy-s21-008.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 5G | Giao diện chụp ảnh\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/220833/samsung-galaxy-s21-008.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/220833/samsung-galaxy-s21-008.jpg\" title=\"Samsung Galaxy S21 5G | Giao diện chụp ảnh\" /></a></p>\r\n\r\n<p>Camera selfie AI 10 MP hỗ trợ chụp g&oacute;c rộng,&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/che-do-chup-anh-hdr-tren-smartphone-la-gi-906400\" rel=\"noopener\" target=\"_blank\" title=\"Chế độ chụp ảnh HDR trên smartphone là gì?\">HDR</a>, chế độ tự động l&agrave;m đẹp k&egrave;m theo&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/ar-stickers-la-gi-vi-sao-hang-nao-cung-ap-dung-ar-1096228\" rel=\"noopener\" target=\"_blank\" title=\"AR Stickers là gì? Vì sao hãng nào cũng áp dụng AR\">Stickers</a>, người d&ugrave;ng thoải m&aacute;i s&aacute;ng tạo đa dạng kiểu mẫu ảnh m&agrave; m&igrave;nh th&iacute;ch. Ngo&agrave;i ra, camera n&agrave;y cũng sở hữu quay video độ ph&acirc;n giải l&ecirc;n đến 4K.&nbsp;</p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/42/220833/galaxy_s21_5g_15.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 5G | Camera selfie AI 10 MP hỗ trợ chụp góc rộng, HDR, làm đẹp thông minh\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_15.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_15.jpg\" title=\"Samsung Galaxy S21 5G | Camera selfie AI 10 MP hỗ trợ chụp góc rộng, HDR, làm đẹp thông minh\" /></a><br />\r\nVi xử l&yacute; Exynos 2100 mới nhất của Samsung&nbsp;</h3>\r\n\r\n<p>Cho đến th&aacute;ng 01/2021 th&igrave; CPU Exynos 2100 8 nh&acirc;n l&agrave; con chip thứ 2 của nh&agrave; Samsung được sản xuất tr&ecirc;n quy tr&igrave;nh 5 nm EUV, được cải tiến với tốc độ nhanh hơn, th&ocirc;ng minh hơn v&agrave; tiết kiệm điện hơn. Cho bạn thỏa sức l&agrave;m việc v&agrave; giải tr&iacute; hiệu quả với khả năng xử l&yacute; mạnh mẽ vượt trội.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220833/samsung-galaxy-s21-009.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 5G | Trang bị vi xử lý Exynos 2100\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/220833/samsung-galaxy-s21-009.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/220833/samsung-galaxy-s21-009.jpg\" title=\"Samsung Galaxy S21 5G | Trang bị vi xử lý Exynos 2100\" /></a></p>\r\n\r\n<p>CPU Exynos 2100 đi k&egrave;m với GPU Mali-G78 cải thiện hiệu suất đồ họa l&ecirc;n tới 40% so với thế hệ trước.&nbsp;Hiệu năng đo được tr&ecirc;n ứng dụng AnTuTu cho số liệu l&agrave; 460.537 điểm gi&uacute;p điện thoại chơi game tốt mượt m&agrave; tr&ecirc;n c&aacute;c d&ograve;ng game phổ biến hiện nay như PUBG Mobile, Li&ecirc;n Qu&acirc;n Mobile,...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220833/samsung-galaxy-s21-5g-test.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 5G | Điểm Antutu\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/220833/samsung-galaxy-s21-5g-test.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/220833/samsung-galaxy-s21-5g-test.jpg\" title=\"Samsung Galaxy S21 5G | Điểm Antutu\" /></a></p>\r\n\r\n<p>Chế độ Priority Mode mới từ Game Booster của h&atilde;ng sẽ chặn c&aacute;c cuộc gọi, th&ocirc;ng b&aacute;o, AI sẽ tự động điều chỉnh c&aacute;c c&agrave;i đặt để mang đến những trải nghiệm tốt nhất, đồng thời cải thiện hiệu suất của m&aacute;y l&ecirc;n mức tối đa.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220833/galaxy_s21_5g_6.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 5G | Chế độ Priority Mode mới từ Game Booster chặn cuộc gọi, thông báo\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_6.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_6.jpg\" title=\"Samsung Galaxy S21 5G | Chế độ Priority Mode mới từ Game Booster chặn cuộc gọi, thông báo\" /></a></p>\r\n\r\n<p>Đi k&egrave;m với CPU mạnh mẽ tr&ecirc;n l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-8gb\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo điện thoại có RAM 8 GB tại Thegioididong.com\">8 GB RAM</a>&nbsp;cho khả năng đa nhiệm mượt m&agrave;,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-rom-128gb\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo điện thoại có bộ nhớ 128 GB tại Thegioididong.com\">bộ nhớ trong 128 GB</a>&nbsp;cho ph&eacute;p người d&ugrave;ng thoải m&aacute;i lưu trữ dữ liệu cần thiết. Lưu &yacute; rằng chiếc smartphone n&agrave;y sẽ kh&ocirc;ng hỗ trợ thẻ nhớ ngo&agrave;i.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220833/galaxy_s21_5g_1.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 5G | RAM 8 GB đa nhiệm mượt mà, bộ nhớ trong 128 GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_1.jpg\" title=\"Samsung Galaxy S21 5G | RAM 8 GB đa nhiệm mượt mà, bộ nhớ trong 128 GB\" /></a></p>\r\n\r\n<p>Exynos 2100 l&agrave; một CPU tương th&iacute;ch với mạng 5G, sẽ mang đến tốc độ truyền tải nhanh hơn gấp nhiều lần khi so s&aacute;nh với mạng 4G, gi&uacute;p bạn tải l&ecirc;n hoặc tải xuống mọi dữ liệu trong t&iacute;ch tắc.</p>\r\n\r\n<h3>Vi&ecirc;n pin t&iacute;ch hợp nhiều c&ocirc;ng nghệ th&ocirc;ng minh</h3>\r\n\r\n<p>Samsung Galaxy S21 c&oacute; vi&ecirc;n pin dung lượng 4000 mAh, tuy dung lượng kh&ocirc;ng qu&aacute; cao nhưng khi kết hợp c&ugrave;ng c&ocirc;ng nghệ m&agrave;n h&igrave;nh mới, CPU được n&acirc;ng cấp. S21 c&oacute; thể c&ugrave;ng bạn học tập, giải tr&iacute; cả ng&agrave;y m&agrave; kh&ocirc;ng qu&aacute; lo lắng về vấn đề cạn năng lượng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220833/galaxy_s21_5g_10.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 5G | Viên pin dung lượng 4000 mAh\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_10.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_10.jpg\" title=\"Samsung Galaxy S21 5G | Viên pin dung lượng 4000 mAh\" /></a></p>\r\n\r\n<p>C&ocirc;ng nghệ&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo sản phẩm điện thoại sạc pin nhanh tại Thế Giới Di Động\">sạc nhanh</a>&nbsp;25 W tr&ecirc;n Galaxy S21 cung cấp cho người d&ugrave;ng h&agrave;ng giờ sử dụng chỉ trong v&agrave;i ph&uacute;t sạc. Ngo&agrave;i ra, c&ocirc;ng nghệ sạc nhanh kh&ocirc;ng d&acirc;y Qi 2.0 sẽ tiện lợi hơn nữa khi kh&ocirc;ng cần đến c&aacute;p sạc, gọn g&agrave;ng v&agrave; thuận tiện.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220833/samsung-galaxy-s21-005.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 5G | Công nghệ sạc nhanh 25 W\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/220833/samsung-galaxy-s21-005.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/220833/samsung-galaxy-s21-005.jpg\" title=\"Samsung Galaxy S21 5G | Công nghệ sạc nhanh 25 W\" /></a></p>\r\n\r\n<p>PowerShare đ&atilde; c&oacute; mặt tr&ecirc;n Galaxy S21, c&ocirc;ng nghệ n&agrave;y c&oacute; khả năng biến điện thoại th&agrave;nh một vi&ecirc;n pin dự ph&ograve;ng khi c&oacute; thể chia sẻ pin điện thoại của m&igrave;nh cho c&aacute;c thiết bị kh&aacute;c hỗ trợ sạc kh&ocirc;ng d&acirc;y như: Đồng hồ th&ocirc;ng minh hoặc điện thoại,... một t&iacute;nh năng v&ocirc; c&ugrave;ng lợi &iacute;ch.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/220833/galaxy_s21_5g_8.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 5G | Có thể chia sẻ pin điện thoại của mình cho các thiết bị khác\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_8.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_8.jpg\" title=\"Samsung Galaxy S21 5G | Có thể chia sẻ pin điện thoại của mình cho các thiết bị khác\" /></a></p>\r\n\r\n<p>Chiếc điện thoại Samsung Galaxy S21 5G c&ugrave;ng to&agrave;n bộ S21&nbsp;series mới hứa hẹn sẽ l&agrave;m mưa l&agrave;m gi&oacute; ở ph&acirc;n kh&uacute;c cao cấp, từ diện mạo sang trọng đến hiệu năng, từ c&ocirc;ng nghệ m&agrave;n h&igrave;nh đến cụm camera. Đ&acirc;y l&agrave; những yếu tố khẳng định đẳng cấp ch&iacute;nh chiếc điện thoại v&agrave; của chủ sở hữu.</p>\r\n', 2, 'dien-thoai-samsung-galaxy-s21-5g-8.html', 'Vũ Bảo', '', 'https://cdn.tgdd.vn/Products/Images/42/220833/TimerThumb/samsung-galaxy-s21-(8).jpg', '2', NULL, NULL, 1, 2);
INSERT INTO `tbl_product` (`product_id`, `product_code`, `product_name`, `product_price`, `old_price`, `product_desc`, `content`, `qty`, `slug`, `creator`, `editor`, `product_thumb`, `product_status`, `add_date`, `update_date`, `cat_id`, `child_id`) VALUES
(9, '#IMIP9', 'iPhone 12 64GB', 20690000, NULL, '<p>M&agrave;n h&igrave;nh 6.1&quot;, Chip Apple A14 Bionic</p>\r\n\r\n<p>RAM 4 GB, ROM 64 GB</p>\r\n\r\n<p>Camera sau: 2 camera 12 MP</p>\r\n\r\n<p>Camera trước: 12 MP</p>\r\n\r\n<p>Pin 2815 mAh, Sạc 20 W</p>\r\n', '<h3>Trong những th&aacute;ng cuối năm 2020,&nbsp;<a href=\"https://www.thegioididong.com/apple\" target=\"_blank\" title=\"Tham khảo sản phẩm chính hãng của Apple tại Thế Giới Di Động\">Apple</a>&nbsp;đ&atilde; ch&iacute;nh thức giới thiệu đến người d&ugrave;ng cũng như iFan thế hệ iPhone&nbsp;12&nbsp;series&nbsp;mới với h&agrave;ng loạt t&iacute;nh năng bứt ph&aacute;, thiết kế được lột x&aacute;c ho&agrave;n to&agrave;n, hiệu năng đầy mạnh mẽ v&agrave; một trong số đ&oacute; ch&iacute;nh l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-12\" target=\"_blank\" title=\"Tham khảo điện thoại iPhone 12 chính hãng tại Thế Giới Di Động\">iPhone 12 64GB</a>.</h3>\r\n\r\n<h3>Hiệu năng vượt xa mọi giới hạn</h3>\r\n\r\n<p>Apple đ&atilde; trang bị con chip mới nhất của h&atilde;ng (t&iacute;nh đến 11/2020) cho iPhone 12 đ&oacute; l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-chip-apple-a14-bionic-tren-iphone-12-va-ipad-1290695\" target=\"_blank\" title=\"Tìm hiểu chip A14 Bionic là gì?\">A14 Bionic</a>, được sản xuất tr&ecirc;n tiến tr&igrave;nh 5 nm với hiệu suất ổn định hơn so với chip A13 được trang bị tr&ecirc;n phi&ecirc;n bản tiền nhiệm&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-11\" target=\"_blank\" title=\"Tham khảo điện thoại iPhone 11 chính hãng tại Thế Giới Di Động\">iPhone 11</a>.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/213031/iphone-12-144220-044259.jpg\" onclick=\"return false;\"><img alt=\"Chip A14 Bionic mạnh mẽ | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-144220-044259.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-144220-044259.jpg\" title=\"Chip A14 Bionic mạnh mẽ | iPhone 12\" /></a></p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-chip-apple-a14-bionic-tren-iphone-12-va-ipad-1290695\" target=\"_blank\" title=\"Tìm hiểu về chip Apple A14 Bionic trên iPhone 12 và iPad Air 2020\">T&igrave;m hiểu về chip Apple A14 Bionic tr&ecirc;n iPhone 12 v&agrave; iPad Air 2020</a></p>\r\n\r\n<p>Với CPU Apple A14 Bionic, bạn c&oacute; thể dễ d&agrave;ng trải nghiệm mọi tựa game với những pha chuyển cảnh mượt m&agrave; hay h&agrave;ng loạt hiệu ứng đồ họa tuyệt đẹp ở mức đồ họa cao m&agrave; kh&ocirc;ng lo t&igrave;nh trạng giật lag.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/213031/iphone-12-18.jpg\" onclick=\"return false;\"><img alt=\"Chiến game siêu mượt, đồ họa tuyệt đẹp | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-18.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-18.jpg\" title=\"Chiến game siêu mượt, đồ họa tuyệt đẹp | iPhone 12\" /></a></p>\r\n\r\n<p>Chưa hết, Apple c&ograve;n g&acirc;y bất ngờ đến người d&ugrave;ng với hệ thống 5G lần đầu ti&ecirc;n được trang bị tr&ecirc;n những chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Tham khảo iPhone kinh doanh tại thegioididong.com\">iPhone</a>, cho tốc độ truyền tải dữ liệu nhanh hơn, ổn định hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/213031/iphone-12-20.jpg\" onclick=\"return false;\"><img alt=\"Kết nối 5G truyền tải dữ liệu nhanh chóng | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-20.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-20.jpg\" title=\"Kết nối 5G truyền tải dữ liệu nhanh chóng | iPhone 12\" /></a></p>\r\n\r\n<p>iPhone 12 sẽ chạy tr&ecirc;n hệ điều h&agrave;nh&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/ios-14-va-5-tinh-nang-moi-thu-vi-khong-the-bo-qua-tren-1268933\" target=\"_blank\" title=\"Tìm hiểu thêm những tính năng trên iOS 14\">iOS 14</a>&nbsp;với nhiều t&iacute;nh năng hấp dẫn như hỗ trợ Widget cũng như những n&acirc;ng cấp tối ưu phần mềm đ&aacute;ng kể mang lại những trải nghiệm th&uacute; vị mới lạ đến người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/213031/iphone-12-13.jpg\" onclick=\"return false;\"><img alt=\"Khám phá những tính năng mới hấp dẫn trên iOS 14 | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-13.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-13.jpg\" title=\"Khám phá những tính năng mới hấp dẫn trên iOS 14 | iPhone 12\" /></a></p>\r\n\r\n<h3>Cụm camera kh&ocirc;ng ngừng cải tiến</h3>\r\n\r\n<p>iPhone 12 được trang bị hệ thống camera k&eacute;p bao gồm&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-goc-rong\" target=\"_blank\" title=\"Tham khảo điện thoại có camera góc rộng tại Thế Giới Di Động\">camera g&oacute;c rộng</a>&nbsp;v&agrave; camera si&ecirc;u rộng c&oacute; c&ugrave;ng độ ph&acirc;n giải l&agrave; 12 MP, chế độ ban đ&ecirc;m (<a href=\"https://www.thegioididong.com/hoi-dap/che-do-chup-dem-night-mode-la-gi-907873\" target=\"_blank\" title=\"Tìm hiểu chế độ chụp đêm Night Mode là gì?\">Night Mode</a>) tr&ecirc;n bộ đ&ocirc;i camera n&agrave;y cũng đ&atilde; được n&acirc;ng cấp về phần cứng lẫn thuật to&aacute;n xử l&yacute;, khi chụp những bức ảnh thiếu s&aacute;ng bạn sẽ nhận được kết quả ấn tượng với m&agrave;u sắc, độ chi tiết r&otilde; n&eacute;t đ&aacute;ng kinh ngạc.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/213031/iphone-12-040321-030344.jpg\" onclick=\"return false;\"><img alt=\"Cụm camera sau| iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040321-030344.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040321-030344.jpg\" title=\"Cụm camera sau| iPhone 12\" /></a></p>\r\n\r\n<p>Bạn c&oacute; thể kh&aacute;m ph&aacute; th&ecirc;m những t&iacute;nh năng của camera tr&ecirc;n iPhone 12 như chế độ smart HDR 3 gi&uacute;p c&acirc;n bằng yếu tố &aacute;nh s&aacute;ng trong ảnh, l&agrave;m nổi bật chi tiết đối tượng v&agrave; c&acirc;y cối trong khi vẫn giữ được m&agrave;u sắc phong ph&uacute; của bầu trời ngay cả v&agrave;o buổi trưa nắng gắt.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/213031/iphone-12-9.jpg\" onclick=\"return false;\"><img alt=\"Chụp ảnh với tính năng smart HDR 3 sắc nét, chất lượng | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-9.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-9.jpg\" title=\"Chụp ảnh với tính năng smart HDR 3 sắc nét, chất lượng | iPhone 12\" /></a></p>\r\n\r\n<p>Chế độ chụp ch&acirc;n dung đ&atilde; tốt nay c&ograve;n tốt hơn trong việc l&agrave;m mờ hậu cảnh một c&aacute;ch nghệ thuật để dồn hết sự tập trung v&agrave;o đối tượng m&agrave; bạn muốn chụp.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/213031/iphone-12-10.jpg\" onclick=\"return false;\"><img alt=\"Nổi bật chủ thể với tính năng chụp chân dung độc đáo | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-10.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-10.jpg\" title=\"Nổi bật chủ thể với tính năng chụp chân dung độc đáo | iPhone 12\" /></a></p>\r\n\r\n<p>Bạn sẽ dễ d&agrave;ng quay video 4K HDR với chuẩn điện ảnh&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/dolby-vision-la-gi-cac-ung-dung-noi-bat-va-nhung-loai-1226284\" target=\"_blank\" title=\"Tìm hiểu Dolby Vision là gì?\">Dolby Vision</a>&nbsp;v&agrave; chỉnh sửa ngay tr&ecirc;n chiếc điện thoại của m&igrave;nh, bạn c&oacute; thể đưa video l&ecirc;n TV của bạn để thưởng thức thước phim sắc n&eacute;t chất lượng cao.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/213031/iphone-12-11.jpg\" onclick=\"return false;\"><img alt=\"Quay video chất lượng ngay cả trong điều kiện ánh sáng yếu | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-11.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-11.jpg\" title=\"Quay video chất lượng ngay cả trong điều kiện ánh sáng yếu | iPhone 12\" /></a></p>\r\n\r\n<p>Sự kết hợp của 2 cảm biến chất lượng n&agrave;y đ&atilde; tạo n&ecirc;n một hệ thống camera chuy&ecirc;n nghiệp kh&ocirc;ng kh&aacute;c g&igrave; những chiếc m&aacute;y ảnh thực thụ, dễ d&agrave;ng đem lại những bức h&igrave;nh sắc n&eacute;t tuyệt đối, độ chi tiết cao v&agrave; đa dạng chế độ chụp cho người d&ugrave;ng linh hoạt sử dụng.</p>\r\n\r\n<h3>Ghi dấu ấn về mặt thiết kế</h3>\r\n\r\n<p>Về ngoại h&igrave;nh iPhone 12 c&oacute; thiết kế ho&agrave;i niệm với phần cạnh được l&agrave;m vu&ocirc;ng vức tương tự tr&ecirc;n mẫu iPhone 4 thay v&igrave; bo cong như iPhone 11.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/213031/iphone-12-040321-030352.jpg\" onclick=\"return false;\"><img alt=\" iPhone 12 có thiết kế hoài niệm tương tự iPhone 4 series\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040321-030352.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040321-030352.jpg\" title=\" iPhone 12 có thiết kế hoài niệm tương tự iPhone 4 series\" /></a></p>\r\n\r\n<p>Ở mặt trước iPhone 12 phần tai thỏ được l&agrave;m nhỏ gọn hơn v&agrave; cũng l&agrave; nơi chứa cảm biến Face ID c&oacute; thể nhận diện khu&ocirc;n mặt một c&aacute;ch nhanh ch&oacute;ng v&agrave; ch&iacute;nh x&aacute;c.</p>\r\n\r\n<p>Apple c&ograve;n mang đến cho người một loạt gam m&agrave;u c&aacute; t&iacute;nh, độc đ&aacute;o tr&ecirc;n những chiếc iPhone của m&igrave;nh để người d&ugrave;ng c&oacute; sự lựa chọn ph&ugrave; hợp với những phong c&aacute;ch kh&aacute;c nhau.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/213031/iphone-12-17.jpg\" onclick=\"return false;\"><img alt=\"Sở hữu loạt gam màu cá tính | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-17.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-17.jpg\" title=\"Sở hữu loạt gam màu cá tính | iPhone 12\" /></a></p>\r\n\r\n<p>iPhone 12 được trang bị m&agrave;n h&igrave;nh Super Retina XDR OLED tr&agrave;n viền c&oacute; k&iacute;ch thước 6.1 inch, cho bạn kh&ocirc;ng gian trải nghiệm lớn cũng như những gi&acirc;y ph&uacute;t giải tr&iacute; hấp dẫn tr&ecirc;n một m&agrave;n ảnh v&ocirc; c&ugrave;ng chất lượng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/213031/iphone-12-040421-030410.jpg\" onclick=\"return false;\"><img alt=\"Màn hình Super Retina XDR OLED 6.1 inch | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040421-030410.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040421-030410.jpg\" title=\"Màn hình Super Retina XDR OLED 6.1 inch | iPhone 12\" /></a></p>\r\n\r\n<p>M&aacute;y được chế t&aacute;c c&oacute; độ ho&agrave;n thiện cực cao với thiết kế nguy&ecirc;n khối, khung nh&ocirc;m v&agrave; mặt sau l&agrave; k&iacute;nh cường lực cao cấp to&aacute;t l&ecirc;n vẻ ngo&agrave;i sang chảnh cũng như mang lại độ hiệu quả an to&agrave;n cao mỗi khi sử dụng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/213031/iphone-12-041521-031513.jpg\" onclick=\"return false;\"><img alt=\"Độ hoàn thiện cao, chắc chắn cho thiết bị | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-041521-031513.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-041521-031513.jpg\" title=\"Độ hoàn thiện cao, chắc chắn cho thiết bị | iPhone 12\" /></a></p>\r\n\r\n<p>Mặt trước của iPhone 12 được phủ ho&agrave;n to&agrave;n bởi lớp k&iacute;nh cường lực Ceramic Shield cứng c&aacute;p, được đ&aacute;nh gi&aacute; l&agrave; c&oacute; độ bền cao v&agrave; cứng c&aacute;p hơn hầu hết c&aacute;c loại mặt k&iacute;nh c&oacute; tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại chính hãng tại Thế Giới Di Động\">điện thoại th&ocirc;ng minh</a>&nbsp;kh&aacute;c c&oacute; mặt tr&ecirc;n thị trường.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/213031/iphone-12-040321-030328.jpg\" onclick=\"return false;\"><img alt=\"Lớp kính cường lực Ceramic Shield | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040321-030328.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040321-030328.jpg\" title=\"Lớp kính cường lực Ceramic Shield | iPhone 12\" /></a></p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/mat-kinh-ceramic-shield-tren-iphone-12-la-gi-co-xin-so-nhu-1298900\" target=\"_blank\" title=\"Mặt kính Ceramic Shield trên iPhone 12 là gì? Có xịn sò như quảng cáo?\">Mặt k&iacute;nh Ceramic Shield tr&ecirc;n iPhone 12 l&agrave; g&igrave;? C&oacute; xịn s&ograve; như quảng c&aacute;o?</a></p>\r\n\r\n<p>V&agrave; để cho thiết bị trở n&ecirc;n ho&agrave;n hảo hơn n&ecirc;n kh&ocirc;ng thể thiếu khả năng kh&aacute;ng nước, bụi chuẩn IP68 gi&uacute;p người d&ugrave;ng y&ecirc;n t&acirc;m sử dụng với những buổi đi chơi biển m&agrave; kh&ocirc;ng hề lo chiếc m&aacute;y bị v&ocirc; nước.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/213031/iphone-12-2.jpg\" onclick=\"return false;\"><img alt=\"Kháng nước bụi chuẩn IP68 | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-2.jpg\" title=\"Kháng nước bụi chuẩn IP68 | iPhone 12\" /></a></p>\r\n\r\n<h3>Trải nghiệm xuy&ecirc;n suốt, liền mạch cả ng&agrave;y d&agrave;i</h3>\r\n\r\n<p>Để bạn c&oacute; những gi&acirc;y ph&uacute;t trải nghiệm liền mạch, Apple đ&atilde; trang bị một vi&ecirc;n pin c&oacute; dung lượng 2815 mAh, t&iacute;ch hợp cho khả năng tiết kiệm pin gi&uacute;p người d&ugrave;ng c&oacute; thể giải tr&iacute; đa phương tiện l&ecirc;n đến 17 giờ v&agrave; nghe nhạc li&ecirc;n tục l&ecirc;n đến 65 giờ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/213031/iphone-12-040321-030358.jpg\" onclick=\"return false;\"><img alt=\"Dung lượng pin sử dụng đáp ứng đủ một ngày | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040321-030358.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040321-030358.jpg\" title=\"Dung lượng pin sử dụng đáp ứng đủ một ngày | iPhone 12\" /></a></p>\r\n\r\n<p>V&agrave; kh&ocirc;ng thể thiếu đ&oacute; ch&iacute;nh l&agrave; t&iacute;nh năng sạc nhanh, iPhone 12 c&oacute; khả năng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo điện thoại có sạc pin nhanh tại Thế Giới Di Động\">sạc pin nhanh</a>&nbsp;qua c&aacute;p c&ocirc;ng suất 20 W, chỉ trong v&ograve;ng 30 ph&uacute;t th&igrave; chiếc m&aacute;y đ&atilde; c&oacute; thế sạc được 50% pin. Th&ecirc;m v&agrave;o đ&oacute; l&agrave; khả năng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-khong-day\" target=\"_blank\" title=\"Tham khảo điện thoại có sạc không dây tại Thế Giới Di Động\">sạc kh&ocirc;ng d&acirc;y</a>&nbsp;MagSafe v&ocirc; c&ugrave;ng tiện dụng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/213031/iphone-12-16.jpg\" onclick=\"return false;\"><img alt=\"Sạc nhanh và sạc không dây tiện lợi hữu ích | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-16.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-16.jpg\" title=\"Sạc nhanh và sạc không dây tiện lợi hữu ích | iPhone 12\" /></a></p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-magsafe-magsafe-2-la-gi-co-tren-thiet-bi-nao-cua-1241888\" target=\"_blank\" title=\"MagSafe trên iPhone 12 là gì? Dùng để làm gì trên các thiết bị Apple?\">MagSafe tr&ecirc;n iPhone 12 l&agrave; g&igrave;? D&ugrave;ng để l&agrave;m g&igrave; tr&ecirc;n c&aacute;c thiết bị Apple?</a></p>\r\n\r\n<p>Sự lột x&aacute;c đầy mạnh mẽ lần n&agrave;y của Apple kh&ocirc;ng chỉ g&acirc;y bất ngờ đến người d&ugrave;ng m&agrave; c&ograve;n đ&aacute;nh dấu một kỷ nguy&ecirc;n mới trong nền ph&aacute;t triển smartphone Apple. V&agrave; đ&acirc;y cũng được xem l&agrave; một trong những bộ series iPhone m&agrave; Apple đặt nhiều t&acirc;m huyết, mục đ&iacute;ch v&agrave; đầy t&iacute;nh năng mạnh mẽ chưa từng thấy.</p>\r\n', 10, 'dien-thoai-iphone-12-64gb-9.html', 'Quốc Toàn', '', 'https://cdn.tgdd.vn/Products/Images/42/213031/TimerThumb/iphone-12-(8).jpg', '2', NULL, NULL, 1, 1),
(10, '#IMOP10', 'OPPO A74', 6690000, NULL, '<p>M&agrave;n h&igrave;nh 6.43&quot;, Chip Snapdragon 662</p>\r\n\r\n<p>RAM 8 GB, ROM 128 GB</p>\r\n\r\n<p>Camera sau: Ch&iacute;nh 48 MP &amp; Phụ 2 MP, 2 MP</p>\r\n\r\n<p>Camera trước: 16 MP</p>\r\n\r\n<p>Pin 5000 mAh, Sạc 33 W</p>\r\n', '<h3><a href=\"https://www.thegioididong.com/dtdd-oppo\" target=\"_blank\" title=\"Tham khảo các sản phẩm điện thoại OPPO tại Thế Giới Di Động\">OPPO</a>&nbsp;lu&ocirc;n đa dạng ho&aacute; c&aacute;c sản phẩm của m&igrave;nh từ gi&aacute; rẻ cho đến cao cấp. Trong đ&oacute;, điện thoại&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oppo-a74\" target=\"_blank\" title=\"Tham khảo sản phẩm OPPO A74 4G tại Thế Giới Di Động\">OPPO A74 4G</a>&nbsp;được nằm trong ph&acirc;n kh&uacute;c tầm trung rất đ&aacute;ng ch&uacute; &yacute; với nhiều t&iacute;nh năng v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các sản phẩm điện thoại tại Thế Giới Di Động\">smartphone</a>&nbsp;cũng ch&iacute;nh&nbsp;l&agrave; bản n&acirc;ng cấp của&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oppo-a73\" target=\"_blank\" title=\"Tham khảo sản phẩm OPPO A73 tại Thế Giới Di Động\">OPPO A73</a>&nbsp;ra mắt trước đ&oacute;.</h3>\r\n\r\n<h3>Thiết kế quen thuộc, m&agrave;u sắc tối giản</h3>\r\n\r\n<p>Chiếc điện thoại OPPO A74 vẫn sở hữu cụm camera nằm trong m&ocirc;-đun h&igrave;nh chữ nhật g&oacute;c tr&aacute;i th&acirc;n thuộc của h&atilde;ng, m&aacute;y được gia c&ocirc;ng tỉ mỉ, tối ưu k&iacute;ch thước gi&uacute;p smartphone mỏng nhẹ, thiết kế th&acirc;n m&aacute;y cong 3D mảnh mai, dễ cầm nắm sử dụng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235653/oppo-a74-4g-002.jpg\" onclick=\"return false;\"><img alt=\"OPPO A74 | Thiết kế cụm camera nằm trong mô-đun hình chữ nhật quen thuộc\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-4g-002.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-4g-002.jpg\" title=\"OPPO A74 | Thiết kế cụm camera nằm trong mô-đun hình chữ nhật quen thuộc\" /></a></p>\r\n\r\n<p>OPPO A74 tối giản sự lựa chọn của người d&ugrave;ng chỉ với 2 phi&ecirc;n bản m&agrave;u sắc, đ&oacute; l&agrave;: M&agrave;u xanh thanh m&aacute;t như một tảng băng tr&ocirc;i to&aacute;t vẻ thanh lịch, tươi mới v&agrave; lớp vỏ m&agrave;u đen huyền b&iacute;, sang trọng lại cực kỳ dễ phối m&agrave;u với trang phục.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235653/oppo-a74-14.jpg\" onclick=\"return false;\"><img alt=\"OPPO A74 | Với 2 phiên bản màu sắc, đó là: Màu xanh thanh mát và màu đen huyền bí\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-14.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-14.jpg\" title=\"OPPO A74 | Với 2 phiên bản màu sắc, đó là: Màu xanh thanh mát và màu đen huyền bí\" /></a></p>\r\n\r\n<h3>Tỏa s&aacute;ng sắc n&eacute;t với m&agrave;n h&igrave;nh&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-amoled-la-gi-905766\" target=\"_blank\" title=\"Màn hình AMOLED Plus là gì?\">AMOLED</a>&nbsp;</h3>\r\n\r\n<p>OPPO A74 sở hữu m&agrave;n h&igrave;nh c&oacute; k&iacute;ch thước hiển thị kh&aacute; rộng 6.43 inch với tỷ lệ m&agrave;n h&igrave;nh so với th&acirc;n m&aacute;y l&ecirc;n đến 90.8% cho kh&ocirc;ng gian hiển thị rộng mở, cho trải nghiệm người d&ugrave;ng th&ecirc;m sự r&otilde; r&agrave;ng, đ&atilde; mắt.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235653/oppo-a74-12.jpg\" onclick=\"return false;\"><img alt=\"OPPO A74 | Sở hữu màn hình có kích thước hiển thị khá rộng 6.43 inch\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-12.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-12.jpg\" title=\"OPPO A74 | Sở hữu màn hình có kích thước hiển thị khá rộng 6.43 inch\" /></a></p>\r\n\r\n<p>Trang bị m&agrave;n h&igrave;nh đục lỗ AMOLED với m&agrave;u sắc tươi tắn, h&agrave;i h&ograve;a, gam m&agrave;u đa dạng, mở rộng g&oacute;c nh&igrave;n kết hợp với tiết kiệm điện năng mang lại sự đắm ch&igrave;m cho người d&ugrave;ng trong từng h&igrave;nh ảnh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235653/oppo-a74-4g-008.jpg\" onclick=\"return false;\"><img alt=\"OPPO A74 | Trang bị màn hình đục lỗ AMOLED với màu sắc tươi tắn, hài hòa\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-4g-008.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-4g-008.jpg\" title=\"OPPO A74 | Trang bị màn hình đục lỗ AMOLED với màu sắc tươi tắn, hài hòa\" /></a></p>\r\n\r\n<p>Độ s&aacute;ng tối đa 800 nits rực rỡ, th&ecirc;m phần tinh tế bằng khả năng li&ecirc;n tục điều chỉnh mức độ s&aacute;ng ph&ugrave; hợp cho đ&ocirc;i mắt của bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235653/oppo-a74-4g-007.jpg\" onclick=\"return false;\"><img alt=\"OPPO A74 | Tính năng bảo mật vân tay quang học cho tốc độ quét nhanh và chính xác\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-4g-007.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-4g-007.jpg\" title=\"OPPO A74 | Tính năng bảo mật vân tay quang học cho tốc độ quét nhanh và chính xác\" /></a></p>\r\n\r\n<p>Bảo vệ những th&ocirc;ng tin của bạn với t&iacute;nh năng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-bao-mat-van-tay\" target=\"_blank\" title=\"Tham khảo các sản phẩm điện thoại bảo mật vân tay tại Thế Giới Di Động\">bảo mật v&acirc;n tay</a>&nbsp;quang học cho tốc độ qu&eacute;t nhanh v&agrave; ch&iacute;nh x&aacute;c, bạn c&oacute; thể y&ecirc;n t&acirc;m c&agrave;i đặt dấu v&acirc;n tay v&agrave; sử dụng c&ocirc;ng nghệ n&agrave;y một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<h3>Sẵn s&agrave;ng chụp ảnh đẹp mọi l&uacute;c mọi nơi</h3>\r\n\r\n<p>OPPO đ&atilde; trang bị cho chiếc smartphone A74 của m&igrave;nh một cụm 3 camera sau gồm: Camera ch&iacute;nh c&oacute; độ ph&acirc;n giải 48 MP,&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/che-do-chup-macro-la-gi-907851\" target=\"_blank\" title=\"Ống kính Macro, chụp ảnh Macro là gì? Các lưu ý khi chụp ảnh Macro\">camera macro</a>&nbsp;2 MP v&agrave; cuối c&ugrave;ng l&agrave; camera đo độ s&acirc;u 2 MP.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235653/oppo-a74-4g-003.jpg\" onclick=\"return false;\"><img alt=\"OPPO A74 | Cụm 3 camera sau độ phân giải đến 48 MP\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-4g-003.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-4g-003.jpg\" title=\"OPPO A74 | Cụm 3 camera sau độ phân giải đến 48 MP\" /></a></p>\r\n\r\n<p>Với camera ch&iacute;nh m&aacute;y sẽ cho bạn những khung ảnh chụp được c&oacute; độ chi tiết cao, sắc n&eacute;t. Camera macro bộc lộ vẻ đẹp mới mẻ trong những chi tiết nhỏ c&ugrave;ng với ống k&iacute;nh đo độ s&acirc;u chụp những bức ch&acirc;n dung nổi bật hơn với hiệu ứng bokeh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235653/oppo-a74-5.jpg\" onclick=\"return false;\"><img alt=\"OPPO A74 | Camera chính máy sẽ cho bạn những khung ảnh chụp được có độ chi tiết cao, sắc nét\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-5.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-5.jpg\" title=\"OPPO A74 | Camera chính máy sẽ cho bạn những khung ảnh chụp được có độ chi tiết cao, sắc nét\" /></a></p>\r\n\r\n<p>Điện thoại cũng hỗ trợ c&aacute;c chuẩn quay video sắc n&eacute;t gi&uacute;p người d&ugrave;ng ghi lại những khoảnh khắc đ&aacute;ng nhớ trong cuộc sống hằng ng&agrave;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235653/oppo-a74-4g-006.jpg\" onclick=\"return false;\"><img alt=\"OPPO A74 | Giao diện chụp ảnh\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-4g-006.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-4g-006.jpg\" title=\"OPPO A74 | Giao diện chụp ảnh\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, m&aacute;y ảnh selfie hỗ trợ độ ph&acirc;n giải 16 MP với c&aacute;c hiệu ứng AI l&agrave;m đẹp được tuỳ chỉnh dễ d&agrave;ng mang lại vẻ đẹp thực sự cho bạn,&nbsp;mang đến cho bạn sự tự tin hơn trước ống k&iacute;nh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235653/oppo-a74-17.jpg\" onclick=\"return false;\"><img alt=\"OPPO A74 | Máy ảnh selfie hỗ trợ độ phân giải 16 MP\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-17.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-17.jpg\" title=\"OPPO A74 | Máy ảnh selfie hỗ trợ độ phân giải 16 MP\" /></a></p>\r\n\r\n<h3>Hiệu năng ổn định, mượt m&agrave;&nbsp;</h3>\r\n\r\n<p>OPPO A74 trang bị chipset&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/chip-xu-ly-qualcomm-snapdragon-662-co-gi-noi-bat-1311449\" target=\"_blank\" title=\"Chip xử lý Qualcomm Snapdragon 662 có gì nổi bật?\">Snapdragon 662</a>&nbsp;được sản xuất dựa tr&ecirc;n tiến tr&igrave;nh 11 nm, c&oacute; tốc độ xử l&yacute; 2.0 GHz với c&aacute;c t&aacute;c vụ th&ocirc;ng thường m&aacute;y ho&agrave;n to&agrave;n đ&aacute;p ứng tốt mang đến những trải nghiệm cao cấp cho bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235653/oppo-a74-15.jpg\" onclick=\"return false;\"><img alt=\"OPPO A74 | Trang bị chipset Snapdragon 662\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-15.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-15.jpg\" title=\"OPPO A74 | Trang bị chipset Snapdragon 662\" /></a></p>\r\n\r\n<p>Khi được kiểm tra qua phần mềm đo hiệu năng Antutu, m&aacute;y cho ra kết quả hơn 180.000 điểm, v&igrave; thế m&agrave; c&aacute;c t&aacute;c vụ thường ng&agrave;y như Facebook, Youtube,... đều diễn ra trơn tru, chiến c&aacute;c tựa game đồ hoạ tương đối ổn định với mức tinh chỉnh ph&ugrave; hợp.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235653/oppo-a74-antutu.jpg\" onclick=\"return false;\"><img alt=\"OPPO A74 | Điểm số Antutu\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-antutu.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-antutu.jpg\" title=\"OPPO A74 | Điểm số Antutu\" /></a></p>\r\n\r\n<p>Với chế độ tập trung tr&ograve; chơi, m&aacute;y sẽ cải thiện hiệu năng tốt hơn cũng như ngăn chặn c&aacute;c th&ocirc;ng b&aacute;o, cuộc gọi ảnh hưởng đến qu&aacute; tr&igrave;nh chơi của bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235653/oppo-a74-8.jpg\" onclick=\"return false;\"><img alt=\"OPPO A74 | Đa nhiệm mượt mà với RAM 8 GB, 128 GB bộ nhớ trong\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-8.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-8.jpg\" title=\"OPPO A74 | Đa nhiệm mượt mà với RAM 8 GB, 128 GB bộ nhớ trong\" /></a></p>\r\n\r\n<p>OPPO A74 hoạt động v&agrave; xử l&yacute; dữ liệu nhanh ch&oacute;ng, đa nhiệm mượt m&agrave; với RAM 8 GB&nbsp;v&agrave;&nbsp;bạn c&oacute; thể tận hưởng một bộ nhớ dồi d&agrave;o, rộng r&atilde;i với&nbsp;<a href=\"https://www.thegioididong.com/dtdd-rom-128gb\" target=\"_blank\" title=\"Tham khảo điện thoại có bộ nhớ 128 GB tại Thegioididong.com\">128 GB bộ nhớ trong</a>. Nếu nhu cầu sử dụng cao hơn, OPPO A74 vẫn hỗ trợ thẻ nhớ ngo&agrave;i MicroSD cho bạn y&ecirc;n t&acirc;m hơn khi sử dụng.</p>\r\n\r\n<h3>Vi&ecirc;n pin lớn k&egrave;m theo khả năng sạc nhanh</h3>\r\n\r\n<p>OPPO A74 hoạt động cả ng&agrave;y d&agrave;i tr&agrave;n đầy năng lượng khi được cung vi&ecirc;n pin c&oacute; dung lượng 5000 mAh. Bạn c&oacute; thể học tập, l&agrave;m việc v&agrave; chơi c&aacute;c tr&ograve; chơi giải tr&iacute; đơn giản trong một khoảng thời gian kh&aacute; l&acirc;u tr&ecirc;n thiết bị.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235653/oppo-a74-4g-004.jpg\" onclick=\"return false;\"><img alt=\"OPPO A74 | Viên pin có dung lượng 5000 mAh\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-4g-004.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-4g-004.jpg\" title=\"OPPO A74 | Viên pin có dung lượng 5000 mAh\" /></a></p>\r\n\r\n<p>Sạc pin dễ d&agrave;ng hơn hết với tốc độ si&ecirc;u tốc của củ sạc nhanh 33 W gi&uacute;p r&uacute;t ngắn thời gian sạc đ&aacute;ng kể, với 30 ph&uacute;t sẽ c&oacute; ngay khoảng 54% pin cho bạn c&oacute; được sự trải nghiệm li&ecirc;n tục.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235653/oppo-a74-2.jpg\" onclick=\"return false;\"><img alt=\"OPPO A74 | Hỗ trợ sạc nhanh 33 W\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-2.jpg\" title=\"OPPO A74 | Hỗ trợ sạc nhanh 33 W\" /></a></p>\r\n\r\n<p>Sở hữu một hiệu năng tốt, OPPO A74 lại c&oacute; mức gi&aacute; kh&aacute; hấp dẫn cho những bạn y&ecirc;u th&iacute;ch một chiếc smartphone ổn định, pin tr&acirc;u v&agrave; m&agrave;n h&igrave;nh đẹp rực rỡ,... B&ecirc;n cạnh đ&oacute;, với hiệu năng được cải tiến đ&aacute;ng kể, ở ph&acirc;n kh&uacute;c tầm trung OPPO A74 sẽ l&agrave; một lựa chọn rất đ&aacute;ng để bạn xem x&eacute;t.</p>\r\n', 2, 'dien-thoai-oppo-a74-10.html', 'Toàn Thắng', '', 'https://cdn.tgdd.vn/Products/Images/42/235653/oppo-a74-blue-9-600x600.jpg', '2', NULL, NULL, 1, 3),
(11, '#IMXM11', 'Xiaomi Redmi Note 10 5G', 5990000, NULL, '<p>M&agrave;n h&igrave;nh 6.5&quot;, Chip MediaTek Dimensity 700</p>\r\n\r\n<p>RAM 8 GB, ROM 128 GB</p>\r\n\r\n<p>Camera sau: Ch&iacute;nh 48 MP &amp; Phụ 2 MP, 2 MP</p>\r\n\r\n<p>Camera trước: 8 MP</p>\r\n\r\n<p>Pin 5000 mAh, Sạc 18 W</p>\r\n', '<h3><a href=\"https://www.thegioididong.com/dtdd/xiaomi-redmi-note-10-5g\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo giá điện thoại Redmi Note 10 5G chính hãng tại Thegioididong.com\" type=\"Tham khảo giá điện thoại Redmi Note 10 5G chính hãng tại Thegioididong.com\">Redmi Note 10 5G</a>&nbsp;một trong những mẫu&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo giá điện thoại, smartphone chính hãng đang kinh doanh tại Thegioididong.com\" type=\"Tham khảo giá điện thoại, smartphone chính hãng đang kinh doanh tại Thegioididong.com\">điện thoại</a>&nbsp;thuộc series&nbsp;<a href=\"https://www.thegioididong.com/dtdd/xiaomi-redmi-note-10\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo giá điện thoại Xiaomi Redmi Note 10 (4GB/64GB) chính hãng, siêu khuyến mãi tại Thegioididong.com\" type=\"Tham khảo giá điện thoại Xiaomi Redmi Note 10 (4GB/64GB) chính hãng, siêu khuyến mãi tại Thegioididong.com\">Redmi Note 10</a>&nbsp;của&nbsp;<a href=\"https://www.thegioididong.com/dtdd-xiaomi\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo toàn bộ điện thoại, smartphone Xiaomi chính hãng mới nhất đang kinh doanh tại Thegioididong.com\" type=\"Tham khảo toàn bộ điện thoại, smartphone Xiaomi chính hãng mới nhất đang kinh doanh tại Thegioididong.com\">Xiaomi</a>, kh&ocirc;ng chỉ sở hữu hiệu năng mạnh mẽ đ&aacute;p ứng tốt nhu cầu&nbsp;<a href=\"https://www.thegioididong.com/dtdd-choi-game\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo các mẫu điện thoại có cấu hình cao, chơi game cực đã tại Thegioididong.com\" type=\"Tham khảo các mẫu điện thoại có cấu hình cao, chơi game cực đã tại Thegioididong.com\">chơi game</a>, đ&acirc;y c&ograve;n l&agrave; chiếc điện thoại c&oacute; hỗ trợ mạng 5G cho tốc độ kết nối nhanh ch&oacute;ng.</h3>\r\n\r\n<h3>Cấu h&igrave;nh mạnh tối ưu cho nhu cầu chơi game</h3>\r\n\r\n<p>Redmi Note 10 5G trang bị vi xử l&yacute; 8 nh&acirc;n Dimensity 700 của MediaTek sản xuất tr&ecirc;n tiến tr&igrave;nh 7 nm ti&ecirc;n tiến, hứa hẹn hiệu năng mạnh mẽ cho mọi t&aacute;c vụ h&agrave;ng ng&agrave;y nhanh ch&oacute;ng hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235971/xiaomi-redmi-note-10-5g-8.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi Note 10 5G | Trang bị vi xử lý 8 nhân Dimensity 700 của MediaTek\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-8.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-8.jpg\" title=\"Xiaomi Redmi Note 10 5G | Trang bị vi xử lý 8 nhân Dimensity 700 của MediaTek\" /></a></p>\r\n\r\n<p>Được biết đ&acirc;y l&agrave; d&ograve;ng chip được tối ưu cho nhu cầu chơi game. V&igrave; thế, smartphone tầm trung của Xiaomi ho&agrave;n to&agrave;n đảm đương ổn định c&aacute;c tựa game hiện nay một c&aacute;ch mượt m&agrave;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235971/xiaomi-redmi-note-10-5g-7.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi Note 10 5G | Chiến các tựa game hiện nay một cách mượt mà\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-7.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-7.jpg\" title=\"Xiaomi Redmi Note 10 5G | Chiến các tựa game hiện nay một cách mượt mà\" /></a></p>\r\n\r\n<p>Với&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-8gb\" title=\"Tham khảo điện thoại có RAM 8 GB tại Thegioididong.com\">8&nbsp;GB RAM</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd-rom-128gb\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo điện thoại có bộ nhớ 128 GB tại Thegioididong.com\" type=\"Tham khảo điện thoại có bộ nhớ 128 GB tại Thegioididong.com\">128 GB</a>&nbsp;bộ nhớ trong, Redmi Note 10 5G đ&aacute;p ứng ở mức ổn khả năng đa nhiệm v&agrave; cung cấp kh&ocirc;ng gian lưu trữ lớn gi&uacute;p bạn thỏa sức lưu trữ dữ liệu.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235971/xiaomi-redmi-note-10-5g-0006.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi Note 10 5G | 8 GB RAM và 128 GB bộ nhớ trong\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-0006.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-0006.jpg\" title=\"Xiaomi Redmi Note 10 5G | 8 GB RAM và 128 GB bộ nhớ trong\" /></a></p>\r\n\r\n<p>T&iacute;ch hợp modem 5G, với tốc độ kết nối vượt xa 4G, truy cập mạng nhanh ch&oacute;ng hơn ở bất kỳ đ&acirc;u, hỗ trợ 2 chế độ SA/NSA v&agrave; bố tr&iacute; dải anten th&ocirc;ng minh gi&uacute;p cho smartphone 5G của Xiaomi c&oacute; thể thu s&oacute;ng tốt trong điều kiện mạng k&eacute;m, chập chờn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235971/xiaomi-redmi-note-10-5g-9.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi Note 10 5G | Tích hợp modem 5G truy cập mạng nhanh chóng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-9.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-9.jpg\" title=\"Xiaomi Redmi Note 10 5G | Tích hợp modem 5G truy cập mạng nhanh chóng\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh si&ecirc;u lớn, tần số 90 Hz mượt m&agrave;</h3>\r\n\r\n<p>Redmi Note 10 5G sở hữu m&agrave;n h&igrave;nh&nbsp;<a href=\"https://www.thegioididong.com/dtdd-man-hinh-tran-vien\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo các mẫu điện thoại có màn hình tràn viền tại Thegioididong.com\" type=\"Tham khảo các mẫu điện thoại có màn hình tràn viền tại Thegioididong.com\">tr&agrave;n viền</a>&nbsp;với phần &ldquo;nốt ruồi&rdquo; chứa camera selfie, k&iacute;ch thước 6.5 inch v&ocirc; c&ugrave;ng rộng r&atilde;i, để bạn thoải m&aacute;i sử dụng hay chơi game.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235971/xiaomi-redmi-note-10-5g-11.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi Note 10 5G | Màn hình tràn viền với phần “nốt ruồi” chứa camera selfie, kích thước 6.5 inch\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-11.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-11.jpg\" title=\"Xiaomi Redmi Note 10 5G | Màn hình tràn viền với phần “nốt ruồi” chứa camera selfie, kích thước 6.5 inch\" /></a></p>\r\n\r\n<p>Độ ph&acirc;n giải Full HD+ đảm bảo cho c&aacute;c chi tiết hiển thị sắc n&eacute;t tr&ecirc;n m&agrave;n h&igrave;nh lớn, tấm nền IPS LCD giữ cho m&agrave;u sắc lu&ocirc;n tươi tắn từ nhiều g&oacute;c nh&igrave;n kh&aacute;c nhau.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235971/xiaomi-redmi-note-10-5g-0001.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi Note 10 5G | Độ phân giải Full HD+ đảm bảo cho các chi tiết hiển thị sắc nét\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-0001.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-0001.jpg\" title=\"Xiaomi Redmi Note 10 5G | Độ phân giải Full HD+ đảm bảo cho các chi tiết hiển thị sắc nét\" /></a></p>\r\n\r\n<p>Đặc biệt với tần số qu&eacute;t đạt 90 Hz, bạn c&oacute; thể nhận r&otilde; được sự mượt m&agrave; đ&aacute;ng kể đến hiệu ứng chuyển cảnh mỗi khi vuốt chạm, n&acirc;ng cấp trải nghiệm xem phim hay chơi game của bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235971/xiaomi-redmi-note-10-5g-0007.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi Note 10 5G | Tần số quét đạt 90 Hz mượt mà\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-0007.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-0007.jpg\" title=\"Xiaomi Redmi Note 10 5G | Tần số quét đạt 90 Hz mượt mà\" /></a></p>\r\n\r\n<h3>Cụm camera đa năng chụp ảnh dễ d&agrave;ng</h3>\r\n\r\n<p>Redmi Note 10 5G sở hữu hệ thống 3 camera chứa camera ch&iacute;nh 48 MP,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-macro\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo các mẫu điện thoại có camera macro chụp cận cảnh tại Thegioididong.com\" type=\"Tham khảo các mẫu điện thoại có camera macro chụp cận cảnh tại Thegioididong.com\">camera chụp macro</a>&nbsp;v&agrave; cảm biến chiều s&acirc;u c&oacute; c&ugrave;ng độ ph&acirc;n giải 2 MP, hỗ trợ nhiều kiểu chụp ấn tượng cho người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235971/xiaomi-redmi-note-10-5g-0003.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi Note 10 5G | Hệ thống 3 camera độ phân giải lên đến 48 MP\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-0003.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-0003.jpg\" title=\"Xiaomi Redmi Note 10 5G | Hệ thống 3 camera độ phân giải lên đến 48 MP\" /></a></p>\r\n\r\n<p>Camera ch&iacute;nh c&oacute; độ n&eacute;t cao đ&aacute;p ứng tốt nhu cầu chụp ảnh thường ng&agrave;y. B&ecirc;n cạnh đ&oacute;, m&aacute;y c&ograve;n hỗ trợ&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-xoa-phong\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo thêm các mẫu điện thoại có camera chụp chân dung xóa phông tại Thegioididong.com\" type=\"Tham khảo thêm các mẫu điện thoại có camera chụp chân dung xóa phông tại Thegioididong.com\">chụp x&oacute;a ph&ocirc;ng</a>&nbsp;với cảm biến chiều s&acirc;u, camera macro sẽ tạo n&ecirc;n những khung h&igrave;nh chụp cận cảnh để bạn kh&aacute;m ph&aacute; thế giới của những chi tiết si&ecirc;u nhỏ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235971/xiaomi-redmi-note-10-5g-0009.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi Note 10 5G | Giao diện chụp ảnh\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-0009.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-0009.jpg\" title=\"Xiaomi Redmi Note 10 5G | Giao diện chụp ảnh\" /></a></p>\r\n\r\n<p>Camera selfie 8 MP ở mặt trước sẽ gi&uacute;p bạn chụp lại ảnh tự chụp của ch&iacute;nh m&igrave;nh hay video call c&ugrave;ng bạn b&egrave;, chất lượng ảnh tương đối, đ&aacute;p ứng đủ cho nhu cầu h&agrave;ng ng&agrave;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235971/xiaomi-redmi-note-10-5g-15.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi Note 10 5G | Camera selfie 8 MP chất lượng tương đối\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-15.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-15.jpg\" title=\"Xiaomi Redmi Note 10 5G | Camera selfie 8 MP chất lượng tương đối\" /></a></p>\r\n\r\n<h3>Giải tr&iacute; cả ng&agrave;y với dung lượng pin cực khủng</h3>\r\n\r\n<p>Với mức&nbsp;<a href=\"https://www.thegioididong.com/dtdd-pin-khung\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo thêm các mẫu điện thoại có dung lượng pin khủng trên 5000 mAh tại Thegioididong.com\" type=\"Tham khảo thêm các mẫu điện thoại có dung lượng pin khủng trên 5000 mAh tại Thegioididong.com\">pin khủng</a>&nbsp;l&ecirc;n đến 5000 mAh, Redmi Note 10 5G ho&agrave;n to&agrave;n đảm bảo một ng&agrave;y sử dụng thoải m&aacute;i với nhiều t&aacute;c vụ kh&aacute;c nhau.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235971/xiaomi-redmi-note-10-5g-0005.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi Note 10 5G | Pin khủng lên đến 5000 mAh\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-0005.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-0005.jpg\" title=\"Xiaomi Redmi Note 10 5G | Pin khủng lên đến 5000 mAh\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, m&aacute;y c&ograve;n trang bị&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo giá điện thoại có sạc nhanh tại Thegioididong.com\" type=\"Tham khảo giá điện thoại có sạc nhanh tại Thegioididong.com\">sạc nhanh</a>&nbsp;c&ocirc;ng suất 18 W gi&uacute;p r&uacute;t ngắn thời gian sạc pin v&agrave; bạn sẽ c&oacute; nhiều thời gian hơn để giải tr&iacute; trải nghiệm.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/235971/xiaomi-redmi-note-10-5g-6.jpg\" onclick=\"return false;\"><img alt=\"Xiaomi Redmi Note 10 5G | Trang bị sạc nhanh công suất 18 W\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-6.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235971/xiaomi-redmi-note-10-5g-6.jpg\" title=\"Xiaomi Redmi Note 10 5G | Trang bị sạc nhanh công suất 18 W\" /></a></p>\r\n\r\n<h3>Thiết kế hiện đại, thời trang</h3>\r\n\r\n<p>Redmi Note 10 5G thừa hưởng thiết kế đặc trưng của d&ograve;ng Redmi Note 10 với th&acirc;n m&aacute;y nguy&ecirc;n khối mang đến diện mạo cao cấp, sang trọng hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Phần khung viền được bo cong, ho&agrave;n thiện cứng c&aacute;p liền mạch từ mặt lưng đến m&agrave;n h&igrave;nh ph&iacute;a trước, kiểu d&aacute;ng mỏng nhẹ, tạo n&ecirc;n tạo sự thoải m&aacute;i vừa vặn khi cầm tr&ecirc;n tay.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, cảm biến&nbsp;<a href=\"https://www.thegioididong.com/dtdd-bao-mat-van-tay\" rel=\"noopener\" target=\"_blank\" title=\"Tham khảo các mãu điện thoại có bảo mật mở khóa bằng cảm biến vân tay tại Thegioididong.com\" type=\"Tham khảo các mãu điện thoại có bảo mật mở khóa bằng cảm biến vân tay tại Thegioididong.com\">v&acirc;n tay</a>&nbsp;đ&atilde; được Xiaomi t&iacute;ch hợp ở cạnh b&ecirc;n vừa tăng t&iacute;nh thẩm mỹ cho thiết kế vừa gi&uacute;p cho việc mở kh&oacute;a nhanh ch&oacute;ng tiện lợi hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cụm camera l&agrave; đặc điểm nhận diện giữa Redmi Note 10 5G với c&aacute;c mẫu điện thoại c&ograve;n lại trong series Redmi Note 10, m&aacute;y sở hữu cụm camera chữ nhật, c&aacute;c ống k&iacute;nh được xếp dọc với điểm nhấn l&agrave; phần khung kim loại s&aacute;ng b&oacute;ng bao quanh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>T&oacute;m lại, Redmi Note 10 5G tiếp tục l&agrave; lựa chọn &ldquo;ngon, bổ, rẻ&rdquo; m&agrave; Xiaomi đem đến trong ph&acirc;n kh&uacute;c, mẫu smartphone n&agrave;y kh&aacute; ph&ugrave; hợp cho những ai th&iacute;ch chơi game hay cần một thời lượng pin si&ecirc;u &ldquo;tr&acirc;u&rdquo; đ&aacute;p ứng cường độ sử dụng cao.</p>\r\n', 4, 'dien-thoai-xiaomi-redmi-note-10-5g-8gb-11.html', 'Nguyễn Như', '', 'https://cdn.tgdd.vn/Products/Images/42/235969/xiaomi-redmi-note-10s-xanh-1-600x600.jpg', '2', NULL, NULL, 1, 4);
INSERT INTO `tbl_product` (`product_id`, `product_code`, `product_name`, `product_price`, `old_price`, `product_desc`, `content`, `qty`, `slug`, `creator`, `editor`, `product_thumb`, `product_status`, `add_date`, `update_date`, `cat_id`, `child_id`) VALUES
(12, '#IMMB12', 'Apple MacBook Pro M1', 41990000, 42990000, '<p>13.3&quot;</p>\r\n\r\n<p>CPU: Apple M1</p>\r\n\r\n<p>Card: 8 nh&acirc;n GPU</p>\r\n\r\n<p>Nặng: 1.4 kg, Khoảng 10 tiếng</p>\r\n', '<h3><a href=\"https://www.thegioididong.com/laptop/apple-macbook-pro-2020-z11c\" target=\"_blank\" title=\"Apple Macbook Pro 2020 (Z11C)\">Apple Macbook Pro M1 2020 (Z11C)</a>&nbsp;được n&acirc;ng cấp với bộ vi xử l&yacute; mới cực kỳ mạnh mẽ, con laptop n&agrave;y sẽ phục vụ tốt cho c&ocirc;ng việc của bạn, đặc biệt b&ecirc;n lĩnh vực đồ họa, kỹ thuật.</h3>\r\n\r\n<h3>Tăng cường kh&ocirc;ng gian lưu trữ, tốc độ xử l&yacute;</h3>\r\n\r\n<p>Chip&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-chip-apple-m1-con-chip-arm-5nm-dau-tien-danh-1305955\" target=\"_blank\" title=\"Tìm hiểu về chip Apple M1\">Apple M1</a>&nbsp;l&agrave; một bộ vi xử l&yacute; mạnh mẽ, được ra mắt lần đầu ti&ecirc;n tr&ecirc;n MAC. Đ&acirc;y l&agrave; con chip với bộ xử l&yacute; 5 nm, t&iacute;ch hợp&nbsp;<strong>CPU 8 l&otilde;i&nbsp;</strong>với 4 l&otilde;i CPU tốc độ v&agrave; v&agrave; 4 l&otilde;i tiết kiệm năng lượng. Nhờ vậy, thời lượng pin của laptop được k&eacute;o d&agrave;i đến tận 10 tiếng đồng hồ, gi&uacute;p cho bạn thoải m&aacute;i l&agrave;m việc với một hiệu suất cực kỳ cao.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/231255/apple-macbook-pro-2020-z11c-060520-120550.jpg\" onclick=\"return false;\"><img alt=\"Laptop Apple Macbook Pro 2020 M1/16GB/512GB (Z11C) - Apple M1\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/231255/apple-macbook-pro-2020-z11c-060520-120550.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/231255/apple-macbook-pro-2020-z11c-060520-120550.jpg\" title=\"Laptop Apple Macbook Pro 2020 M1/16GB/512GB (Z11C) - Apple M1\" /></a></p>\r\n\r\n<p>Ngo&agrave;i ra, card đồ họa t&iacute;ch hợp&nbsp;<strong>8 nh&acirc;n GPU&nbsp;</strong>gi&uacute;p người d&ugrave;ng xử l&yacute; c&aacute;c t&aacute;c vụ văn ph&ograve;ng cũng như đồ họa một c&aacute;ch trơn tru, mượt m&agrave; với tốc độ xử l&yacute; gấp 5 lần c&aacute;c thế hệ trước đ&oacute;. Nhờ vậy, bạn c&oacute; thể n&acirc;ng cao hiệu suất l&agrave;m việc cũng như tiết kiệm được thời gian một c&aacute;ch tối ưu nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/231255/apple-macbook-pro-2020-z11c-060520-120542.jpg\" onclick=\"return false;\"><img alt=\"Laptop Apple Macbook Pro 2020 M1/16GB/512GB (Z11C) - GPU\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/231255/apple-macbook-pro-2020-z11c-060520-120542.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/231255/apple-macbook-pro-2020-z11c-060520-120542.jpg\" title=\"Laptop Apple Macbook Pro 2020 M1/16GB/512GB (Z11C) - GPU\" /></a></p>\r\n\r\n<p>Với ổ cứng&nbsp;<strong><a href=\"https://www.thegioididong.com/laptop?g=ssd-512-gb\" target=\"_blank\" title=\"Xem thêm các laptop có SSD 512 GB\">SSD 512 GB</a></strong>&nbsp;gi&uacute;p bạn&nbsp;tăng tốc to&agrave;n diện m&aacute;y t&iacute;nh với tốc độ khởi động, mở ứng dụng, truyền dữ liệu nhanh vượt trội so với ổ cứng HDD truyền thống. B&ecirc;n cạnh đ&oacute;,&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=16-gb\" target=\"_blank\" title=\"Laptop được trang bị RAM 16 GB đang kinh doanh tại Thegioididong.com\">RAM 16 GB</a>&nbsp;của laptop gi&uacute;p n&acirc;ng cao khả năng đa nhiệm, dễ d&agrave;ng mở những tập tin lớn, nặng hay mở nhiều tr&igrave;nh duyệt c&ugrave;ng l&uacute;c nhưng kh&ocirc;ng xảy ra t&igrave;nh trạng giật lag.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/231255/apple-macbook-pro-2020-z11c-060620-120607.jpg\" onclick=\"return false;\"><img alt=\"Laptop Apple Macbook Pro 2020 M1/16GB/512GB (Z11C) - RAM\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/231255/apple-macbook-pro-2020-z11c-060620-120607.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/231255/apple-macbook-pro-2020-z11c-060620-120607.jpg\" title=\"Laptop Apple Macbook Pro 2020 M1/16GB/512GB (Z11C) - RAM\" /></a></p>\r\n\r\n<p>C&oacute; thể xem&nbsp;<a href=\"https://www.thegioididong.com/game-app/danh-sach-ung-dung-chay-duoc-tren-macbook-chip-apple-m1-1310311\" target=\"_blank\" title=\"Danh sách ứng dụng chạy được trên Macbook chip Apple M1\">danh s&aacute;ch ứng dụng chạy được tr&ecirc;n Macbook chip Apple M1</a>&nbsp;để c&agrave;i đặt tương th&iacute;ch nhất.</p>\r\n\r\n<h3>Kiểu d&aacute;ng thời trang, mỏng nhẹ</h3>\r\n\r\n<p>Chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm các mẫu laptop đang kinh doanh tại thegioididong.com\">laptop</a>&nbsp;phi&ecirc;n bản m&agrave;u bạc với vỏ kim loại nguy&ecirc;n khối cực kỳ sang trọng, nhỏ gọn v&agrave; thời trang. Bạn cũng c&oacute; thể dễ d&agrave;ng cho&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook-pro\" target=\"_blank\" title=\"Xem thêm các sản phẩm MacBook đang bán tại Thegioididong.com\">Macbook Pro</a>&nbsp;2020 v&agrave;o cặp x&aacute;ch hay balo, cầm tr&ecirc;n tay nhẹ nh&agrave;ng để di chuyển bất cứ nơi đ&acirc;u chỉ với trọng lượng&nbsp;<strong>1.4 kg</strong>&nbsp;v&agrave; mỏng&nbsp;<strong>15.6 mm</strong>.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/231255/apple-macbook-pro-2020-z11c-060620-120611.jpg\" onclick=\"return false;\"><img alt=\"Laptop Apple Macbook Pro 2020 M1/16GB/512GB (Z11C) - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/231255/apple-macbook-pro-2020-z11c-060620-120611.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/231255/apple-macbook-pro-2020-z11c-060620-120611.jpg\" title=\"Laptop Apple Macbook Pro 2020 M1/16GB/512GB (Z11C) - Thiết kế\" /></a></p>\r\n\r\n<p>B&agrave;n ph&iacute;m<strong>&nbsp;Magic Keyboard</strong>&nbsp;mới tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook-m1\" target=\"_blank\" title=\"Xem thêm Macbook M1 đang bán tại Thế Giới Di Động\">MacBook M1&nbsp;</a>c&oacute; cơ chế cắt k&eacute;o tinh tế, mang lại cho bạn trải nghiệm g&otilde; nhạy, thoải m&aacute;i v&agrave; y&ecirc;n tĩnh. Kh&ocirc;ng thể kh&ocirc;ng nhắc đến đ&egrave;n LED được t&iacute;ch hợp tr&ecirc;n b&agrave;n ph&iacute;m gi&uacute;p bạn l&agrave;m việc linh hoạt, năng suất hơn kể cả khi ở trong điều kiện thiếu s&aacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/231255/apple-macbook-pro-2020-z11c-060520-120558.jpg\" onclick=\"return false;\"><img alt=\"Laptop Apple Macbook Pro 2020 M1/16GB/512GB (Z11C) - Magic Keyboard\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/231255/apple-macbook-pro-2020-z11c-060520-120558.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/231255/apple-macbook-pro-2020-z11c-060520-120558.jpg\" title=\"Laptop Apple Macbook Pro 2020 M1/16GB/512GB (Z11C) - Magic Keyboard\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/231255/apple-macbook-pro-2020-z11c-060520-120538.jpg\" onclick=\"return false;\"><img alt=\"Laptop Apple Macbook Pro 2020 M1/16GB/512GB (Z11C) - Thanh cảm ứng\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/231255/apple-macbook-pro-2020-z11c-060520-120538.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/231255/apple-macbook-pro-2020-z11c-060520-120538.jpg\" title=\"Laptop Apple Macbook Pro 2020 M1/16GB/512GB (Z11C) - Thanh cảm ứng\" /></a></p>\r\n\r\n<p>Được trang bị cổng kết nối USB Type-C hỗ trợ kết nối 2 chiều cực kỳ tiện lợi với 2 cổng&nbsp;Thunderbolt 3, đem đến&nbsp;tốc độ nhanh hơn nhiều so với c&aacute;c cổng kết nối trước đ&acirc;y. Ngo&agrave;i ra,&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\" title=\"Xem thêm các sản phẩm MacBook đang bán tại Thegioididong.com\">MacBook</a>&nbsp;bạn c&ograve;n c&oacute; c&aacute;c cổng kết nối kh&ocirc;ng d&acirc;y kh&aacute;c như&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/wi-fi-chuan-80211ax-la-gi-tim-hieu-ve-wi-fi-the-he-thu-6-1187524\" target=\"_blank\" title=\"Wi-Fi chuẩn 802.11ax là gì? Tìm hiểu về Wi-Fi thế hệ thứ 6\">Wi-Fi 6 802.11ax</a>,&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/bluetooth-50-chuan-bluetooth-danh-cho-thoi-dai-kho-1113891\" target=\"_blank\" title=\"Bluetooth 5.0 chuẩn Bluetooth dành cho thời đại không dây\">Bluetooth v5.0</a>&nbsp;để phục vụ tốt cho những c&ocirc;ng việc của bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/231255/apple-macbook-pro-2020-z11c-060620-120615.jpg\" onclick=\"return false;\"><img alt=\"Laptop Apple Macbook Pro 2020 M1/16GB/512GB (Z11C) - Kết nối\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/231255/apple-macbook-pro-2020-z11c-060620-120615.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/231255/apple-macbook-pro-2020-z11c-060620-120615.jpg\" title=\"Laptop Apple Macbook Pro 2020 M1/16GB/512GB (Z11C) - Kết nối\" /></a></p>\r\n\r\n<h3>H&igrave;nh ảnh ch&acirc;n thật đến từng chi tiết</h3>\r\n\r\n<p>Với độ ph&acirc;n giải&nbsp;<strong>2560 x 1600</strong>&nbsp;v&ocirc; c&ugrave;ng sắc n&eacute;t,&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-retina-la-gi-905780\" target=\"_blank\" title=\"Màn hình Retina là gì? | Thegioididiong.com\">m&agrave;n h&igrave;nh Retina</a>&nbsp;<a href=\"https://www.thegioididong.com/laptop-13-inch\" target=\"_blank\" title=\"Xem thêm một số laptop màn hình 13 inch đang được kinh doanh tại Thegioididong.com\">13.3 inch</a>&nbsp;hiển thị l&yacute; tưởng mọi nội dung bạn cần theo d&otilde;i, từ trang web, văn bản r&otilde; r&agrave;ng cho đến những đoạn phim sống động.</p>\r\n\r\n<p>Ấn tượng hơn nữa, với độ&nbsp;s&aacute;ng m&agrave;n h&igrave;nh l&ecirc;n đến&nbsp;<strong>500 nits</strong>, bạn sẽ c&oacute; được một trải nghiệm &aacute;nh s&aacute;ng tuyệt vời tr&ecirc;n một m&agrave;n h&igrave;nh hiển thị h&igrave;nh ảnh cực kỳ sống động v&agrave; ch&acirc;n thật. B&ecirc;n cạnh đ&oacute;, với&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-ips-lcd-la-gi-905752\" target=\"_blank\" title=\"Tấm nền IPS\">tấm nền IPS</a>, bạn sẽ đắm ch&igrave;m v&agrave;o một thế giới m&agrave;u sắc v&ocirc; c&ugrave;ng rực rỡ v&agrave; c&oacute; được một g&oacute;c nh&igrave;n rộng hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/231255/apple-macbook-pro-2020-z11c-060620-120603.jpg\" onclick=\"return false;\"><img alt=\"Laptop Apple Macbook Pro 2020 M1/16GB/512GB (Z11C) - Màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/231255/apple-macbook-pro-2020-z11c-060620-120603.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/231255/apple-macbook-pro-2020-z11c-060620-120603.jpg\" title=\"Laptop Apple Macbook Pro 2020 M1/16GB/512GB (Z11C) - Màn hình\" /></a></p>\r\n\r\n<p>H&igrave;nh ảnh của bạn sẽ kh&ocirc;ng bị mờ đi, m&agrave; c&ograve;n trở n&ecirc;n lung linh hơn, xinh đẹp hơn với&nbsp;<strong>webcam HD</strong>&nbsp;đem lại một h&igrave;nh ảnh si&ecirc;u n&eacute;t khi sử dụng&nbsp;FaceTime camera. Những cuộc họp trực tuyến cũng sẽ được diễn ra tr&ocirc;i chảy với &acirc;m thanh ổn định nhờ t&iacute;ch hợp&nbsp;<strong>3 micro</strong>&nbsp;tr&ecirc;n laptop.</p>\r\n\r\n<p>Mở kh&oacute;a m&aacute;y t&iacute;nh x&aacute;ch tay dễ d&agrave;ng v&agrave; bảo mật hơn bao giờ hết tr&ecirc;n Macbook Pro 2020 bởi n&oacute; sẽ c&oacute;&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/fingerprint-sensor-la-gi-va-co-tac-dung-gi-904389\" target=\"_blank\" title=\"Bảo mật vân tay\">cảm biến v&acirc;n tay</a>&nbsp;tr&ecirc;n laptop, bạn sẽ kh&ocirc;ng cần phải nhập mật khẩu khi đăng nhập m&agrave; chỉ một lần chạm l&agrave; đủ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/231255/apple-macbook-pro-2020-z11c-062620-122635.jpg\" onclick=\"return false;\"><img alt=\"Laptop Apple Macbook Pro 2020 M1/16GB/512GB (Z11C) - Tiện ích\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/231255/apple-macbook-pro-2020-z11c-062620-122635.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/231255/apple-macbook-pro-2020-z11c-062620-122635.jpg\" title=\"Laptop Apple Macbook Pro 2020 M1/16GB/512GB (Z11C) - Tiện ích\" /></a></p>\r\n\r\n<h3>Tận hưởng &acirc;m thanh cao cấp</h3>\r\n\r\n<p>Nếu bạn l&agrave; một người th&iacute;ch nghe nhạc, xem phim th&igrave; đ&acirc;y l&agrave; một con laptop ph&ugrave; hợp để đ&aacute;p ứng &acirc;m thanh cực chất. Bạn sẽ được trải nghiệm chiều s&acirc;u &acirc;m nhạc mới đầy sống động v&agrave; ch&acirc;n thực với&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-am-thanh-stereo-am-thanh-kep-dual-spea-920547\" target=\"_blank\" title=\"Tìm hiểu về loa kép (2 kênh)\">loa k&eacute;p (2 k&ecirc;nh)</a>&nbsp;cộng với c&ocirc;ng nghệ &acirc;m thanh&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-am-thanh-dolby-altmos-la-gi-co-nhung-gi-noi-bat-1280672\" target=\"_blank\" title=\"Công nghệ âm thanh Dolby Altmos là gì? Có những gì nổi bật?\">Dolby Altmos</a>, đem lại cho bạn những &acirc;m thanh chi tiết, chất lượng cao để được tận hưởng cuộc sống tuyệt vời hơn.</p>\r\n', 3, 'laptop-apple-macbook-pro-m12020-16gb-grey-12.html', 'Khả Như', '', 'https://cdn.tgdd.vn/Products/Images/44/231255/apple-macbook-pro-2020-z11c-3-600x600.jpg', '2', NULL, NULL, 2, 5),
(13, '#IMAS13', 'Laptop Asus VivoBook ', 8490000, NULL, '<p>15.6&quot;, HD</p>\r\n\r\n<p>CPU: Intel Celeron, 1.10 GHz</p>\r\n\r\n<p>Card: Intel UHD Graphics 600</p>\r\n\r\n<p>Nặng: 1.8 kg, 2-cell Li-ion, 37 Wh</p>\r\n', '<h3>Laptop Asus VivoBook X515MA (BR111T) l&agrave; một mẫu&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" rel=\"noopener\" target=\"_blank\" title=\"Xem thêm các sản phẩm laptop học tập, văn phòng đang bán tại Thegioididong.com\">laptop học tập - văn ph&ograve;ng</a>&nbsp;c&oacute; thiết kế gọn nhẹ, hiệu năng ổn định ph&ugrave; hợp với c&aacute;c bạn thường xuy&ecirc;n sử dụng c&aacute;c ứng dụng văn ph&ograve;ng nhẹ nh&agrave;ng.</h3>\r\n\r\n<h3>Cấu h&igrave;nh ổn định, xử l&yacute; tốt c&aacute;c ứng dụng văn ph&ograve;ng</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop-asus-vivobook\" rel=\"noopener\" target=\"_blank\" title=\"Xem thêm các sản phẩm Asus VivoBook đang bán tại Thegioididong.com\">Laptop Asus VivoBook</a>&nbsp;được trang bị con chip&nbsp;<a href=\"https://www.thegioididong.com/laptop-celeron-pentium\" rel=\"noopener\" target=\"_blank\" title=\"Xem thêm các laptop Intel Celeron/Pentium đang bán tại Thegioididong.com\">Intel Celeron&nbsp;</a>c&oacute; hiệu năng ổn định xử l&yacute; c&aacute;c t&aacute;c vụ căn bản, với<strong>&nbsp;2 nh&acirc;n 2 luồng</strong>&nbsp;c&oacute; xung nhịp&nbsp;<strong>1.10 GHz</strong>&nbsp;gi&uacute;p lướt web, xem phim, nghe nhạc,... V&agrave; đạt xung nhịp tối đa l&agrave;<strong>&nbsp;2.8 GHz</strong>&nbsp;nhờ&nbsp;<strong>Turbo Boost&nbsp;</strong>để xử l&yacute; một số c&ocirc;ng việc văn ph&ograve;ng như: Word, Excel, Powerpoint,...</p>\r\n\r\n<p>Đi c&ugrave;ng với chip l&agrave; card đồ họa t&iacute;ch hợp&nbsp;<strong>Intel UHD Graphics 600</strong>&nbsp;hỗ trợ việc xử l&yacute; h&igrave;nh ảnh tốt hơn v&agrave; tiết kiệm pin hiệu quả nhờ c&oacute; mức ti&ecirc;u thụ điện năng thấp n&ecirc;n th&iacute;ch hợp cho c&aacute;c t&aacute;c vụ văn ph&ograve;ng cơ bản. Thời lượng pin đạt khoảng từ&nbsp;<strong>3 - 4 giờ</strong>&nbsp;đồng hồ t&ugrave;y v&agrave;o mục đ&iacute;ch sử dụng của bạn.</p>\r\n\r\n<p>M&aacute;y c&oacute;&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=4-gb\" rel=\"noopener\" target=\"_blank\" title=\"Laptop được trang bị RAM 4 GB đang kinh doanh tại Thegioididong.com\"><strong>RAM 4 GB</strong></a>&nbsp;cho khả năng xử l&yacute; đa nhiệm ổn định, bạn c&oacute; thể mở c&ugrave;ng l&uacute;c nhiều ứng dụng để phục vụ cho c&ocirc;ng việc tốt hơn m&agrave; kh&ocirc;ng lo m&aacute;y bị đơ hay giật. Nếu 4 GB c&oacute; thể l&agrave; qu&aacute; &iacute;t với bạn th&igrave; m&aacute;y c&ograve;n hỗ trợ n&acirc;ng cấp RAM l&ecirc;n&nbsp;<strong>16 GB</strong>&nbsp;gi&uacute;p bạn dễ d&agrave;ng n&acirc;ng cấp cho ph&ugrave; hợp với nhu cầu.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/233852/asus-vivobook-x515ma-n4020-br111t-151221-101256.jpg\" onclick=\"return false;\"><img alt=\"Laptop Asus VivoBook X515MA (BR111T) - Hiệu năng\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/233852/asus-vivobook-x515ma-n4020-br111t-151221-101256.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/233852/asus-vivobook-x515ma-n4020-br111t-151221-101256.jpg\" title=\"Laptop Asus VivoBook X515MA (BR111T) - Hiệu năng\" /></a></p>\r\n\r\n<p>Thời gian khởi động m&aacute;y nhanh ch&oacute;ng v&agrave; khả năng lưu trữ lớn nhờ ổ cứng&nbsp;<strong><a href=\"https://www.thegioididong.com/laptop?g=ssd-256-gb\" rel=\"noopener\" target=\"_blank\" title=\"Xem thêm các laptop có SSD 256 GB\">SSD 256 GB</a>&nbsp;</strong>tr&ecirc;n laptop. Bạn chỉ cần mất khoảng 10 - 15 gi&acirc;y để khởi động laptop v&agrave; 3 &ndash; 5 gi&acirc;y để khởi động c&aacute;c ứng dụng văn ph&ograve;ng, giải tr&iacute;, thỏa sức lưu trữ mọi dữ liệu cần thiết cho c&ocirc;ng việc, học tập hay những bộ phim y&ecirc;u th&iacute;ch để xem khi rảnh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/233852/asus-vivobook-x515ma-n4020-br111t-151321-101347.jpg\" onclick=\"return false;\"><img alt=\"Laptop Asus VivoBook X515MA (BR111T) - SSD\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/233852/asus-vivobook-x515ma-n4020-br111t-151321-101347.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/233852/asus-vivobook-x515ma-n4020-br111t-151321-101347.jpg\" title=\"Laptop Asus VivoBook X515MA (BR111T) - SSD\" /></a></p>\r\n\r\n<h3>Thiết kế gọn g&agrave;ng, linh hoạt cho việc di chuyển</h3>\r\n\r\n<p>Chiếc<a href=\"https://www.thegioididong.com/laptop-asus\" rel=\"noopener\" target=\"_blank\" title=\"Xem thêm các sản phẩm laptop Asus đang bán tại Thegioididong.com\">&nbsp;laptop Asus</a>&nbsp;c&oacute; lớp vỏ được l&agrave;m từ chất liệu nhựa vừa cho khối lượng nhẹ vừa gi&uacute;p tản nhiệt nhanh ch&oacute;ng, c&aacute;c g&oacute;c cạnh của m&aacute;y được v&aacute;t cong tỉ mỉ tạo n&ecirc;n sự liền mạch cho c&aacute;c bộ phận của laptop.</p>\r\n\r\n<p>M&aacute;y c&oacute; trọng lượng chỉ&nbsp;<strong>1.8 kg&nbsp;</strong>v&agrave; mỏng&nbsp;<strong>19.9 mm</strong>&nbsp;kh&aacute; l&agrave; gọn nhẹ, sẽ kh&ocirc;ng g&acirc;y ra bất cứ vấn đề g&igrave; khi bạn di chuyển c&ugrave;ng với n&oacute;, bạn chỉ cần bỏ n&oacute; v&agrave;o những chiếc balo hay t&uacute;i x&aacute;ch c&oacute; k&iacute;ch thước vừa phải l&agrave; đ&atilde; c&oacute; thể mang theo thuận tiện cho c&ocirc;ng việc.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/233852/asus-vivobook-x515ma-n4020-br111t-151421-101410.jpg\" onclick=\"return false;\"><img alt=\"Laptop Asus VivoBook X515MA (BR111T) - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/233852/asus-vivobook-x515ma-n4020-br111t-151421-101410.jpg\" height=\"444\" src=\"https://cdn.tgdd.vn/Products/Images/44/233852/asus-vivobook-x515ma-n4020-br111t-151421-101410.jpg\" title=\"Laptop Asus VivoBook X515MA (BR111T) - Thiết kế\" width=\"800\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/hoi-dap/fingerprint-sensor-la-gi-va-co-tac-dung-gi-904389\" rel=\"noopener\" target=\"_blank\" title=\"Tìm hiểu thêm bảo mật vân tay là gì?\"><strong>Cảm biến v&acirc;n tay</strong></a>&nbsp;được t&iacute;ch hợp tr&ecirc;n b&agrave;n di chuột gi&uacute;p bạn mở kh&oacute;a nhanh ch&oacute;ng chỉ với một c&aacute;i chạm nhẹ. B&ecirc;n cạnh tốc độ mở kh&oacute;a nhanh cảm biến c&ograve;n c&oacute; khả năng bảo mật tốt hơn so với mật khẩu trước đ&acirc;y, gi&uacute;p đảm bảo an to&agrave;n cho c&aacute;c dữ liệu quan trọng b&ecirc;n trong m&aacute;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/233852/cam-bien-van-tay.jpg\" onclick=\"return false;\"><img alt=\"Laptop Asus VivoBook X515MA (BR111T) - Vân tay\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/233852/cam-bien-van-tay.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/233852/cam-bien-van-tay.jpg\" title=\"Laptop Asus VivoBook X515MA (BR111T) - Vân tay\" /></a></p>\r\n\r\n<p>Tuy m&aacute;y c&oacute; k&iacute;ch thước nhỏ gọn nhưng vẫn trang bị đầy đủ c&aacute;c cổng kết nối th&ocirc;ng dụng dọc hai b&ecirc;n th&acirc;n m&aacute;y như: USB 3.2, USB 2.0, HDMI, khe đọc thẻ nhớ Micro SD, USB Type-C gi&uacute;p kết nối c&oacute; d&acirc;y dễ d&agrave;ng với c&aacute;c thiết bị kh&aacute;c như tivi hay m&aacute;y chiếu, chia sẻ dữ liệu nhanh ch&oacute;ng v&agrave; hỗ trợ c&ocirc;ng suất truyền tải điện l&ecirc;n đến 100W. Ngo&agrave;i ra, m&aacute;y c&ograve;n c&oacute; 2 phương thức kết nối kh&ocirc;ng d&acirc;y Bluetooth 4.1 v&agrave; Wi-Fi 802.11 a/b/g/n/ac cho tốc độ đường truyền nhanh, phạm vi kết nối lớn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/2021/02/content/ckn-800x500.jpg\" onclick=\"return false;\"><img alt=\"Laptop Asus VivoBook X515MA (BR111T) - Cổng kết nối\" data-src=\"https://cdn.tgdd.vn/2021/02/content/ckn-800x500.jpg\" height=\"500\" src=\"https://cdn.tgdd.vn/2021/02/content/ckn-800x500.jpg\" width=\"800\" /></a></p>\r\n\r\n<p>VivoBook X515MA cung cấp cho bạn một thời lượng pin từ&nbsp;<strong>3 đến 4 giờ</strong>&nbsp;tuỳ thuộc v&agrave;o nhu cầu sử dụng của bạn, đủ để đi c&ugrave;ng bạn một buổi học tại trường hay c&aacute;c cuộc họp tr&ecirc;n c&ocirc;ng ty.</p>\r\n\r\n<h3>M&agrave;n h&igrave;nh lớn, h&igrave;nh ảnh sắc n&eacute;t</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop\" rel=\"noopener\" target=\"_blank\" title=\"Xem thêm các mẫu laptop đang kinh doanh tại thegioididong.com\">Laptop</a>&nbsp;sở hữu m&agrave;n h&igrave;nh&nbsp;<strong><a href=\"https://www.thegioididong.com/laptop-tren-15-inch\" rel=\"noopener\" target=\"_blank\" title=\"Xem thêm một số laptop màn hình 15.6 inch đang được kinh doanh tại Thegioididong.com\">15.6 inch</a></strong>&nbsp;với độ ph&acirc;n giải&nbsp;<strong><a href=\"https://www.thegioididong.com/laptop-hd\" rel=\"noopener\" target=\"_blank\" title=\"Xem thêm các Laptop có độ phân giải HD đang bán tại Thegioididong.com\">HD</a>&nbsp;</strong>cho h&igrave;nh ảnh hiển thị sống động tr&ecirc;n một kh&ocirc;ng gian rộng r&atilde;i với chất lượng tốt nhất. Kết hợp c&ocirc;ng nghệ chống ch&oacute;i&nbsp;<strong>Anti Glare</strong>&nbsp;để h&igrave;nh ảnh chi tiết, ch&acirc;n thực v&agrave; r&otilde; r&agrave;ng nhất ở mọi g&oacute;c nh&igrave;n, thoải m&aacute;i cả khi l&agrave;m việc ngo&agrave;i trời.&nbsp;</p>\r\n\r\n<p>M&agrave;n h&igrave;nh sử dụng c&ocirc;ng nghệ&nbsp;<strong>LED Backlit</strong>&nbsp;cho m&agrave;n h&igrave;nh mỏng, ti&ecirc;u tốn &iacute;t điện năng. Ngo&agrave;i ra c&ograve;n gi&uacute;p tăng độ tương phản của h&igrave;nh ảnh v&agrave; m&agrave;u sắc hiển thị để khung h&igrave;nh trở n&ecirc;n ho&agrave;n hảo hơn, đem đến cho bạn những khung h&igrave;nh tốt nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/233852/asus-vivobook-x515ma-n4020-br111t-151321-101316.jpg\" onclick=\"return false;\"><img alt=\"Laptop Asus VivoBook X515MA (BR111T) - Màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/233852/asus-vivobook-x515ma-n4020-br111t-151321-101316.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/233852/asus-vivobook-x515ma-n4020-br111t-151321-101316.jpg\" title=\"Laptop Asus VivoBook X515MA (BR111T) - Màn hình\" /></a></p>\r\n\r\n<p>M&aacute;y sử dụng&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/am-thanh-sonicmaster-la-gi-950390\" rel=\"noopener\" target=\"_blank\" title=\"Tìm hiểu về SonicMaster\">c&ocirc;ng nghệ&nbsp;<strong>SonicMaster</strong></a>&nbsp;đ&acirc;y l&agrave; c&ocirc;ng nghệ &acirc;m thanh độc quyền tr&ecirc;n c&aacute;c sản phẩm của Asus. N&oacute; gi&uacute;p tối ưu &acirc;m thanh dựa tr&ecirc;n phần cứng tr&ecirc;n m&aacute;y t&iacute;nh cho &acirc;m thanh ph&aacute;t ra to, r&otilde; r&agrave;ng, lọc bỏ tiếng ồn, &acirc;m Bass s&acirc;u, &acirc;m thanh ch&acirc;n thực v&agrave; sinh động,... tạo n&ecirc;n kh&ocirc;ng gian &acirc;m nhạc sống động để bạn đắm ch&igrave;m v&agrave;o đ&oacute;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/233852/asus-vivobook-x515ma-n4020-br111t-090221-0438053.jpg\" onclick=\"return false;\"><img alt=\"Laptop Asus VivoBook X515MA (BR111T) - Âm thanh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/233852/asus-vivobook-x515ma-n4020-br111t-090221-0438053.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/233852/asus-vivobook-x515ma-n4020-br111t-090221-0438053.jpg\" title=\"Laptop Asus VivoBook X515MA (BR111T) - Âm thanh\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop/asus-vivobook-x515ma-n4020-br111t\" rel=\"noopener\" target=\"_blank\" title=\"Laptop Asus VivoBook X515MA (BR111T)\">Laptop Asus VivoBook X515MA (BR111T)</a>&nbsp;l&agrave; sự lựa chọn ph&ugrave; hợp d&agrave;nh cho sinh vi&ecirc;n, nh&acirc;n vi&ecirc;n văn ph&ograve;ng khi cần một chiếc laptop mỏng nhẹ, di động nhưng vẫn c&oacute; cấu h&igrave;nh ổn định đ&aacute;p ứng tốt c&aacute;c nhu cầu l&agrave;m việc căn bản.</p>\r\n', 5, 'laptop-asus-vivobook-x515ma-n4020-13.html', 'Kiều Diễm', '', 'https://cdn.tgdd.vn/Products/Images/44/233852/t-600x400.jpg', '2', NULL, NULL, 2, 6),
(14, '#IMAC14', 'Acer Nitro 5 AN515', 23490000, 25990000, '<p>15.6&quot;, Full HD</p>\r\n\r\n<p>CPU: AMD Ryzen 5, 3.30 GHz</p>\r\n\r\n<p>Card: NVIDIA GeForce GTX 1650 4 GB</p>\r\n\r\n<p>Nặng: 2.2 kg, 4-cell Li-ion, 57 Wh</p>\r\n', '<h3>Laptop Acer Nitro 5 AN515 45 R3SM R5 (NH.QBMSV.005) c&oacute; vẻ ngo&agrave;i h&igrave;nh hầm hố đặc trưng của d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=laptop-gaming\" target=\"_blank\" title=\"Xem thêm một số mẫu laptop Gaming đang được bán tại Thegioididong.com\">laptop gaming</a>, cấu h&igrave;nh mạnh mẽ chạy mượt c&aacute;c tựa game được c&aacute;c game thủ ưa th&iacute;ch với con chip AMD Ryzen 5 v&agrave; card đồ họa NVIDIA GeForce GTX 1650 4GB.</h3>\r\n\r\n<h3>Bộ xử l&iacute; mạnh mẽ, d&agrave;nh ri&ecirc;ng để chiến game</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop-acer\" target=\"_blank\" title=\"Xem thêm một số mẫu laptop Acer đang được bán tại Thegioididong.com\">Chiếc laptop Acer&nbsp;</a>n&agrave;y được trang bị con chip&nbsp;<strong><a href=\"https://www.thegioididong.com/laptop-cpu-amd\" target=\"_blank\" title=\"Xem thêm một số laptop CPU AMD đang được bán tại Thegioididong.com\">AMD</a>&nbsp;Ryzen 5</strong>&nbsp;<strong>5600H</strong>&nbsp;cho hiệu năng mượt m&agrave; chạy tốt c&aacute;c ứng dụng đồ họa đến c&aacute;c tựa game cấu h&igrave;nh cao với với&nbsp;<strong>6 l&otilde;i 12 luồng</strong>, xung nhịp trung b&igrave;nh l&agrave;&nbsp;<strong>3.30 GHz</strong>&nbsp;v&agrave; đạt tối đa l&ecirc;n đến<strong>&nbsp;4.2 GHz&nbsp;</strong>nhờ Turbo Boost.</p>\r\n\r\n<p><strong><a href=\"https://www.thegioididong.com/laptop-8-gb\" target=\"_blank\" title=\"Xem thêm một số laptop RAM 8 GB đang được bán tại Thegioididong.com\">RAM 8 GB</a>&nbsp;DDR4 (2 khe)</strong>&nbsp;cho khả năng đa nhiệm mượt m&agrave;, bạn c&oacute; thể mở c&ugrave;ng l&uacute;c nhiều tab Chrome, ứng dụng m&agrave; kh&ocirc;ng lo m&aacute;y bị giật, lag với&nbsp;<strong>tốc độ Bus RAM 3200 MHz</strong>. Hỗ trợ khe RAM tối đa l&ecirc;n đến<strong>&nbsp;32 GB</strong>&nbsp;thuận tiện hơn cho việc n&acirc;ng cấp khi nhu cầu sử dụng cao.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-4.jpg\" onclick=\"return false;\"><img alt=\"Acer Nitro 5 AN515 45 R3SM R5 5600H/144Hz (NH.QBMSV.005) - Cấu hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-4.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-4.jpg\" title=\"Acer Nitro 5 AN515 45 R3SM R5 5600H/144Hz (NH.QBMSV.005) - Cấu hình\" /></a></p>\r\n\r\n<p>Đi c&ugrave;ng với CPU l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/laptop-card-do-hoa-roi\" target=\"_blank\" title=\"Xem thêm một số laptop có Card đồ họa rời đang được bán tại Thegioididong.com\">card đồ họa rời&nbsp;</a><a href=\"https://www.thegioididong.com/laptop-geforce-gtx\" target=\"_blank\" title=\"Xem thêm laptop trang bị GeForce GTX đang bán tại thegioididong.com\">NVIDIA GeForce GTX 1650 4 GB</a>&nbsp;cho khả năng xử l&yacute; h&igrave;nh ảnh mượt m&agrave;, tốc độ qu&eacute;t cao bạn c&oacute; thể chiến c&aacute;c tựa game được ưa th&iacute;ch hiện nay như: PUBG, GTA V, FIFA 20, Fortnite, Dota 2, Apex Legends,... ở mức đồ họa trung b&igrave;nh, cao. Ngo&agrave;i ra, bạn ho&agrave;n to&agrave;n c&oacute; thể tận dụng hiệu năng của m&aacute;y để l&agrave;m c&aacute;c c&ocirc;ng việc thiết kế tr&ecirc;n m&agrave; c&aacute;c ứng dụng đồ họa như: Adobe Photoshop, Adobe Premiere, Adobe Illustrator,...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-5-1.jpg\" onclick=\"return false;\"><img alt=\"Acer Nitro 5 AN515 45 R3SM R5 5600H/144Hz (NH.QBMSV.005) - Card đồ hoạ\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-5-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-5-1.jpg\" title=\"Acer Nitro 5 AN515 45 R3SM R5 5600H/144Hz (NH.QBMSV.005) - Card đồ hoạ\" /></a></p>\r\n\r\n<p>Laptop trang bị sẵn ổ cứng&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=ssd-512-gb\" target=\"_blank\" title=\"Xem thêm một số laptop có ổ cứng SSD 512 GB đang được bán tại Thegioididong.com\">SSD 512 GB</a>&nbsp;NVMe PCIe cho tốc độ khởi động m&aacute;y, mở ứng dụng, truy xuất dữ liệu nhanh ch&oacute;ng gi&uacute;p bạn ho&agrave;n th&agrave;nh c&ocirc;ng việc nhanh ch&oacute;ng. Dung lượng lưu trữ l&ecirc;n đến 512 GB bạn c&oacute; thể c&agrave;i đặt c&aacute;c phần mềm, tựa game ưa th&iacute;ch v&agrave; lưu trữ những t&agrave;i liệu quan trọng m&agrave; kh&ocirc;ng cần phải đắn đo suy nghĩ nhiều. M&aacute;y c&ograve;n hỗ trợ th&ecirc;m 2 khe cắm&nbsp;<strong>SSD M.2 PCIe</strong>&nbsp;v&agrave;<strong>&nbsp;HDD SATA</strong>&nbsp;mở rộng kh&ocirc;ng gian lưu trữ dễ d&agrave;ng hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-6.jpg\" onclick=\"return false;\"><img alt=\"Acer Nitro 5 AN515 45 R3SM R5 5600H/144Hz (NH.QBMSV.005) - SSD\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-6.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-6.jpg\" title=\"Acer Nitro 5 AN515 45 R3SM R5 5600H/144Hz (NH.QBMSV.005) - SSD\" /></a></p>\r\n\r\n<p>C&ocirc;ng nghệ tản nhiệt<strong>&nbsp;Acer CoolBoost&nbsp;</strong>với hệ thống 2 quạt tản nhiệt gi&uacute;p loại bỏ t&igrave;nh trạng qu&aacute; nhiệt ở laptop khi bạn chơi game trong thời gian d&agrave;i.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-9.jpg\" onclick=\"return false;\"><img alt=\"Acer Nitro 5 AN515 45 R3SM R5 5600H/144Hz (NH.QBMSV.005) - Tản nhiệt\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-9.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-9.jpg\" title=\"Acer Nitro 5 AN515 45 R3SM R5 5600H/144Hz (NH.QBMSV.005) - Tản nhiệt\" /></a></p>\r\n\r\n<h3>Thiết kế mạnh mẽ, đậm chất gaming</h3>\r\n\r\n<p>Vỏ của chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-acer-nitro\" target=\"_blank\" title=\"https://www.thegioididong.com/laptop-acer-nitroXem thêm một số mẫu laptop Acer Nitro đang được bán tại Thegioididong.com\">laptop Acer Nitro</a>&nbsp;n&agrave;y được l&agrave;m bằng nhựa cao cấp c&oacute; độ bền cao được bao trọn bởi gam m&agrave;u đen mạnh mẽ đầy cuốn h&uacute;t, kết hợp c&ugrave;ng c&aacute;c đường cắt g&oacute;c cạnh c&agrave;ng tạo n&ecirc;n sự hầm hố, bụi bặm chuẩn gaming. Chiếc laptop n&agrave;y c&oacute; độ d&agrave;y&nbsp;<strong>23.9 mm&nbsp;</strong>v&agrave; trọng lượng<strong>&nbsp;2.2 kg&nbsp;</strong>bạn c&oacute; thể để v&agrave;o balo để mang theo khi đi l&agrave;m, đi học.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-2.jpg\" onclick=\"return false;\"><img alt=\"Acer Nitro 5 AN515 45 R3SM R5 5600H/144Hz (NH.QBMSV.005) - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-2.jpg\" title=\"Acer Nitro 5 AN515 45 R3SM R5 5600H/144Hz (NH.QBMSV.005) - Thiết kế\" /></a></p>\r\n\r\n<p>B&agrave;n ph&iacute;m thiết kế rộng r&atilde;i, h&agrave;nh tr&igrave;nh ph&iacute;m tốt, trang bị<strong>&nbsp;d&atilde;y ph&iacute;m số</strong>&nbsp;ri&ecirc;ng biệt thuận tiện cho việc thao t&aacute;c tr&ecirc;n game. B&ecirc;n cạnh việc b&agrave;n ph&iacute;m c&ograve;n được trang bị đ&egrave;n nền chuyển m&agrave;u<strong>&nbsp;RGB</strong>, c&oacute; thể điều chỉnh m&agrave;u sắc v&agrave; hiệu ứng dễ d&agrave;ng từ&nbsp;<a href=\"https://www.thegioididong.com/game-app/nitro-sense-la-gi-cong-cu-toi-uu-hoa-laptop-acer-nitro-5-1340768\" target=\"_blank\" title=\"Phần mềm Nitro Sense là gì?\">phần mềm Nitro Sense</a>&nbsp;tiện lợi,&nbsp;trợ gi&uacute;p bạn sử dụng laptop ở nơi &aacute;nh s&aacute;ng yếu, tạo điểm nhấn v&agrave; th&ecirc;m phần phấn kh&iacute;ch khi trải nghiệm.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-8.jpg\" onclick=\"return false;\"><img alt=\"Acer Nitro 5 AN515 45 R3SM R5 5600H/144Hz (NH.QBMSV.005) - Bàn phím chuyển màu RGB\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-8.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-8.jpg\" title=\"Acer Nitro 5 AN515 45 R3SM R5 5600H/144Hz (NH.QBMSV.005) - Bàn phím chuyển màu RGB\" /></a></p>\r\n\r\n<p>Dọc hai b&ecirc;n th&acirc;n&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm một số laptop đang được bán tại Thegioididong.com\">laptop</a>&nbsp;sở hữu đầy đủ cổng kết nối phổ biến hiện nay: USB 3.2, HDMI, LAN (RJ45), Type-C gi&uacute;p việc truyền tải dữ liệu giữa c&aacute;c thiết bị hiệu quả, tiện lợi hơn.</p>\r\n\r\n<p>Kết nối kh&ocirc;ng d&acirc;y của m&aacute;y n&agrave;y sử dụng chuẩn kết nối&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/wi-fi-chuan-80211ax-la-gi-tim-hieu-ve-wi-fi-the-he-thu-6-1187524\" target=\"_blank\" title=\"Wi-Fi chuẩn 802.11ax là gì? Tìm hiểu về Wi-Fi thế hệ thứ 6\">Wi-Fi 6 AX201</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/bluetooth-50-chuan-bluetooth-danh-cho-thoi-dai-kho-1113891\" target=\"_blank\" title=\"Bluetooth 5.0 chuẩn Bluetooth dành cho thời đại không dây\">Bluetooth v5.0</a>&nbsp;cho tốc độ truyền tải nhanh ch&oacute;ng, phạm vi phủ s&oacute;ng lớn hơn so với thế hệ trước đ&acirc;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-12.jpg\" onclick=\"return false;\"><img alt=\"Acer Nitro 5 AN515 45 R3SM R5 5600H/144Hz (NH.QBMSV.005) - Cổng kết nối\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-12.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-12.jpg\" title=\"Acer Nitro 5 AN515 45 R3SM R5 5600H/144Hz (NH.QBMSV.005) - Cổng kết nối\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh lớn, h&igrave;nh ảnh sắc n&eacute;t đắm ch&igrave;m v&agrave;o từng trận game</h3>\r\n\r\n<p>Laptop sử dụng&nbsp;<a href=\"https://www.thegioididong.com/laptop-tren-15-inch\" target=\"_blank\" title=\"Xem thêm một số laptop màn hình trên 15 inch đang được bán tại Thegioididong.com\">m&agrave;n h&igrave;nh 15.6 inch</a>&nbsp;độ ph&acirc;n giải<a href=\"https://www.thegioididong.com/laptop-full-hd\" target=\"_blank\" title=\"Xem thêm một số laptop có màn hình Full HD đang được bán tại Thegioididong.com\">&nbsp;Full HD</a>&nbsp;đem đến những khung h&igrave;nh đẹp, sắc n&eacute;t, c&ugrave;ng tần số&nbsp;<strong>144 Hz</strong>&nbsp;cho từng chuyển động của nh&acirc;n vật trở n&ecirc;n mượt m&agrave; hơn, hạn chế hiện tượng h&igrave;nh ảnh bị giật x&eacute; h&igrave;nh khi chiến c&aacute;c tựa game nặng hay xem phim h&agrave;nh động.</p>\r\n\r\n<p>Tấm nền&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-ips-lcd-la-gi-905752\" target=\"_blank\" title=\"Màn hình IPS LCD là gì?\">IPS</a>&nbsp;gi&uacute;p g&oacute;c nh&igrave;n của m&agrave;n h&igrave;nh được mở rộng l&ecirc;n đến&nbsp;<strong>178 độ</strong>, bạn c&oacute; thể quan s&aacute;t trận đấu c&ugrave;ng bạn b&egrave; m&agrave; kh&ocirc;ng phải chen ch&uacute;c trước m&agrave;n h&igrave;nh nữa.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-10.jpg\" onclick=\"return false;\"><img alt=\"Acer Nitro 5 AN515 45 R3SM R5 5600H/144Hz (NH.QBMSV.005) - Màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-10.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-10.jpg\" title=\"Acer Nitro 5 AN515 45 R3SM R5 5600H/144Hz (NH.QBMSV.005) - Màn hình\" /></a></p>\r\n\r\n<p>Sự kết hợp của bộ đ&ocirc;i c&ocirc;ng nghệ<strong>&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-tft-lcd-la-gi-905743\" target=\"_blank\" title=\"Màn hình TFT LCD là gì? Có gì vượt trội hơn màn hình LCD?\">TFT</a></strong>&nbsp;v&agrave;&nbsp;<strong>LED Backlit&nbsp;</strong>cho h&igrave;nh ảnh hiển thị sắc n&eacute;t hơn, s&aacute;ng hơn v&agrave; chuyển động mượt m&agrave; hơn.&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-man-hinh-acer-comfyview-co-gi-dac-biet-1192380\" target=\"_blank\" title=\"Tìm hiểu về màn hình Acer ComfyView\">Acer ComfyView</a>&nbsp;cho khả năng chống ch&oacute;i tốt đảm bảo chi tiết, m&agrave;u sắc của h&igrave;nh ảnh kh&ocirc;ng bị biến đổi khi sử dụng ở nơi &aacute;nh s&aacute;ng cao, hạn chế t&igrave;nh trạng mỏi mắt khi bạn tiếp x&uacute;c với m&agrave;n h&igrave;nh trong thời gian d&agrave;i.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-7.jpg\" onclick=\"return false;\"><img alt=\"Acer Nitro 5 AN515 45 R3SM R5 5600H/144Hz (NH.QBMSV.005) - Công nghệ màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-7.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-7.jpg\" title=\"Acer Nitro 5 AN515 45 R3SM R5 5600H/144Hz (NH.QBMSV.005) - \" /></a></p>\r\n\r\n<p>&Acirc;m thanh ph&aacute;t ra ch&acirc;n thực, mạnh mẽ gi&uacute;p bạn nhập vai v&agrave;o nh&acirc;n vật trong game hơn với c&ocirc;ng nghệ &acirc;m thanh độc quyền&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-am-thanh-trueharmony-co-gi-dac-biet-1017616\" target=\"_blank\" title=\"Công nghệ âm thanh Acer TrueHarmony có gì đặc biệt?\">Acer TrueHarmony</a>&nbsp;từ thương hiệu Acer. Kết hợp c&ugrave;ng c&ocirc;ng nghệ giả lập &acirc;m thanh v&ograve;m sống động&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-am-thanh-dts-tren-laptop-la-gi-1261821#dts-ultra-audio\" target=\"_blank\" title=\"Tìm hiều về DTS X: Ultra Audio\"><strong>DTS X:Ultra Audio</strong></a>&nbsp;mang đến cho game thủ c&aacute;c lợi thế cạnh tranh như định vị đối thủ hay x&aacute;c định c&aacute;c &acirc;m thanh m&ocirc;i trường quan trọng kh&aacute;c.​​</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-11.jpg\" onclick=\"return false;\"><img alt=\"Acer Nitro 5 AN515 45 R3SM R5 5600H/144Acer Nitro 5 AN515 45 R3SM R5 5600H/144Hz (NH.QBMSV.005) - Âm thanhHz (NH.QBMSV.005) - Công nghệ màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-11.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-11.jpg\" title=\"Acer Nitro 5 AN515 45 R3SM R5 5600H/144Hz (NH.QBMSV.005) - Âm thanh\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005\" target=\"_blank\" title=\"Laptop Acer Nitro 5 AN515 45 R3SM R5 (NH.QBMSV.005)\">Laptop Acer Nitro 5 AN515 45 R3SM R5 (NH.QBMSV.005)</a>&nbsp;l&agrave; sự lựa chọn ho&agrave;n hảo cho d&acirc;n m&ecirc; game c&oacute; c&ocirc;ng việc li&ecirc;n quan đến kỹ thuật hay đồ họa cần chiếc laptop c&oacute; m&agrave;n h&igrave;nh lớn, hiển thị tốt, thiết kế đẹp mắt v&agrave; cấu h&igrave;nh mạnh mẽ.</p>\r\n', 3, 'laptop-acer-nitro-an515-r3sm-r5-5600-18.html', 'Quốc Trường', '', 'https://cdn.tgdd.vn/Products/Images/44/237636/acer-nitro-5-an515-45-r3sm-r5-nhqbmsv005-600x600.jpg', '2', NULL, NULL, 2, 7);
INSERT INTO `tbl_product` (`product_id`, `product_code`, `product_name`, `product_price`, `old_price`, `product_desc`, `content`, `qty`, `slug`, `creator`, `editor`, `product_thumb`, `product_status`, `add_date`, `update_date`, `cat_id`, `child_id`) VALUES
(15, '#IMMSI15', 'MSI GF63 10SC i5 ', 20990000, NULL, '<p>15.6&quot;, Full HD</p>\r\n\r\n<p>CPU: Intel Core i5 Comet Lake, 2.50 GHz</p>\r\n\r\n<p>Card: NVIDIA GeForce GTX 1650 4 GB</p>\r\n\r\n<p>Nặng: 1.86 kg, 3-cell Li-ion, 51 Wh</p>\r\n', '<h3><a href=\"https://www.thegioididong.com/laptop/msi-gf63-10sc-i5-255vn\" target=\"_blank\" title=\"MSI GF63 10SC i5 (255VN) đang bán tại thegioididong.com\">MSI GF63 10SC i5 (255VN)</a>&nbsp;mang vẻ ngo&agrave;i mạnh mẽ, đậm chất gaming sở hữu chip i5 thế hệ 10 c&ugrave;ng card đồ họa NVIDIA GeForce GTX 1650 mang lại hiệu năng vượt trội, thoải m&aacute;i chiến c&aacute;c tựa game y&ecirc;u th&iacute;ch.</h3>\r\n\r\n<h3>Chiến c&aacute;c game thịnh h&agrave;nh với bộ CPU mạnh mẽ</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm các mẫu laptop đang bán tại thegioididong.com\">Laptop</a>&nbsp;sở hữu bộ vi xử l&yacute;&nbsp;<strong><a href=\"https://www.thegioididong.com/laptop?g=core-i5\" target=\"_blank\" title=\"Xem thêm các mẫu laptop trang bị Intel Core i5 đang bán tại thegioididong.com\">Intel Core i5</a>&nbsp;10300H Gen 10</strong>&nbsp;c&oacute; sức mạnh hiệu năng vượt trội tăng khả năng xử l&yacute; nhanh ch&oacute;ng v&agrave; tiết kiệm năng lượng với tốc độ xung nhịp trung b&igrave;nh&nbsp;<strong>2.50 GHz</strong>&nbsp;đạt cao nhất&nbsp;<strong>Turbo Boost 4.5 GHz</strong>.</p>\r\n\r\n<p><strong><a href=\"https://www.thegioididong.com/laptop-8-gb\" target=\"_blank\" title=\"Xem các mẫu laptp 8 GB đang bán tại thegioididong.com\">RAM 8 GB</a></strong>&nbsp;với tốc độ Bus RAM<strong>&nbsp;2666 MHz</strong>&nbsp;cho khả năng xử l&yacute; đa nhiệm, đ&aacute;p ứng được hầu hết c&aacute;c nhu cầu giải tr&iacute; l&agrave;m việc của bạn, d&ugrave; bạn mở nhiều ứng dụng c&ugrave;ng l&uacute;c cũng kh&ocirc;ng lo bị đơ m&aacute;y, đứng m&aacute;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/238608/msi-gf63-10sc-i5-255vn-4.jpg\" onclick=\"return false;\"><img alt=\"MSI GF63 10SC i5 10300H (255VN) - Cấu hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-4.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-4.jpg\" title=\"MSI GF63 10SC i5 10300H (255VN) - Cấu hình\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop-card-do-hoa-roi\" target=\"_blank\" title=\"Xem các mẫu laptop trang bị card đồ họa rời đang bán tại thegioididong.com\">Card đồ họa rời</a>&nbsp;<a href=\"https://www.thegioididong.com/laptop-geforce-gtx\" target=\"_blank\" title=\"Xem thêm laptop trang bị GeForce GTX đang bán tại thegioididong.com\">NVIDIA GeForce GTX 1650 4 GB</a>&nbsp;mang lại trải nghiệm chiến c&aacute;c tựa game hot như Far Cry 5, GTA 5, Li&ecirc;n Minh Huyền Thoại, FIFA Online 4,... một c&aacute;ch mượt m&agrave;, trơn tru ở mức c&agrave;i đặt trung b&igrave;nh - cao m&agrave; kh&ocirc;ng lo xảy ra hiện tượng b&oacute;ng ma hay giật lag h&igrave;nh ảnh. Đồng thời, bạn c&ograve;n c&oacute; thể sử dụng chiếc m&aacute;y n&agrave;y để thực hiện thiết kế đồ họa như chỉnh sửa h&igrave;nh ảnh, video bằng Photoshop, Ai, Pr,...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/238608/msi-gf63-10sc-i5-255vn-5.jpg\" onclick=\"return false;\"><img alt=\"MSI GF63 10SC i5 10300H (255VN) - Card đồ họa \" data-src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-5.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-5.jpg\" title=\"MSI GF63 10SC i5 10300H (255VN) - Card đồ họa \" /></a></p>\r\n\r\n<p><strong>Ổ cứng&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=ssd-512-gb\" target=\"_blank\" title=\"Xem thêm các sản phẩm laptop SSD 512 GB đang kinh doanh tại thegioididong.com\">SSD 512 GB</a>&nbsp;NVMe PCIe</strong>&nbsp;gi&uacute;p khởi động m&aacute;y, ứng dụng nhanh ch&oacute;ng v&agrave; cho kh&ocirc;ng gian lưu trữ vừa đủ nhiều tựa game y&ecirc;u th&iacute;ch, file h&igrave;nh ảnh, &acirc;m thanh,... Hơn nữa, MSI cung cấp khe cắm HDD SATA thoả sức n&acirc;ng cấp ổ cứng tuỳ theo nhu cầu của bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/238608/msi-gf63-10sc-i5-255vn-6.jpg\" onclick=\"return false;\"><img alt=\"MSI GF63 10SC i5 10300H (255VN) - SSD\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-6.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-6.jpg\" title=\"MSI GF63 10SC i5 10300H (255VN) - SSD\" /></a></p>\r\n\r\n<h3>Thiết kế phong c&aacute;ch, thể hiện c&aacute; t&iacute;nh&nbsp;</h3>\r\n\r\n<p>MSI GF63 thể hiện sự mạnh mẽ đặc trưng của d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=laptop-gaming\" target=\"_blank\" title=\"Xem thêm laptop gaming đang bán tại thegioididong.com\">laptop gaming</a>&nbsp;với vỏ mặt lưng được l&agrave;m từ kim loại bền bỉ kết hợp logo c&agrave;ng tăng th&ecirc;m vẻ huyền b&iacute; cho chiếc laptop n&agrave;y. M&aacute;y c&oacute; trọng lượng&nbsp;<strong>1.86 kg</strong>, độ d&agrave;y&nbsp;<strong>21.7 mm</strong>&nbsp;cũng c&oacute; thể được xem l&agrave; gọn nhẹ so với c&aacute;c laptop c&ugrave;ng ph&acirc;n kh&uacute;c.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/238608/msi-gf63-10sc-i5-255vn-2.jpg\" onclick=\"return false;\"><img alt=\"MSI GF63 10SC i5 10300H (255VN) - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-2.jpg\" title=\"MSI GF63 10SC i5 10300H (255VN) - Thiết kế\" /></a></p>\r\n\r\n<p>B&agrave;n ph&iacute;m c&oacute; k&iacute;ch thước hợp l&yacute;, h&agrave;nh tr&igrave;nh ph&iacute;m s&acirc;u, cảm gi&aacute;c g&otilde; ph&iacute;m tốt v&agrave; được trang bị&nbsp;<strong>đ&egrave;n nền</strong>&nbsp;m&agrave;u đỏ gi&uacute;p tăng th&ecirc;m sự th&iacute;ch th&uacute; hơn khi chơi game. B&ecirc;n cạnh đ&oacute;&nbsp;<strong>chiếu nghỉ tay</strong>&nbsp;rất thoải m&aacute;i v&agrave; chắc chắn, bạn sẽ cảm thấy sự m&aacute;t lạnh khi đặt tay l&ecirc;n khu vực n&agrave;y ngay sau khi mở m&aacute;y l&ecirc;n.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/238608/msi-gf63-10sc-i5-255vn-7.jpg\" onclick=\"return false;\"><img alt=\"MSI GF63 10SC i5 10300H (255VN) - Bàn phím\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-7.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-7.jpg\" title=\"MSI GF63 10SC i5 10300H (255VN) - Bàn phím\" /></a></p>\r\n\r\n<p>Hệ thống tản nhiệt&nbsp;<strong>CoolerBoost</strong>&nbsp;độc quyền hoạt động &ecirc;m &aacute;i gi&uacute;p giảm thiểu hơi n&oacute;ng nhanh ch&oacute;ng, tối ưu luồng gi&oacute; để đảm bảo mang lại hiệu năng game cao trong một th&acirc;n m&aacute;y gọn g&agrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/238608/msi-gf63-10sc-i5-255vn-8.jpg\" onclick=\"return false;\"><img alt=\"MSI GF63 10SC i5 10300H (255VN) - Tản nhiệt\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-8.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-8.jpg\" title=\"MSI GF63 10SC i5 10300H (255VN) - Tản nhiệt\" /></a></p>\r\n\r\n<p>Ngo&agrave;i ra,&nbsp;<a href=\"https://www.thegioididong.com/laptop-msi\" target=\"_blank\" title=\"Xem các mẫu laptop MSI đag bán tại thegioididong.com\">laptop MSI</a>&nbsp;c&ograve;n được trang bị đa dạng c&aacute;c cổng kết nối như HDMI, LAN (RJ45), USB 3.2, USB Type-C kết nối tiện lợi v&agrave; nhanh ch&oacute;ng với chuột, b&agrave;n ph&iacute;m rời, m&agrave;n h&igrave;nh rời, m&aacute;y chiếu,... Chuẩn kết nối kh&ocirc;ng d&acirc;y Bluetooth 5.1,&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/wi-fi-chuan-80211ax-la-gi-tim-hieu-ve-wi-fi-the-he-thu-6-1187524\" target=\"_blank\" title=\"Xem thêm về chuẩn Wi-Fi 6 AX201\">Wi-Fi 6 AX201</a>&nbsp;cho đường truyền mạng lu&ocirc;n ổn định, kh&ocirc;ng lo bị mất kết nối khi chơi game, giải tr&iacute;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/238608/msi-gf63-10sc-i5-255vn-12.jpg\" onclick=\"return false;\"><img alt=\"MSI GF63 10SC i5 10300H (255VN) - Cổng kết nối\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-12.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-12.jpg\" title=\"MSI GF63 10SC i5 10300H (255VN) - Cổng kết nối\" /></a></p>\r\n\r\n<h3>Trải nghiệm nhập vai qua&nbsp;<a href=\"https://www.thegioididong.com/laptop-tren-15-inch\" target=\"_blank\" title=\"Xem thêm các mẫu laptop trên 15 inch đang bán tại thegioididong.com\">m&agrave;n h&igrave;nh 15.6 inch</a>&nbsp;hiển thị ch&acirc;n thật</h3>\r\n\r\n<p>M&agrave;n h&igrave;nh c&oacute; độ ph&acirc;n giải&nbsp;<strong>Full HD (1920 x 1080)</strong>&nbsp;với tần số qu&eacute;t&nbsp;<strong>144 Hz</strong>&nbsp;mang lại h&igrave;nh ảnh sống động, m&agrave;u sắc được t&aacute;i tạo ch&iacute;nh x&aacute;c v&agrave; gi&uacute;p nắm bắt kịp thời từng chuyển động trong game, tốc độ chuyển đổi giữa c&aacute;c khung h&igrave;nh nhanh hơn, tăng khả năng chiến thắng.</p>\r\n\r\n<p>G&oacute;c nh&igrave;n rộng hơn đến&nbsp;<strong>178 độ</strong>&nbsp;cho h&igrave;nh ảnh chi tiết sắc n&eacute;t với&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-ips-lcd-la-gi-905752\" target=\"_blank\" title=\"Tìm hiểu thêm về tấm nền IPS\">tấm nền IPS</a>&nbsp;c&ugrave;ng c&ocirc;ng nghệ chống ch&oacute;i&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-man-hinh-chong-choi-anti-glare-1182688\" target=\"_blank\" title=\"Tìm hiểu thêm về công nghệ chống chói Anti Glare\">Anti Glare</a>&nbsp;giảm t&igrave;nh trạng ch&oacute;i, nh&ograve;e h&igrave;nh hay mỏi mắt, ch&oacute;ng mặt khi sử dụng m&aacute;y l&acirc;u hay trong điều kiện &aacute;nh s&aacute;ng mạnh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/238608/msi-gf63-10sc-i5-255vn-9.jpg\" onclick=\"return false;\"><img alt=\"MSI GF63 10SC i5 10300H (255VN) - Hình ảnh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-9.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-9.jpg\" title=\"MSI GF63 10SC i5 10300H (255VN) - Hình ảnh\" /></a></p>\r\n\r\n<p>Đắm ch&igrave;m trong c&aacute;c tr&ograve; chơi hay nghe nhạc, xem phim giải tr&iacute; qua kh&ocirc;ng gian &acirc;m thanh ph&aacute;t ra to r&otilde;, ch&acirc;n thật từ c&ocirc;ng nghệ&nbsp;<strong>Nahimic Audio</strong>.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/238608/msi-gf63-10sc-i5-255vn-11.jpg\" onclick=\"return false;\"><img alt=\"MSI GF63 10SC i5 10300H (255VN) - Âm thanh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-11.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255vn-11.jpg\" title=\"MSI GF63 10SC i5 10300H (255VN) - Âm thanh\" /></a></p>\r\n\r\n<p>H&atilde;y sở hữu ngay chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-msi?g=laptop-gaming\" target=\"_blank\" title=\"Xem thêm laptop gaming MSI đang bán tại thegioididong.com\">laptop MSI Gaming</a>&nbsp;n&agrave;y để thỏa sức c&ugrave;ng chiến c&aacute;c tựa game y&ecirc;u th&iacute;ch nh&eacute;.</p>\r\n', 2, 'laptop-msi-gf63-10sc-i5-10300h-8gb-15.html', 'Khả Ái', '', 'https://cdn.tgdd.vn/Products/Images/44/238608/msi-gf63-10sc-i5-255v-600x600.jpg', '2', NULL, NULL, 2, 8),
(16, '#IMIP16', 'iPhone 12 mini', 18990000, NULL, '<p>M&agrave;n h&igrave;nh 5.4&quot;, Chip Apple A14 Bionic</p>\r\n\r\n<p>RAM 4 GB, ROM 64 GB</p>\r\n\r\n<p>Camera sau: 2 camera 12 MP</p>\r\n\r\n<p>Camera trước: 12 MP</p>\r\n\r\n<p>Pin 2227 mAh, Sạc 20 W</p>\r\n', '<h3 id=\"docs-internal-guid-3e10861b-7fff-16ed-2752-ebfada7a01dd\"><a href=\"https://www.thegioididong.com/dtdd/iphone-12-mini\" title=\"Tham khảo giá điện thoại iPhone 12 Mini chính hãng\">iPhone 12 mini&nbsp;64 GB</a>&nbsp;tuy l&agrave; phi&ecirc;n bản thấp nhất trong bộ 4&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-12\" title=\"Tham khảo giá điện thoại iPhone 12 chính hãng\">iPhone 12</a>&nbsp;series, nhưng vẫn sở hữu những ưu điểm vượt trội về k&iacute;ch thước nhỏ gọn, tiện lợi, hiệu năng đỉnh cao, t&iacute;nh năng sạc nhanh c&ugrave;ng bộ camera chất lượng cao.</h3>\r\n\r\n<h3>Thiết kế sang trọng, cao cấp</h3>\r\n\r\n<p>Điểm nhấn đầu ti&ecirc;n phải kể đến ở d&ograve;ng m&aacute;y n&agrave;y ch&iacute;nh l&agrave; viền m&aacute;y kh&ocirc;ng c&ograve;n được thiết kế bo cong c&aacute;c cạnh, m&agrave; thay v&agrave;o đ&oacute; l&agrave; phần cạnh m&aacute;y được v&aacute;t phẳng v&ocirc; c&ugrave;ng mạnh mẽ v&agrave; c&aacute; t&iacute;nh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/225380/iphone-12-mini-055121-085135.jpg\" onclick=\"return false;\"><img alt=\"iPhone 12 Mini 64 GB | Thiết kế sang trọng, tỉ mỉ\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-055121-085135.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-055121-085135.jpg\" title=\"iPhone 12 Mini 64 GB | Thiết kế sang trọng, tỉ mỉ\" /></a></p>\r\n\r\n<p>Lần đầu ti&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/apple\" target=\"_blank\" title=\"Tham khảo thêm các dòng sản phẩm Apple\">Apple</a>&nbsp;sử dụng khung nh&ocirc;m cao cấp trong ng&agrave;nh h&agrave;ng kh&ocirc;ng vũ trụ tr&ecirc;n iPhone 12 Mini đem đến cho người d&ugrave;ng thiết kế cứng c&aacute;p nhưng trọng lượng kh&ocirc;ng qu&aacute; nặng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/225380/iphone-12-mini-050121-090102.jpg\" onclick=\"return false;\"><img alt=\"iPhone 12 Mini 64GB | Khung nhôm cao cấp từ ngành hàng không\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-050121-090102.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-050121-090102.jpg\" title=\"iPhone 12 Mini 64GB | Khung nhôm cao cấp từ ngành hàng không\" /></a></p>\r\n\r\n<p>M&aacute;y nổi bật với hệ thống camera h&igrave;nh vu&ocirc;ng độc đ&aacute;o, kết hợp c&ugrave;ng mặt lưng bằng k&iacute;nh b&oacute;ng bẩy cho cảm gi&aacute;c cầm nắm v&ocirc; c&ugrave;ng th&iacute;ch.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/225380/iphone-12-mini-064320-074303.jpg\" onclick=\"return false;\"><img alt=\"iPhone 12 Mini 64GB | Mặt lưng bóng bẩy, nhiều màu sắc chọn lựa\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-064320-074303.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-064320-074303.jpg\" title=\"iPhone 12 Mini 64GB | Mặt lưng bóng bẩy, nhiều màu sắc chọn lựa\" /></a></p>\r\n\r\n<p>Ngo&agrave;i ra, iPhone 12 Mini cũng đem tới cho người d&ugrave;ng nhiều m&agrave;u sắc cho bạn tha hồ lựa chọn. Đặc biệt, Apple vừa bổ sung m&agrave;u xanh dương vốn tươi tắn nhẹ nh&agrave;ng nổi bật để l&ocirc;i k&eacute;o sự ch&uacute; &yacute;, l&agrave; một sự lựa chọn mới mẻ ấn tượng ngay từ c&aacute;i nh&igrave;n đầu ti&ecirc;n.&nbsp;</p>\r\n\r\n<h3>M&agrave;n h&igrave;nh OLED Super Retina XDR si&ecirc;u sắc n&eacute;t</h3>\r\n\r\n<p>Ph&iacute;a trước vẫn l&agrave; m&agrave;n h&igrave;nh kiểu d&aacute;ng tai thỏ quen thuộc, với phần viền m&agrave;n h&igrave;nh được tinh gọn hơn một c&aacute;ch đ&aacute;ng kể mang đến cảm gi&aacute;c m&agrave;n h&igrave;nh lớn hơn d&ugrave; iPhone 12 Mini c&oacute; k&iacute;ch cỡ m&agrave;n h&igrave;nh chỉ 5.4 inch.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/225380/iphone-12-mini-055121-085143.jpg\" onclick=\"return false;\"><img alt=\"iPhone 12 Mini 64GB | Màn hình tinh gọn kiểu dáng tai thỏ quen thuộc\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-055121-085143.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-055121-085143.jpg\" title=\"iPhone 12 Mini 64GB | Màn hình tinh gọn kiểu dáng tai thỏ quen thuộc\" /></a></p>\r\n\r\n<p>M&agrave;n h&igrave;nh k&iacute;ch cỡ 5.4 inch l&agrave; điểm thuận lợi bởi m&aacute;y kh&aacute; nhỏ gọn, c&oacute; thể dễ d&agrave;ng đặt trong t&uacute;i &aacute;o, quần hơn so với 6.1 inch tr&ecirc;n điện thoại&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-12-pro\" target=\"_blank\" title=\"Tham khảo giá điện thoại iPhone 12 Pro chính hãng\">iPhone 12 Pro</a>&nbsp;hay 6.7 inch tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-12-pro-max\" target=\"_blank\" title=\"Tham khảo giá điện thoại iPhone 12 Pro Max chính hãng\">iPhone 12 Pro Max</a>.</p>\r\n\r\n<p>M&agrave;n h&igrave;nh của iPhone 12 Mini sử dụng tấm OLED Super Retina XDR tr&agrave;n viền c&oacute; độ ph&acirc;n giải Full HD+ (1080 x 2340 Pixels), từng chi tiết chuyển động tr&ecirc;n m&agrave;n h&igrave;nh đều hiện l&ecirc;n r&otilde; n&eacute;t, tươi s&aacute;ng v&agrave; kh&ocirc;ng gặp phải t&igrave;nh trạng nh&ograve;e m&agrave;u sắc được thể hiện trọn vẹn hơn bao giờ hết.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/225380/iphone-12-mini-055321-085321.jpg\" onclick=\"return false;\"><img alt=\"iPhone 12 Mini 64GB | Màn hình OLED Super Retina XDR tràn viền độ phân giải 1080 x 2340 Pixels\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-055321-085321.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-055321-085321.jpg\" title=\"iPhone 12 Mini 64GB | Màn hình OLED Super Retina XDR tràn viền độ phân giải 1080 x 2340 Pixels\" /></a></p>\r\n\r\n<p>Hơn nữa, Apple c&ograve;n trang bị mặt k&iacute;nh Ceramic Shield vật liệu kết hợp giữa thủy tinh v&agrave; gốm cao cấp với khả năng chịu va đập gấp 4 lần so với c&aacute;c đời trước, bảo vệ m&aacute;y một c&aacute;ch tối đa, c&ugrave;ng với đ&oacute; l&agrave; khả năng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-chong-nuoc-bui\" target=\"_blank\" title=\"Tham khảo điện thoại chống nước, chống bụi tại Thegioididong.com\">kh&aacute;ng nước</a>&nbsp;chuẩn IP68.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/225380/iphone-12-mini-064220-074255.jpg\" onclick=\"return false;\"><img alt=\"iPhone 12 Mini 64GB | Hỗ trợ khả năng kháng nước chuẩn IP68\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-064220-074255.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-064220-074255.jpg\" title=\"iPhone 12 Mini 64GB | Hỗ trợ khả năng kháng nước chuẩn IP68\" /></a></p>\r\n\r\n<h3>Camera k&eacute;p th&aacute;ch thức mọi giới hạn ban đ&ecirc;m</h3>\r\n\r\n<p>Camera của điện thoại iPhone 12 Mini với camera k&eacute;p 12 MP nhờ đ&oacute; h&igrave;nh ảnh sẽ được ghi lại một c&aacute;ch ch&acirc;n thực, r&otilde; n&eacute;t. Camera ch&iacute;nh 12 MP với khẩu độ lớn f/1.6 gi&uacute;p tăng 27% khả năng thu s&aacute;ng. V&igrave; vậy, ngay cả trong b&oacute;ng tối, h&igrave;nh chụp, video của bạn vẫn cho độ chi tiết v&agrave; m&agrave;u sắc tuyệt vời.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/225380/iphone-12-mini-055221-085243.jpg\" onclick=\"return false;\"><img alt=\"iPhone 12 Mini 64GB | Cụm camera sau\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-055221-085243.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-055221-085243.jpg\" title=\"iPhone 12 Mini 64GB | Cụm camera sau\" /></a></p>\r\n\r\n<p>Đồng thời, bạn c&ograve;n c&oacute; thể quay video chất lượng 4K HDR c&ugrave;ng c&ocirc;ng nghệ Dolby Vision tr&ecirc;n iPhone 12 Mini với chất lượng, m&agrave;u sắc v&agrave; chi tiết được thể hiện trọn vẹn hơn bao giờ hết.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/225380/iphone-12-mini-064220-074230.jpg\" onclick=\"return false;\"><img alt=\"iPhone 12 Mini 64GB | Quay video chất lượng 4K HDR cùng công nghệ Dolby Vision\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-064220-074230.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-064220-074230.jpg\" title=\"iPhone 12 Mini 64GB | Quay video chất lượng 4K HDR cùng công nghệ Dolby Vision\" /></a></p>\r\n\r\n<p>Ở ph&acirc;̀n notch mặt trước của điện thoại iPhone 12 còn có camera selfie đ&ocirc;̣ ph&acirc;n giải 12 MP. Tương tự như&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-11\" title=\"Tham khảo giá điện thoại iPhone 11 chính hãng\">iPhone 11</a>, camera selfie của&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone iPhone chính hãng\">iPhone</a>&nbsp;có th&ecirc;m tính năng gyro-EIS và cảm bi&ecirc;́n đo chi&ecirc;̀u s&acirc;u sinh trắc học SL 3D hi&ecirc;̣n đại, mang đ&ecirc;́n ch&acirc;́t lượng hình ảnh rõ nét v&agrave; hoàn mỹ.</p>\r\n\r\n<h3>Vi xử l&yacute; Apple A14 khẳng định sức mạnh dẫn đầu</h3>\r\n\r\n<p>iPhone 12 Mini cũng tương tự c&aacute;c phi&ecirc;n bản iPhone 12 kh&aacute;c khi m&aacute;y được trang bị con chip Apple A14 cho khả năng xử l&yacute; nhanh ch&oacute;ng mượt m&agrave;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/225380/iphone-12-mini-064220-074243.jpg\" onclick=\"return false;\"><img alt=\"iPhone 12 Mini 64GB | Chíp xử lí A14 mượt mà\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-064220-074243.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-064220-074243.jpg\" title=\"iPhone 12 Mini 64GB | Chíp xử lí A14 mượt mà\" /></a></p>\r\n\r\n<p>A14 Bionic l&agrave; chip xử l&yacute; được sản xuất tr&ecirc;n quy tr&igrave;nh 5 nm, cung cấp tốc độ tải nhanh hơn, khả năng học hỏi AI th&ocirc;ng minh hơn sẵn s&agrave;ng phục vụ người d&ugrave;ng trong nhiều năm tới.</p>\r\n\r\n<p>iPhone 12 Mini được trang bị dung lượng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-4gb\" target=\"_blank\" title=\"Tham khảo điện thoại có RAM 4 GB tại Thegioididong.com\">RAM 4 GB</a>&nbsp;v&agrave;<a href=\"https://www.thegioididong.com/dtdd-rom-64gb\" target=\"_blank\" title=\"Tham khảo điện thoại có bộ nhớ 64 GB tại Thegioididong.com\">&nbsp;bộ nhớ trong 64 GB</a>&nbsp;đủ khả năng để người d&ugrave;ng lưu trữ thả ga v&agrave; tốc độ giải quyết thao t&aacute;c nhanh ch&oacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/225380/iphone-12-mini-055321-085311.jpg\" onclick=\"return false;\"><img alt=\"iPhone 12 Mini 64GB | Khay sim\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-055321-085311.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-055321-085311.jpg\" title=\"iPhone 12 Mini 64GB | Khay sim\" /></a></p>\r\n\r\n<p>Đặc biệt, iPhone 12 Mini ch&iacute;nh l&agrave; thế hệ iPhone đầu ti&ecirc;n trang bị 5G. Giờ đ&acirc;y, những bộ phim chất lượng cao hay c&aacute;c ứng dụng nặng bạn y&ecirc;u th&iacute;ch sẽ được tải xong trong chớp mắt.</p>\r\n\r\n<p>Việc gửi c&aacute;c tệp lớn, đăng tải h&igrave;nh ảnh, livestream hay ph&aacute;t trực tiếp video chất lượng cao được ho&agrave;n th&agrave;nh nhanh v&agrave; tiện lợi hơn bao giờ hết.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/225380/iphone-12-mini-064320-074325.jpg\" onclick=\"return false;\"><img alt=\"iPhone 12 Mini 64GB | Thế hệ iPhone đầu tiên trang bị 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-064320-074325.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-064320-074325.jpg\" title=\"iPhone 12 Mini 64GB | Thế hệ iPhone đầu tiên trang bị 5G\" /></a></p>\r\n\r\n<p>Ngo&agrave;i ra, Apple đ&atilde; ph&aacute;t triển chế độ Smart Data, c&oacute; chức năng chuyển đổi qua lại giữa 4G v&agrave; 5G khi chạy c&aacute;c ứng dụng nhằm tiết kiệm pin cho m&aacute;y một c&aacute;ch tối đa, n&acirc;ng cao trải nghiệm sử dụng m&aacute;y cho người d&ugrave;ng.</p>\r\n\r\n<h3>Hỗ trợ sạc nhanh 20 W</h3>\r\n\r\n<p>Chiếc điện thoại iPhone 12 Mini n&agrave;y c&oacute; dung lượng pin tuy kh&ocirc;ng thuộc h&agrave;ng &ldquo;khủng&rdquo; nhưng vẫn cho thời lượng sử dụng l&ecirc;n đến 50 giờ nghe nhạc.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/225380/iphone-12-mini-055221-085253.jpg\" onclick=\"return false;\"><img alt=\"iPhone 12 Mini 64GB | Pin tương đối hời lượng sử dụng lên đến 17 giờ xem video, 50 giờ nghe nhạc\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-055221-085253.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-055221-085253.jpg\" title=\"iPhone 12 Mini 64GB | Pin tương đối hời lượng sử dụng lên đến 17 giờ xem video, 50 giờ nghe nhạc\" /></a></p>\r\n\r\n<p>Ngo&agrave;i ra, iPhone 12 Mini c&ograve;n được trang bị t&iacute;nh năng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone sạc pin nhanh\">sạc pin nhanh</a>&nbsp;20 W, người d&ugrave;ng c&oacute; thể nhanh ch&oacute;ng sạc đầy chiếc điện thoại của m&igrave;nh, để tiếp tục c&ocirc;ng việc m&agrave; kh&ocirc;ng bị gi&aacute;n đoạn qu&aacute; l&acirc;u.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/225380/iphone-12-mini-064220-074247.jpg\" onclick=\"return false;\"><img alt=\"iPhone 12 Mini 64GB | Trang bị tính năng sạc pin nhanh 20 W\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-064220-074247.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/225380/iphone-12-mini-064220-074247.jpg\" title=\"iPhone 12 Mini 64GB | Trang bị tính năng sạc pin nhanh 20 W\" /></a></p>\r\n\r\n<p>Với sạc nhanh 20 W, bạn sẽ c&oacute; ngay 50% pin chỉ trong 30 phút sạc. iPhone 12 Series cũng c&oacute; th&ecirc;m t&iacute;nh năng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-khong-day\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone hỗ trợ sạc pin không dây\">sạc kh&ocirc;ng d&acirc;y</a>&nbsp;Qi v&agrave; MagSafe, cho trải nghiệm sạc kh&ocirc;ng d&acirc;y cực kỳ hữu ích v&agrave; tiện lợi.</p>\r\n\r\n<p>T&oacute;m lại, iPhone 12 phi&ecirc;n bản mini l&agrave; một trong những phi&ecirc;n bản điện thoại si&ecirc;u phẩm của Apple với nhiều đột ph&aacute; về c&ocirc;ng nghệ cũng như hiệu năng hứa hẹn sẽ l&agrave; mẫu điện thoại th&agrave;nh c&ocirc;ng nhất của Apple trong năm 2020.</p>\r\n', 2, 'iphone-12-mini-16.html', '', '', 'https://cdn.tgdd.vn/Products/Images/42/225380/TimerThumb/iphone-12-mini-(12).jpg', '2', 1625986782, NULL, 1, 1),
(17, '#IMMSI17', 'MSI Katana GF66', 27890000, 29990000, '<p>15.6&quot;, Full HD</p>\r\n\r\n<p>CPU: Intel Core i7 Tiger Lake, 2.30 GHz</p>\r\n\r\n<p>Card: NVIDIA GeForce RTX3050, 4 GB</p>\r\n\r\n<p>Nặng: 2.1 kg, 3-cell Li-ion, 53 Wh</p>\r\n', '<h3><a href=\"https://www.thegioididong.com/laptop/msi-gf66-11uc-i7-224vn\" target=\"_blank\" title=\"Laptop MSI Katana GF66 11UC i7 (224VN)\">Laptop MSI Katana GF66 11UC i7 (224VN)</a>&nbsp;mang vẻ ngo&agrave;i cơ động c&ugrave;ng cấu h&igrave;nh mạnh mẽ, đẩy nhanh qu&aacute; tr&igrave;nh xử l&yacute; mọi t&aacute;c vụ hay sẵn s&agrave;ng đồng h&agrave;nh c&ugrave;ng bạn chiến mọi thể loại game đầy kịch t&iacute;nh.&nbsp;</h3>\r\n\r\n<h3>Ph&aacute;t huy sức mạnh hiệu năng với&nbsp;<a href=\"https://www.thegioididong.com/laptop-cpu-intel-gen-11\" target=\"_blank\" title=\"Một số laptop sở hữu Intel thê hệ 11 được kinh doanh tại thegioididong.com\">chip Intel Gen 11</a></h3>\r\n\r\n<p>Hiệu năng được n&acirc;ng cấp tối đa l&ecirc;n đến&nbsp;<strong>40%</strong>&nbsp;so với c&aacute;c thế hệ tiền nhiệm nhờ bộ vi xử l&yacute;&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=core-i7\" target=\"_blank\" title=\"Một số laptop có bộ vi xử lý Intel Core i7 được kinh doanh tại thegioididong.com\">Intel Core i7</a>&nbsp;<strong>Tiger Lake 11800H</strong>&nbsp;sở hữu&nbsp;<strong>8 nh&acirc;n 16 luồng&nbsp;</strong>c&ugrave;ng xung nhịp tốc độ tối đa đến&nbsp;<strong>4.6 GHz</strong>&nbsp;ph&aacute;t huy hiệu suất tối ưu trong xử l&yacute; c&ocirc;ng việc hay thoải m&aacute;i chơi mọi trận game cực chất sau những giờ l&agrave;m việc căng thẳng.</p>\r\n\r\n<p>Bộ nhớ<a href=\"https://www.thegioididong.com/laptop-8-gb\" target=\"_blank\" title=\"Một số laptop có RAM 8 GB được kinh doanh tại thegioididong.com\">&nbsp;RAM 8 GB</a>&nbsp;loại&nbsp;<strong>DDR4 2 khe</strong>&nbsp;c&oacute; tốc độ Bus RAM<strong>&nbsp;3200 MHz</strong>&nbsp;gi&uacute;p laptop đa nhiệm mượt m&agrave; hơn trong mọi t&aacute;c vụ, hỗ trợ mở nhiều ứng dụng m&agrave; kh&ocirc;ng lo giật lag m&aacute;y. Đặc biệt, MSI Katana được hỗ trợ 1 khe RAM trống để bạn dễ d&agrave;ng n&acirc;ng cấp RAM đến&nbsp;<strong>64 GB&nbsp;</strong>m&agrave; kh&ocirc;ng phải thay thế thanh RAM cũ<strong>&nbsp;</strong>đ&aacute;p ứng th&ecirc;m nhu cầu của bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/242201/msi-gf66-11uc-i7-224vn-3.jpg\" onclick=\"return false;\"><img alt=\"MSI Katana GF66 11UC i7 11800H (224VN) - Cấu hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/242201/msi-gf66-11uc-i7-224vn-3.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/242201/msi-gf66-11uc-i7-224vn-3.jpg\" title=\"MSI Katana GF66 11UC i7 11800H (224VN) - Cấu hình\" /></a></p>\r\n\r\n<p>Khả năng đồ họa vượt bật c&ugrave;ng card rời<strong>&nbsp;</strong><a href=\"https://www.thegioididong.com/laptop-geforce-rtx\" target=\"_blank\" title=\"Xem thêm laptop trang bị GeForce RTX đang bán tại thegioididong.com\">NVIDIA GeForce RTX&nbsp;</a><strong>3050&nbsp;</strong>sở hữu bộ nhớ đệm&nbsp;<strong>4 GB</strong>, đ&aacute;p ứng hầu hết c&aacute;c nhu cầu thiết kế 2D, 3D, chỉnh sửa video,... hay thỏa m&atilde;n người d&ugrave;ng với c&aacute;c tựa game hấp dẫn như Far Cry 5, Li&ecirc;n Minh Huyền Thoại, FIFA, GTA V, Apex Legends... ở mức c&agrave;i đặt cao.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/242201/msi-gf66-11uc-i7-224vn-4.jpg\" onclick=\"return false;\"><img alt=\"MSI Katana GF66 11UC i7 11800H (224VN) - Card đồ họa\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/242201/msi-gf66-11uc-i7-224vn-4.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/242201/msi-gf66-11uc-i7-224vn-4.jpg\" title=\"MSI Katana GF66 11UC i7 11800H (224VN) - Card đồ họa\" /></a></p>\r\n\r\n<p>Tiết kiệm tối đa thời gian mở v&agrave; vận h&agrave;nh m&aacute;y nhờ ổ cứng&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=ssd-512-gb\" target=\"_blank\" title=\"Một số laptop có ổ cứng SSD 512 GB được kinh doanh tại thegioididong.com\">SSD 512 GB</a>&nbsp;<strong>NVME PCle</strong>&nbsp;gi&uacute;p bạn khởi động game một c&aacute;ch nhanh ch&oacute;ng, đồng thời cung cấp kh&ocirc;ng gian đủ lớn để bạn thoải m&aacute;i lưu trữ c&aacute;c tựa game hay tệp đồ họa nặng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/242201/msi-gf66-11uc-i7-224vn-5.jpg\" onclick=\"return false;\"><img alt=\"MSI Katana GF66 11UC i7 11800H (224VN) - SSD\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/242201/msi-gf66-11uc-i7-224vn-5.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/242201/msi-gf66-11uc-i7-224vn-5.jpg\" title=\"MSI Katana GF66 11UC i7 11800H (224VN) - SSD\" /></a></p>\r\n\r\n<h3>Ngoại h&igrave;nh c&aacute; t&iacute;nh đầy nổi bật</h3>\r\n\r\n<p>Phi&ecirc;n bản&nbsp;<a href=\"https://www.thegioididong.com/laptop-msi\" target=\"_blank\" title=\"Một số laptop MSI được kinh doanh tại thegioididong.com\">laptop MSI</a>&nbsp;n&agrave;y được thiết kế&nbsp;<strong>nắp lưng kim loại&nbsp;</strong>bền bỉ với t&ocirc;ng m&agrave;u chủ đạo đen nh&aacute;m c&ugrave;ng c&aacute;c cạnh c&oacute; đường cắt n&eacute;t gọt mạnh mẽ l&agrave;m t&ocirc;n l&ecirc;n vẻ c&aacute; t&iacute;nh cho người sở hữu.</p>\r\n\r\n<p>M&aacute;y sở hữu trọng lượng<strong>&nbsp;2.1 kg&nbsp;</strong>v&agrave; d&agrave;y&nbsp;<strong>24.9 mm</strong>&nbsp;đem đến sự tiện lợi trong qu&aacute; tr&igrave;nh di chuyển, thể hiện t&iacute;nh di động đối với một chiếc laptop gaming.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/242201/msi-gf66-11uc-i7-224vn-2-2.jpg\" onclick=\"return false;\"><img alt=\"MSI Katana GF66 11UC i7 11800H (224VN) - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/242201/msi-gf66-11uc-i7-224vn-2-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/242201/msi-gf66-11uc-i7-224vn-2-2.jpg\" title=\"MSI Katana GF66 11UC i7 11800H (224VN) - Thiết kế\" /></a></p>\r\n\r\n<p>B&agrave;n ph&iacute;m&nbsp;<strong>Fullsize</strong>&nbsp;được trang bị&nbsp;<strong>đ&egrave;n nền&nbsp;sắc đỏ&nbsp;</strong>nổi bật t&iacute;ch hợp th&ecirc;m b&agrave;n ph&iacute;m số gi&uacute;p bạn dễ d&agrave;ng nhập dữ liệu ch&iacute;nh x&aacute;c hơn ở mọi điều kiện &aacute;nh s&aacute;ng. Đồng thời, b&agrave;n di chuột được thiết kế lệch sang b&ecirc;n tr&aacute;i tạo khoảng kh&ocirc;ng nghỉ tay rộng hơn, hạn chế tối đa t&igrave;nh trạng nhầm lẫn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/242201/msi-gf66-11uc-i7-224vn-6.jpg\" onclick=\"return false;\"><img alt=\"MSI Katana GF66 11UC i7 11800H (224VN) - Bàn phím\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/242201/msi-gf66-11uc-i7-224vn-6.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/242201/msi-gf66-11uc-i7-224vn-6.jpg\" title=\"MSI Katana GF66 11UC i7 11800H (224VN) - Bàn phím\" /></a></p>\r\n\r\n<p>Dọc th&acirc;n<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Một số laptop được kinh doanh tại thegioididong.com\">&nbsp;laptop&nbsp;</a>MSI GF66 l&agrave; h&agrave;ng loạt c&aacute;c cổng kết nối phổ biến như: 2 cổng USB 3.2, HDMI, LAN (RJ45), USB 2.0 v&agrave; USB C gi&uacute;p bạn li&ecirc;n kết nhanh ch&oacute;ng với c&aacute;c thiết bị ngoại vi kh&aacute;c m&agrave; kh&ocirc;ng phải chuyển đổi rườm r&agrave;, mất thời gian.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/242201/msi-gf66-11uc-i7-224vn-11.jpg\" onclick=\"return false;\"><img alt=\"MSI Katana GF66 11UC i7 11800H (224VN) - Cổng kết nối\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/242201/msi-gf66-11uc-i7-224vn-11.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/242201/msi-gf66-11uc-i7-224vn-11.jpg\" title=\"MSI Katana GF66 11UC i7 11800H (224VN) - Cổng kết nối\" /></a></p>\r\n\r\n<p>Nhằm đảm bảo chất lượng đường truyền ổn định trong suốt qu&aacute; tr&igrave;nh chơi game hay l&agrave;m việc, MSI gaming c&ograve;n được hỗ trợ kết nối chuẩn kh&ocirc;ng d&acirc;y<strong>&nbsp;Wi-Fi 6 AX201</strong>&nbsp;v&agrave;<strong>&nbsp;Bluetooth 5.1</strong>&nbsp;gi&uacute;p người d&ugrave;ng truyền xuất dữ liệu dễ d&agrave;ng kh&ocirc;ng lo gi&aacute;n đoạn.</p>\r\n\r\n<p>Hiệu suất hoạt động của chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-msi-gaming\" target=\"_blank\" title=\"Một số laptop MSI gaing được kinh doanh tại thegioididong.com\">MSI gaming</a>&nbsp;n&agrave;y được tối ưu hơn nhờ hệ thống tản nhiệt với&nbsp;<strong>c&ocirc;ng nghệ Cooler Boost&nbsp;</strong>c&oacute;&nbsp;<strong>2 quạt</strong>&nbsp;v&agrave;&nbsp;<strong>6 ống&nbsp;</strong>dẫn nhiệt cho m&aacute;y hoạt động ổn định, kh&ocirc;ng lo qu&aacute; tải nhiệt độ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/242201/msi-gf66-11uc-i7-224vn-7.jpg\" onclick=\"return false;\"><img alt=\"MSI Katana GF66 11UC i7 11800H (224VN) - Tản nhiệt\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/242201/msi-gf66-11uc-i7-224vn-7.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/242201/msi-gf66-11uc-i7-224vn-7.jpg\" title=\"MSI Katana GF66 11UC i7 11800H (224VN) - Tản nhiệt\" /></a></p>\r\n\r\n<h3>H&igrave;nh ảnh sắc n&eacute;t c&ugrave;ng &acirc;m thanh chuy&ecirc;n nghiệp</h3>\r\n\r\n<p>M&agrave;n h&igrave;nh&nbsp;<a href=\"https://www.thegioididong.com/laptop-15-6-inch\" target=\"_blank\" title=\"Một số laptop có màn hình 15.6 inch được kinh doanh tại thegioididong.com\">15.6 inch&nbsp;</a>c&ugrave;ng độ ph&acirc;n giải&nbsp;<a href=\"https://www.thegioididong.com/laptop-full-hd\" target=\"_blank\" title=\"Một số màn hình có độ phân giải Full HD được kinh doanh tại thegioididong.com\">Full HD</a>&nbsp;<strong>(1920 x 1080)</strong>&nbsp;c&oacute; tần số qu&eacute;t&nbsp;<strong>144 Hz</strong>&nbsp;cho khung ảnh hiển thị r&otilde; n&eacute;t, m&ocirc; phỏng lại từng chuyển động mượt m&agrave;, hạn chế tối đa t&igrave;nh trạng giật x&eacute; h&igrave;nh cho bạn những trải nghiệm tr&ograve; chơi đầy th&uacute; vị v&agrave; hấp dẫn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-ips-lcd-la-gi-905752\" target=\"_blank\" title=\"Tìm hiểu thêm về tấm nền IPS\">Tấm nền IPS</a>&nbsp;cung cấp g&oacute;c nh&igrave;n rộng l&ecirc;n đến<strong>&nbsp;178 độ</strong>&nbsp;c&ugrave;ng khả năng hiển thị tốt dưới mọi điều kiện &aacute;nh s&aacute;ng nhờ&nbsp;<strong>c&ocirc;ng nghệ LED&nbsp;</strong>đem đến những giờ ph&uacute;t giải tr&iacute; thư gi&atilde;n trọn vẹn hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/242201/msi-gf66-11uc-i7-224vn-8.jpg\" onclick=\"return false;\"><img alt=\"MSI Katana GF66 11UC i7 11800H (224VN) - Hình ảnh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/242201/msi-gf66-11uc-i7-224vn-8.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/242201/msi-gf66-11uc-i7-224vn-8.jpg\" title=\"MSI Katana GF66 11UC i7 11800H (224VN) - Hình ảnh\" /></a></p>\r\n\r\n<p>N&acirc;ng tầm chất lượng &acirc;m thanh c&ugrave;ng c&ocirc;ng nghệ&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-nahimic-audio-964800\" target=\"_blank\" title=\"Tìm hiểu thêm về Nahimic Audio\">Nahimic Audio</a>&nbsp;với &acirc;m thanh v&ograve;m đ&uacute;ng chuẩn cho người d&ugrave;ng cảm gi&aacute;c ch&acirc;n thật như đang trong ch&iacute;nh tr&ograve; chơi.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/242201/msi-gf66-11uc-i7-224vn-10.jpg\" onclick=\"return false;\"><img alt=\"MSI Katana GF66 11UC i7 11800H (224VN) - Âm thanh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/242201/msi-gf66-11uc-i7-224vn-10.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/242201/msi-gf66-11uc-i7-224vn-10.jpg\" title=\"MSI Katana GF66 11UC i7 11800H (224VN) - Âm thanh\" /></a></p>\r\n\r\n<p>MSI Katana GF66 11UC i7 (224VN) l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=laptop-gaming\" target=\"_blank\" title=\"Một số laptop gaming được kinh doanh tại thegioididong.com\">laptop gaming</a>&nbsp;đ&aacute;ng sở hữu với hiệu năng mạnh mẽ đ&aacute;p ứng mọi nhu cầu cả về game hay đồ họa phức tạp c&ugrave;ng thiết kế đầy nổi bật v&agrave; c&aacute; t&iacute;nh, ph&ugrave; hợp cho hầu hết tất cả mọi người.</p>\r\n', 2, 'mssi-katana-gf66-11uc-i7-11800h-224vn-17.html', '', '', 'https://cdn.tgdd.vn/Products/Images/44/235404/acer-nitro-an515-44-r9jm-r5-nhq9msv003-15-600x600.jpg', '2', 1625987295, NULL, 2, 8),
(18, '#IMAC18', 'Acer Aspire 3 A315', 8490000, NULL, '<p>15.6&quot;, Full HD</p>\r\n\r\n<p>CPU: Intel Pentium, 1.10 GHz</p>\r\n\r\n<p>Card: Intel UHD Graphics 605</p>\r\n\r\n<p>Nặng: 1.7 kg, 2-cell Li-ion, 37 Wh</p>\r\n', '<h3><a href=\"https://www.thegioididong.com/laptop/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n\" target=\"_blank\" title=\"Laptop Acer Aspire 3 A315 34 P8VA (NX.HE3SV.00N)\">Acer Aspire 3 A315 34 P8VA (NX.HE3SV.00N)</a>&nbsp;được thiết kế ph&ugrave; hợp cho nhu cầu học tập - văn ph&ograve;ng với cấu h&igrave;nh ổn định cho bạn những trải nghiệm mượt m&agrave; trong xử l&yacute; c&aacute;c t&aacute;c vụ cơ bản lướt web hay giải tr&iacute;.</h3>\r\n\r\n<h3>Thiết kế h&agrave;i h&ograve;a, hợp thời trang</h3>\r\n\r\n<p>Phi&ecirc;n bản&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Một số laptop tại thegioididong.com\">laptop</a>&nbsp;n&agrave;y g&acirc;y ấn tượng với gam m&agrave;u đen mạnh mẽ, lớp vỏ nhựa &iacute;t trầy xước, hạn chế b&aacute;m v&acirc;n tay c&ugrave;ng trọng lượng&nbsp;<strong>1.7 kg</strong>&nbsp;v&agrave; d&agrave;y&nbsp;<strong>19.9 mm&nbsp;</strong>được tối ưu h&oacute;a cho bạn linh hoạt trong việc di chuyển, mang theo b&ecirc;n m&igrave;nh phục vụ cho nhu cầu c&ocirc;ng việc hay giải tr&iacute;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/239565/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n-2-1.jpg\" onclick=\"return false;\"><img alt=\"Acer Aspire 3 A315 34 P8VA N5030 (NX.HE3SV.00N) - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/239565/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n-2-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/239565/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n-2-1.jpg\" title=\"Acer Aspire 3 A315 34 P8VA N5030 (NX.HE3SV.00N) - Thiết kế\" /></a></p>\r\n\r\n<p>Đặc biệt,&nbsp;<a href=\"https://www.thegioididong.com/laptop-acer-aspire\" target=\"_blank\" title=\"Một số laptop Acer Aspire tại thegioididong.com\">Acer Aspire 3</a>&nbsp;c&ograve;n c&oacute; bản lề mở&nbsp;<strong>180 độ</strong>&nbsp;bền bỉ gi&uacute;p bạn dễ d&agrave;ng chia sẻ th&ocirc;ng tin với bạn b&egrave; hay tương t&aacute;c m&agrave;n h&igrave;nh ở nhiều g&oacute;c độ, hạn chế hư hỏng khi va đập m&aacute;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/239565/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n-6-1.jpg\" onclick=\"return false;\"><img alt=\"Acer Aspire 3 A315 34 P8VA N5030 (NX.HE3SV.00N) - Bản lề 180 độ\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/239565/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n-6-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/239565/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n-6-1.jpg\" title=\"Acer Aspire 3 A315 34 P8VA N5030 (NX.HE3SV.00N) - Bản lề 180 độ\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop-acer\" target=\"_blank\" title=\"Một số laptop Acer tại thegioididong.com\">Laptop Acer</a>&nbsp;n&agrave;y c&ograve;n được trang bị 2 cổng USB 2.0, cổng USB 3.1, HDMI v&agrave; LAN (RJ45) đem đến khả năng truyền xuất dữ liệu một c&aacute;ch nhanh ch&oacute;ng với c&aacute;c thiết bị tương th&iacute;ch kh&aacute;c.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/239565/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n-10-1.jpg\" onclick=\"return false;\"><img alt=\"Acer Aspire 3 A315 34 P8VA N5030 (NX.HE3SV.00N) - Cổng kết nối\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/239565/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n-10-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/239565/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n-10-1.jpg\" title=\"Acer Aspire 3 A315 34 P8VA N5030 (NX.HE3SV.00N) - Cổng kết nối\" /></a></p>\r\n\r\n<p><strong>Wi-Fi 802.11 a/b/g/n/ac</strong>&nbsp;c&ugrave;ng&nbsp;<strong>Bluetooth 5.0&nbsp;</strong>với tốc độ truyền tải ổn định&nbsp;<strong>50 Mbps,</strong>&nbsp;cho ph&eacute;p bạn kết nối với nhiều thiết bị hay internet mượt m&agrave; m&agrave; kh&ocirc;ng lo bị gi&aacute;n đoạn.</p>\r\n\r\n<h3>Hiệu năng đ&aacute;p ứng tốt c&aacute;c t&aacute;c vụ văn ph&ograve;ng</h3>\r\n\r\n<p>Laptop sở hữu chip&nbsp;<a href=\"https://www.thegioididong.com/laptop-celeron-pentium\" target=\"_blank\" title=\"Một số laptop có chip Intel Pentium tại thegioididong.com\">Intel Pentium</a>&nbsp;c&oacute; tốc độ xung nhịp cơ bản&nbsp;<strong>1.10 GHz</strong>&nbsp;đạt tối đa&nbsp;<strong>3.1 GHz&nbsp;</strong>t&iacute;ch hợp card đồ họa&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-card-do-hoa-intel-uhd-graphics-605-1269356\" target=\"_blank\" title=\"Thông tin thêm về card đồ họa Intel UHD Graphics 605\">Intel UHD Graphics 605</a>&nbsp;với mức ti&ecirc;u thụ điện năng thấp mang lại hiệu năng đa nhiệm mượt m&agrave;, xử l&yacute; tốt c&aacute;c t&aacute;c vụ h&agrave;ng ng&agrave;y cơ bản.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop?g=4-gb\" target=\"_blank\" title=\"Một số laptop có RAM 4 GB tại thegioididong.com\">RAM 4 GB</a>&nbsp;với tốc độ Bus RAM<strong>&nbsp;2666 MHz</strong>&nbsp;đ&aacute;p ứng tốt c&aacute;c nhu cầu về Word, Excel, Power Point,... hay lướt web, nghe nhạc, xem phim kh&aacute; thoải m&aacute;i. M&aacute;y c&ograve;n được hỗ trợ n&acirc;ng cấp RAM tối đa đến&nbsp;<strong>8 GB&nbsp;</strong>cho bạn những trải nghiệm tốt hơn nữa.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/239565/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n-4-1.jpg\" onclick=\"return false;\"><img alt=\"Acer Aspire 3 A315 34 P8VA N5030 (NX.HE3SV.00N) - Cấu hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/239565/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n-4-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/239565/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n-4-1.jpg\" title=\"Acer Aspire 3 A315 34 P8VA N5030 (NX.HE3SV.00N) - Cấu hình\" /></a></p>\r\n\r\n<p>Ổ cứng&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=ssd-256-gb\" target=\"_blank\" title=\"Một số laptop có ổ cứng SSD 256 GB tại thegioididong.com\">SSD 256 GB</a>&nbsp;<strong>NVMe PCle</strong>&nbsp;cho kh&ocirc;ng gian vừa đủ để lưu trữ c&aacute;c tệp tin, file ảnh, khởi động m&aacute;y, vận h&agrave;nh ứng dụng nhanh ch&oacute;ng v&agrave; hỗ trợ khe cắm&nbsp;<strong>HD SATA</strong>&nbsp;gi&uacute;p người d&ugrave;ng dễ d&agrave;ng n&acirc;ng cấp m&aacute;y khi cần.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/239565/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n-5-1.jpg\" onclick=\"return false;\"><img alt=\"Acer Aspire 3 A315 34 P8VA N5030 (NX.HE3SV.00N) - SSD\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/239565/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n-5-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/239565/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n-5-1.jpg\" title=\"Acer Aspire 3 A315 34 P8VA N5030 (NX.HE3SV.00N) - SSD\" /></a></p>\r\n\r\n<h3>H&igrave;nh ảnh sống động, &acirc;m thanh ch&acirc;n thật</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop-tren-15-inch\" target=\"_blank\" title=\"Một số laptop có màn hình khoảng 15 inch tại thegioididong.com\">M&agrave;n h&igrave;nh 15.6 inch</a>&nbsp;với độ ph&acirc;n giải&nbsp;<strong>Full HD (1920 x 1080)</strong>&nbsp;cho h&igrave;nh ảnh hiển thị r&otilde; n&eacute;t, tương mới, mang lại trải nghiệm ho&agrave;n hảo qua từng khung h&igrave;nh phim ảnh, video &acirc;m nhạc sống động.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;,&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-man-hinh-acer-comfyview-co-gi-dac-biet-1192380\" target=\"_blank\" title=\"Thông tin thêm về công nghệ Acer ComfyView\">c&ocirc;ng nghệ Acer ComfyView</a>&nbsp;đem đến h&igrave;nh ảnh c&oacute; độ tương phản cao, m&agrave;u sắc ch&iacute;nh x&aacute;c ngay cả trong điều kiện &aacute;nh s&aacute;ng cao v&agrave; hạn chế &aacute;nh s&aacute;ng xanh, giảm mỏi mắt, ch&oacute;i mắt.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/239565/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n-8-1.jpg\" onclick=\"return false;\"><img alt=\"Acer Aspire 3 A315 34 P8VA N5030 (NX.HE3SV.00N) - Màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/239565/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n-8-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/239565/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n-8-1.jpg\" title=\"Acer Aspire 3 A315 34 P8VA N5030 (NX.HE3SV.00N) - Màn hình\" /></a></p>\r\n\r\n<p>Webcam của chiếc laptop Acer Aspire 3 A315 hỗ trợ người d&ugrave;ng c&oacute; thể thực hiện c&aacute;c cuộc gọi video chất lượng tốt, hiển thị h&igrave;nh ảnh ghi được với độ ph&acirc;n giải<strong>&nbsp;640 x 480px.</strong></p>\r\n\r\n<p>M&aacute;y c&ograve;n được trang bị c&ocirc;ng nghệ &acirc;m thanh&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-am-thanh-stereo-am-thanh-kep-dual-spea-920547\" target=\"_blank\" title=\"Tìm hiểu thêm về công nghệ âm thanh Stereo speakers\">Stereo speakers</a>&nbsp;gi&uacute;p người d&ugrave;ng cảm gi&aacute;c được kh&ocirc;ng gian, vị tr&iacute; của từng &acirc;m sắc, cho bạn ch&igrave;m đắm trong những giờ ph&uacute;t giải tr&iacute; thoải m&aacute;i nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/239565/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n-9-1.jpg\" onclick=\"return false;\"><img alt=\"Acer Aspire 3 A315 34 P8VA N5030 (NX.HE3SV.00N) - Âm thanh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/239565/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n-9-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/239565/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n-9-1.jpg\" title=\"Acer Aspire 3 A315 34 P8VA N5030 (NX.HE3SV.00N) - Âm thanh\" /></a></p>\r\n\r\n<p>Laptop Acer Aspire 3 A315 34 P8VA&nbsp;(NX.HE3SV.00N) với hiệu năng ổn định trong c&aacute;c t&aacute;c vụ cơ bản hằng ng&agrave;y, đ&aacute;p ứng tốt như cầu&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" target=\"_blank\" title=\"Một số laptop học tập - văn phòng tại thegioididong.com\">học tập - văn ph&ograve;ng</a>, ph&ugrave; hợp cho cả học sinh, sinh vi&ecirc;n hay nh&acirc;n vi&ecirc;n văn ph&ograve;ng.</p>\r\n', 2, 'acer-aspire-3-a315-34-p8va-n5030-nx-he3sv-00n-18.html', '', '', 'https://cdn.tgdd.vn/Products/Images/44/239565/acer-aspire-3-a315-34-p8va-n5030-nxhe3sv00n-600x600.jpg', '2', 1625987676, NULL, 2, 7);
INSERT INTO `tbl_product` (`product_id`, `product_code`, `product_name`, `product_price`, `old_price`, `product_desc`, `content`, `qty`, `slug`, `creator`, `editor`, `product_thumb`, `product_status`, `add_date`, `update_date`, `cat_id`, `child_id`) VALUES
(19, '#IMXM19', 'Xiaomi Redmi Note 8', 4490000, NULL, '<p>M&agrave;n h&igrave;nh 6.3&quot;, Chip Snapdragon 665</p>\r\n\r\n<p>RAM 4 GB, ROM 64 GB</p>\r\n\r\n<p>Camera sau: Ch&iacute;nh 48 MP &amp; Phụ 8 MP, 2 MP, 2 MP</p>\r\n\r\n<p>Camera trước: 13 MP</p>\r\n\r\n<p>Pin 4000 mAh, Sạc 18 W</p>\r\n', '<h3><a href=\"https://www.thegioididong.com/dtdd/xiaomi-redmi-note-8\" target=\"_blank\" title=\"Tham khảo điện thoại Xiaomi Redmi Note 8 chính hãng\">Redmi Note 8 4GB/64GB</a>&nbsp;l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone chính hãng\">smartphone</a>&nbsp;tầm trung đến từ Xiaomi, chiếc m&aacute;y n&agrave;y g&acirc;y ấn tượng với cấu h&igrave;nh phần cứng mạnh mẽ, hệ thống 4 camera sau chất lượng v&agrave; đi k&egrave;m gi&aacute; b&aacute;n cực kỳ hấp dẫn.</h3>\r\n\r\n<h3>4 camera chất lượng h&agrave;ng đầu</h3>\r\n\r\n<p>Kh&ocirc;ng chỉ c&oacute; 2 hay 3 camera m&agrave; với chiếc smartphone mới&nbsp;Xiaomi Redmi Note 8 th&igrave; người d&ugrave;ng sẽ c&oacute; tới 4 camera để thỏa m&atilde;n nhu cầu chụp ảnh của m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/209535/xiaomi-redmi-note-8-64gb-8.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Xiaomi Redmi Note 8 | Thiết kế mới với bộ 4 camera\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/209535/xiaomi-redmi-note-8-64gb-8.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/209535/xiaomi-redmi-note-8-64gb-8.jpg\" title=\"Điện thoại Xiaomi Redmi Note 8 | Thiết kế mới với bộ 4 camera\" /></a></p>\r\n\r\n<p>Xiaomi Redmi Note 8 sở hữu cho m&igrave;nh camera ch&iacute;nh với độ ph&acirc;n giải khủng &quot;khủng&quot; với &quot;số chấm&quot; l&ecirc;n tới 48 MP.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/209535/xiaomi-redmi-note-8-64gb-9.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Xiaomi Redmi Note 8 | Thiết kế bộ 4 camera\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/209535/xiaomi-redmi-note-8-64gb-9.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/209535/xiaomi-redmi-note-8-64gb-9.jpg\" title=\"Điện thoại Xiaomi Redmi Note 8 | Thiết kế bộ 4 camera\" /></a></p>\r\n\r\n<p>Đi k&egrave;m l&agrave; một camera g&oacute;c rộng 8 MP, một cảm biến 2 MP hỗ trợ đo độ s&acirc;u trường ảnh phục vụ cho việc x&oacute;a ph&ocirc;ng v&agrave; cuối c&ugrave;ng l&agrave; một cảm biến 2 MP cho chụp ảnh macro.</p>\r\n\r\n<p>Chiếc m&aacute;y n&agrave;y sử dụng c&ocirc;ng nghệ pixel-binning để kết hợp dữ liệu &aacute;nh s&aacute;ng từ 4 pixels liền kề nhằm tạo ra h&igrave;nh ảnh c&oacute; độ chi tiết cao.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/209535/xiaomi-redmi-note-8-64gb-19.jpg\" />Ảnh chụp chế độ ban đ&ecirc;m tr&ecirc;n điện thoại Xiaomi Redme Note 8</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/209535/xiaomi-redmi-note-8-64gb-18.jpg\" />Ảnh chụp chế độ ban ng&agrave;y tr&ecirc;n điện thoại Xiaomi Redme Note 8</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/209535/xiaomi-redmi-note-8-64gb-1-1.jpg\" />Ảnh chụp chế g&oacute;c rộng tr&ecirc;n điện thoại Xiaomi Redme Note 8</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;c chế độ chụp tr&ecirc;n thiết bị n&agrave;y bao gồm chế độ ch&acirc;n dung, HDR, chế độ chụp đ&ecirc;m,... đem tới cho người d&ugrave;ng nhiều lựa chọn để s&aacute;ng tạo ra một bức ảnh đẹp.</p>\r\n\r\n<p>Với những trang bị như vậy th&igrave; chiếc smartphone tới từ Xiaomi n&agrave;y tự tin c&oacute; thể đ&aacute;p ứng tốt mọi nhu cầu của người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/209535/xiaomi-redmi-note-8-tgdd-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Xiaomi Redme Note 8 | Ảnh chụp selfie bằng camera trước\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/209535/xiaomi-redmi-note-8-tgdd-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/209535/xiaomi-redmi-note-8-tgdd-1.jpg\" title=\"Điện thoại Xiaomi Redme Note 8 | Ảnh chụp selfie bằng camera trước\" /></a></p>\r\n\r\n<p>Camera selfie cũng kh&ocirc;ng k&eacute;m cạnh với độ ph&acirc;n giải 13 MP thoải m&aacute;i cho bạn selfie vui vẻ với bạn b&egrave; v&agrave; người th&acirc;n.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/mo-hop-danh-gia-nhanh-redmi-note-8-1204513\" target=\"_blank\" title=\"Mở hộp &amp; đánh giá nhanh Redmi Note 8: Smartphone quốc dân là đây!\" type=\"Mở hộp &amp; đánh giá nhanh Redmi Note 8: Smartphone quốc dân là đây!\">Mở hộp &amp; đ&aacute;nh gi&aacute; nhanh Redmi Note 8: Smartphone quốc d&acirc;n l&agrave; đ&acirc;y!</a></p>\r\n\r\n<h3>Hiệu năng vẫn rất ấn tượng</h3>\r\n\r\n<p>Cấu h&igrave;nh vốn l&agrave; thế mạnh của c&aacute;c thiết bị&nbsp;<a href=\"https://www.thegioididong.com/dtdd-xiaomi\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone Xiaomi chính hãng\">Xiaomi</a>&nbsp;n&ecirc;n sẽ kh&ocirc;ng qu&aacute; ngạc nhi&ecirc;n khi Xiaomi Redmi Note 8 được cung cấp th&ocirc;ng số kỹ thuật mạnh mẽ c&ugrave;ng mức gi&aacute; rẻ.</p>\r\n\r\n<p>Thiết bị n&agrave;y sử dụng bộ vi xử l&yacute;&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-chip-qualcomm-snapdragon-665-1177729\" target=\"_blank\" title=\"Tìm hiểu chip Qualcomm Snapdragon 665 8 nhân\">Qualcomm Snapdragon 665 8 nh&acirc;n</a>, với c&aacute;c phi&ecirc;n bản bộ nhớ 4 GB RAM, 64 GB bộ nhớ trong ho&agrave;n to&agrave;n c&oacute; thể gi&uacute;p&nbsp;<a href=\"https://www.thegioididong.com/dtdd-choi-game\" target=\"_blank\" title=\"Tham khảo các mẫu điện thoại chơi game tại Thế Giới Di Động\">điện thoại chơi game</a>&nbsp;tốt ở mức cấu h&igrave;nh trung b&igrave;nh với c&aacute;c d&ograve;ng game phổ biến hiện nay.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/209535/xiaomi-redmi-note-8-64gb-5.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Xiaomi Redme Note 8 | Điểm Antutu Benchmark\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/209535/xiaomi-redmi-note-8-64gb-5.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/209535/xiaomi-redmi-note-8-64gb-5.jpg\" title=\"Điện thoại Xiaomi Redme Note 8 | Điểm Antutu Benchmark\" /></a></p>\r\n\r\n<p>Giao diện Android 9 mới được tuỳ biến đem lại cho Xiaomi Redmi Note 8 nhiều t&iacute;nh năng hỗ trợ: Chia đ&ocirc;i m&agrave;n h&igrave;nh, Ứng dụng k&eacute;p, Kh&ocirc;ng gian thứ hai,...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/209535/xiaomi-redmi-note-8-64gb-14.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Xiaomi Redme Note 8 | Giao diện màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/209535/xiaomi-redmi-note-8-64gb-14.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/209535/xiaomi-redmi-note-8-64gb-14.jpg\" title=\"Điện thoại Xiaomi Redme Note 8 | Giao diện màn hình\" /></a></p>\r\n\r\n<p>M&agrave;n h&igrave;nh 6.3 inch độ ph&acirc;n giải Full HD+ cũng đem lại trải nghiệm thoải m&aacute;i c&ugrave;ng với đ&oacute; l&agrave; thiết kế&nbsp;<a href=\"https://www.thegioididong.com/dtdd-man-hinh-giot-nuoc\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone màn hình giọt nước\">giọt nước</a>&nbsp;mới mẻ.</p>\r\n\r\n<h3>Dung lượng pin lớn, thoải m&aacute;i sử dụng</h3>\r\n\r\n<p>Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd?p=tu-4-7-trieu\" target=\"_blank\" title=\"Tham khảo điện thoại tầm trung chính hãng tại Thế Giới Di Động\">điện thoại tầm trung</a>&nbsp;Xiaomi Redmi Note 8 sở hữu cho m&igrave;nh vi&ecirc;n pin c&oacute; dung lượng l&ecirc;n tới 4000 mAh cho bạn sử dụng kh&aacute; thoải m&aacute;i cả ng&agrave;y d&agrave;i.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/209535/xiaomi-redmi-note-8-64gb-10.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Xiaomi Redme Note 8 | Cổng sạc USB Type C có hỗ trợ sạc nhanh\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/209535/xiaomi-redmi-note-8-64gb-10.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/209535/xiaomi-redmi-note-8-64gb-10.jpg\" title=\"Điện thoại Xiaomi Redme Note 8 | Cổng sạc USB Type C có hỗ trợ sạc nhanh\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; l&agrave; khả năng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone sạc pin nhanh\">sạc nhanh</a>&nbsp;l&ecirc;n tới 18W gi&uacute;p bạn r&uacute;t ngắn được đ&aacute;ng kể thời gian chờ sạc pin cho thiết bị.</p>\r\n\r\n<p>M&aacute;y vẫn sở hữu cho m&igrave;nh&nbsp;<a href=\"https://www.thegioididong.com/dtdd-bao-mat-van-tay\" target=\"_blank\" title=\"Tham khảo điện thoại có bảo mật vân tay tại Thế Giới Di Động\">cảm biến v&acirc;n tay</a>&nbsp;đặt ở mặt lưng với tốc độ nhận diện rất nhanh v&agrave; theo Xiaomi th&igrave; cảm biến truyền thống vẫn nhanh hơn cảm biến v&acirc;n tay trong m&agrave;n h&igrave;nh rất nhiều.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/209535/xiaomi-redmi-note-8-64gb-12.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Xiaomi Redme Note 8 | Cảm biến vân tay ở mặt lưng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/209535/xiaomi-redmi-note-8-64gb-12.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/209535/xiaomi-redmi-note-8-64gb-12.jpg\" title=\"Điện thoại Xiaomi Redme Note 8 | Cảm biến vân tay ở mặt lưng\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; bạn vẫn c&oacute; thể sử dụng t&iacute;nh năng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-bao-mat-khuon-mat\" target=\"_blank\" title=\"Tham khảo điện thoại có bảo mật khuôn mặt tại Thế Giới Di Động\">bảo mật khu&ocirc;n mặt</a>&nbsp;trong những trường hợp v&acirc;n tay kh&ocirc;ng khả dụng.</p>\r\n', 1, 'xiaomi-redmi-note-8-4gb-64gb-19.html', '', '', 'https://cdn.tgdd.vn/Products/Images/42/209535/xiaomi-redmi-note-8-blue-1-600x600.jpg', '2', 1626004843, NULL, 1, 4),
(20, '#IMSS20', 'Samsung Galaxy Note 10+', 16490000, 17990000, '<p>M&agrave;n h&igrave;nh 6.8&quot;, Chip Exynos 9825</p>\r\n\r\n<p>RAM 12 GB, ROM 256 GB</p>\r\n\r\n<p>Camera sau: Ch&iacute;nh 12 MP &amp; Phụ 12 MP, 16 MP, TOF 3D</p>\r\n\r\n<p>Camera trước: 10 MP</p>\r\n\r\n<p>Pin 4300 mAh, Sạc 45 W</p>\r\n', '<h3>Tr&ocirc;ng ngoại h&igrave;nh kh&aacute; giống nhau, tuy nhi&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-note-10-plus\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy Note 10+ chính hãng, giá rẻ\" type=\"Tham khảo điện thoại Samsung Galaxy Note 10+ chính hãng, giá rẻ\">Samsung Galaxy Note 10+</a>&nbsp;vẫn sở hữu kh&aacute; nhiều điểm kh&aacute;c biệt so với&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-note-10\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy Note 10 chính hãng, giá rẻ\" type=\"Tham khảo điện thoại Samsung Galaxy Note 10 chính hãng, giá rẻ\">Galaxy Note 10</a>&nbsp;v&agrave; m&aacute;y đặc biệt d&agrave;nh cho những người th&iacute;ch một chiếc m&aacute;y m&agrave;n h&igrave;nh lớn, camera chất lượng h&agrave;ng đầu.</h3>\r\n\r\n<h3>Camera đứng đầu thế giới</h3>\r\n\r\n<p>DxOMark l&agrave; chuy&ecirc;n trang đ&aacute;nh gi&aacute; camera uy t&iacute;n thế giới mới đ&acirc;y đ&atilde; khẳng định, Galaxy Note 10+ l&agrave; chiếc smartphone c&oacute; camera tốt nhất hiện nay.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/206176/samsung-galaxy-note-10-plus-tgdd-26.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy Note 10+ | Giao diện camera sau\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-26.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-26.jpg\" title=\"Điện thoại Samsung Galaxy Note 10+ | Giao diện camera sau\" /></a></p>\r\n\r\n<p>Galaxy Note 10+ c&oacute; camera ch&iacute;nh 12 MP với khẩu độ c&oacute; thể thay đổi từ F/1.5 &ndash; F/2.4, hỗ trợ&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/che-do-chong-rung-quang-hoc-ois-chup-anh-tren-sm-906416\" target=\"_blank\" title=\"Tìm hiểu về công nghệ chống rung quang học OIS\" type=\"Tìm hiểu về công nghệ chống rung quang học OIS\">chống rung quang học OIS</a>&nbsp;v&agrave; tự động lấy n&eacute;t dual-pixel.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-34.jpg\" />Ảnh chụp g&oacute;c si&ecirc;u rộng 0.5x tr&ecirc;n Note 10</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-33.jpg\" />Ảnh chụp g&oacute;c rộng 1x tr&ecirc;n Note 10</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-35.jpg\" />Ảnh chụp g&oacute;c thường 2x tr&ecirc;n Note 10</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tiếp theo l&agrave; cảm biến camera g&oacute;c si&ecirc;u rộng 16 MP khẩu độ F/2.2 c&ugrave;ng ống k&iacute;nh tele 12 MP khẩu độ F/2.1 v&agrave; n&oacute; cũng c&oacute; th&ecirc;m 1 cảm biến 3D ToF, điều m&agrave; Samsung Galaxy Note 10 kh&ocirc;ng được trang bị.</p>\r\n\r\n<p>Samsung đ&atilde; cải thiện c&aacute;c thuật to&aacute;n xử l&yacute; b&ecirc;n trong phần mềm gi&uacute;p m&aacute;y c&oacute; khả năng phơi s&aacute;ng tốt, nhất qu&aacute;n v&agrave; ch&iacute;nh x&aacute;c cho d&ugrave; trong điều kiện &aacute;nh s&aacute;ng thế n&agrave;o.</p>\r\n\r\n<p>Galaxy Note 10+ cũng hỗ trợ zoom quang 2x, n&oacute; c&oacute; thể chụp h&igrave;nh ảnh với m&agrave;u sắc sống động, độ chi tiết cao v&agrave; độ nhiễu thấp.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/206176/samsung-galaxy-note-10-plus-tgdd-31.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy Note 10+ | Ảnh chụp ban đêm\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-31.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-31.jpg\" title=\"Điện thoại Samsung Galaxy Note 10+ | Ảnh chụp ban đêm\" /></a></p>\r\n\r\n<p>Galaxy Note 10+ cũng c&oacute; một t&iacute;nh năng mới l&agrave; Live Focus Video cho ph&eacute;p &aacute;p dụng hiệu ứng bokeh v&agrave;o c&aacute;c video quay được cũng như h&igrave;nh ảnh, c&ugrave;ng c&aacute;c hiệu ứng kh&aacute;c như thay đổi m&agrave;u ph&ocirc;ng nền.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/206176/samsung-galaxy-note-10-plus-tgdd-32.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy Note 10+ | Ảnh chụp ban đêm\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-32.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-32.jpg\" title=\"Điện thoại Samsung Galaxy Note 10+ | Ảnh chụp ban đêm\" /></a></p>\r\n\r\n<p>Galaxy Note 10+ vẫn c&ograve;n những t&iacute;nh năng mới kh&aacute;c như chế độ AR cho ph&eacute;p vẽ l&ecirc;n c&aacute;c video gọi l&agrave; AR Doodle, t&iacute;nh năng ph&oacute;ng to mic để thu &acirc;m thanh r&otilde; hơn ở từng phần cụ thể của cảnh khi đang quay video.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/206176/samsung-galaxy-note-10-plus-tgdd-30.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy Note 10+ | Ảnh chụp thiếu sáng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-30.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-30.jpg\" title=\"Điện thoại Samsung Galaxy Note 10+ | Ảnh chụp thiếu sáng\" /></a></p>\r\n\r\n<p>Galaxy Note 10+ c&oacute; t&iacute;nh năng Si&ecirc;u chống rung cực ấn tượng, gi&uacute;p người d&ugrave;ng c&oacute; được những thước phim mượt m&agrave;, giảm thiểu tối đa sự giật lag ảnh hưởng tới sự tập trung khi xem.</p>\r\n\r\n<p>Camera selfie 10 MP ở mặt trước với t&iacute;nh năng l&agrave;m đẹp được t&iacute;ch hợp sẵn hứa hẹn sẽ kh&ocirc;ng l&agrave;m người d&ugrave;ng phải thất vọng với chất lượng ảnh mang lại.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/206176/samsung-galaxy-note-10-plus-blue-g2_800x600.jpg\" onclick=\"return false;\"><img alt=\" Galaxy Note 10+ | Camera selfie 10 MP\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-blue-g2_800x600.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-blue-g2_800x600.jpg\" title=\" Galaxy Note 10+ | Camera selfie 10 MP\" /></a></p>\r\n\r\n<h3>C&ocirc;ng nghệ sạc nhanh&nbsp;Superfast Charge 45W</h3>\r\n\r\n<p>Samsung Galaxy Note 10+ ch&iacute;nh l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone chính hãng\">smartphone</a>&nbsp;duy nhất tại thời điểm hiện tại hỗ trợ sạc nhanh l&ecirc;n tới 45W của Samsung.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/206176/samsung-galaxy-note-10-plus-tgdd-23.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy Note 10+ | Khả năng sạc nhanh trên Type-C\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-23.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-23.jpg\" title=\"Điện thoại Samsung Galaxy Note 10+ | Khả năng sạc nhanh trên Type-C\" /></a></p>\r\n\r\n<p>C&ocirc;ng nghệ sạc nhanh Superfast Charge mới n&agrave;y cung cấp nhiều năng lượng hơn so với chuẩn sạc xuất hiện tr&ecirc;n c&aacute;c flagship&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone Samsung chính hãng\">Samsung</a>&nbsp;trước đ&acirc;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/206176/samsung-galaxy-note-10-plus-tgdd-21.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy Note 10+ | Khả năng sạc nhanh\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-21.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-21.jpg\" title=\"Điện thoại Samsung Galaxy Note 10+ | Khả năng sạc nhanh\" /></a></p>\r\n\r\n<p>Nhanh gấp ba lần Adaptive Fast Charge v&agrave; nhanh hơn 6 lần so với sạc qua cổng USB ti&ecirc;u chuẩn.</p>\r\n\r\n<p>C&ocirc;ng nghệ sạc si&ecirc;u nhanh n&agrave;y gi&uacute;p bạn c&oacute; thể sạc đầy vi&ecirc;n pin c&oacute; dung lượng 4300 mAh của m&aacute;y chỉ trong chưa đầy một giờ đồng hồ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/206176/samsung-galaxy-note-10-plus-tgdd-12.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy Note 10+ | Khả năng sạc nhanh\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-12.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-12.jpg\" title=\"Điện thoại Samsung Galaxy Note 10+ | Khả năng sạc nhanh\" /></a></p>\r\n\r\n<p>Khi m&agrave; dung lượng pin đang tụt lại ph&iacute;a sau so với những cải tiến về camera hay cấu h&igrave;nh th&igrave; việc hỗ trợ c&ocirc;ng nghệ&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone sạc pin nhanh\">sạc nhanh</a>&nbsp;đến như vậy cũng sẽ gi&uacute;p người d&ugrave;ng Samsung phần n&agrave;o bớt được thời gian chờ đợi nạp năng lượng cho m&aacute;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/206176/samsung-galaxy-note-10-plus-tgdd-10.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy Note 10+ | Khả năng sạc nhanh\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-10.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-10.jpg\" title=\"Điện thoại Samsung Galaxy Note 10+ | Khả năng sạc nhanh\" /></a></p>\r\n\r\n<p>Bạn cũng cần lưu &yacute; l&agrave; m&aacute;y chỉ đi k&egrave;m với cục sạc 25W v&agrave; để sử dụng c&ocirc;ng nghệ sạc nhanh tối đa n&agrave;y th&igrave; bạn phải mua th&ecirc;m củ sạc 45W b&ecirc;n ngo&agrave;i nhưng d&ugrave; sao hỗ trợ sạc nhanh tới 45W đ&atilde; l&agrave; điểm cộng qu&aacute; lớn so với những chiếc m&aacute;y đầu bảng kh&aacute;c.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/danh-gia-samsung-galaxy-note-10--flagship-cao-cap-voi-dua-than-s-pen-1185800\" target=\"_blank\" title=\"Đánh giá Samsung Galaxy Note 10+\" type=\"Đánh giá Samsung Galaxy Note 10+\">Đ&aacute;nh gi&aacute; Samsung Galaxy Note 10+: Flagship cao cấp với &#39;đũa thần&#39; S-Pen</a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh lớn thoải m&aacute;i sử dụng</h3>\r\n\r\n<p>Samsung Galaxy Note 10+ l&agrave; một chiếc điện thoại rất lớn, rất c&oacute; thể l&agrave; chiếc điện thoại lớn nhất bạn từng sử dụng, với m&agrave;n h&igrave;nh AMOLED Infinity-O 6.8 inch c&oacute; độ ph&acirc;n giải 3.040 x 1.440 pixels (498 ppi).</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/206176/samsung-galaxy-note-10-plus-tgdd-7.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy Note 10+ | Thiết kế màn hình lớn\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-7.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-7.jpg\" title=\"Điện thoại Samsung Galaxy Note 10+ | Thiết kế màn hình lớn\" /></a></p>\r\n\r\n<p>C&ocirc;ng nghệ m&agrave;n h&igrave;nh&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-ma-hinh-dynamic-amoled-co-gi-noi-bat-1151123\" target=\"_blank\" title=\"Tìm hiểu màn hình Dynamic AMOLED\">Dynamic AMOLED</a>&nbsp;ti&ecirc;n tiến n&agrave;y c&ograve;n cho ra m&agrave;n h&igrave;nh &iacute;t &aacute;nh s&aacute;ng xanh hơn nhằm gi&uacute;p mắt thoải m&aacute;i.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/206176/samsung-galaxy-note-10-plus-tgdd-14.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy Note 10+ | Màn hình phủ lớp HDR10+\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-14.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-14.jpg\" title=\"Điện thoại Samsung Galaxy Note 10+ | Màn hình phủ lớp HDR10+\" /></a></p>\r\n\r\n<p>M&agrave;n h&igrave;nh hỗ trợ cả nội dung HDR10+ v&igrave; thế c&aacute;c chương tr&igrave;nh phim ảnh, game v&agrave; c&aacute;c nội dung kh&aacute;c được hiển thị rất đẹp.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/206176/samsung-galaxy-note-10-plus-tgdd-20.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy Note 10+ | Màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-20.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-20.jpg\" title=\"Điện thoại Samsung Galaxy Note 10+ | Màn hình\" /></a></p>\r\n\r\n<p>Với thiết kế những cạnh tr&ograve;n ở mặt sau khiến điện thoại rất thoải m&aacute;i khi cầm, trong trường hợp bạn c&oacute; b&agrave;n tay đủ lớn để cầm v&agrave; giữ n&oacute;.</p>\r\n\r\n<p>Mặt sau của Note 10+ được l&agrave;m bằng k&iacute;nh, c&ograve;n khung th&igrave; được l&agrave;m bằng kim loại, mang đến sự tinh tế v&agrave; sang trọng cho điện thoại.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/206176/samsung-galaxy-note-10-plus-tgdd-24.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy Note 10+ | Màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-24.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-24.jpg\" title=\"Điện thoại Samsung Galaxy Note 10+ | Màn hình\" /></a></p>\r\n\r\n<p>Galaxy Note 10+ c&oacute; hỗ trợ chuẩn chống nước, chống bụi IP68 v&igrave; thế c&oacute; thể sử dụng ở b&atilde;i biển m&agrave; kh&ocirc;ng sợ bắn nước hay c&aacute;c x&acirc;m nhập v&agrave;o c&aacute;c cổng kết nối.</p>\r\n\r\n<h3>B&uacute;t S Pen ng&agrave;y c&agrave;ng nhiều t&iacute;nh năng</h3>\r\n\r\n<p>S Pen ch&iacute;nh l&agrave; một trong những yếu tố cốt l&otilde;i khiến d&ograve;ng Galaxy Note trở n&ecirc;n đặc biệt v&agrave; hấp dẫn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/206176/samsung-galaxy-note-10-plus-tgdd-29.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy Note 10+ |Trải nghiệm Bút S-Pen\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-29.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-29.jpg\" title=\"Điện thoại Samsung Galaxy Note 10+ |Trải nghiệm Bút S-Pen\" /></a></p>\r\n\r\n<p>B&uacute;t S Pen c&oacute; nhiều t&iacute;nh năng hơn b&uacute;t cảm ứng stylus th&ocirc;ng thường, từ ghi ch&uacute; nhanh bằng c&aacute;ch viết tr&ecirc;n m&agrave;n h&igrave;nh kh&oacute;a, đến chụp ảnh từ xa với S Pen.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/206176/samsung-galaxy-note-10-plus-tgdd-28.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy Note 10+ | Khe cắm bút S-Pen\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-28.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-28.jpg\" title=\"Điện thoại Samsung Galaxy Note 10+ | Khe cắm bút S-Pen\" /></a></p>\r\n\r\n<p>Giờ đ&acirc;y b&uacute;t S Pen tr&ecirc;n Note 10+ đ&atilde; được n&acirc;ng cấp, v&agrave; hiện tại đ&atilde; hỗ trợ điều khiển bằng cử chỉ.</p>\r\n\r\n<p>T&iacute;nh năng n&agrave;y cho ph&eacute;p người d&ugrave;ng điều khiển một số ứng dụng của Samsung như camera,&hellip; từ xa m&agrave; kh&ocirc;ng cần chạm v&agrave;o điện thoại.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/206176/samsung-galaxy-note-10-plus-tgdd-17.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy Note 10+ | Bút S-Pen\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-17.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-17.jpg\" title=\"Điện thoại Samsung Galaxy Note 10+ | Bút S-Pen\" /></a></p>\r\n\r\n<p>V&iacute; dụ ứng dụng camera tr&ecirc;n m&aacute;y hỗ trợ kết nối với S Pen, cho ph&eacute;p bạn biến b&uacute;t S Pen th&agrave;nh c&ocirc;ng cụ gi&uacute;p bạn điều chỉnh ống k&iacute;nh camera, thay đổi m&agrave;u sắc, zoom,... giống như đang thao t&aacute;c bằng tay.</p>\r\n\r\n<h3>Nhiều trang bị cao cấp kh&aacute;c nữa</h3>\r\n\r\n<p>Samsung Galaxy Note 10+ vẫn sở hữu cho m&igrave;nh cảm biến v&acirc;n tay si&ecirc;u &acirc;m b&ecirc;n dưới m&agrave;n h&igrave;nh với tốc độ cải tiến r&otilde; rệt.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/206176/samsung-galaxy-note-10-plus-tgdd-27.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy Note 10+ | Cảm biến vân tay\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-27.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-27.jpg\" title=\"Điện thoại Samsung Galaxy Note 10+ | Cảm biến vân tay\" /></a></p>\r\n\r\n<p>Bạn sẽ kh&ocirc;ng phải hi sinh bất cứ bộ phận n&agrave;o tr&ecirc;n thiết kế của m&aacute;y m&agrave; vẫn c&oacute; thể trải nghiệm&nbsp;<a href=\"https://www.thegioididong.com/dtdd-bao-mat-van-tay\" target=\"_blank\" title=\"Tham khảo điện thoại có bảo mật vân tay tại Thế Giới Di Động\">bảo mật v&acirc;n tay</a>&nbsp;ở mặt trước rất thuận tiện v&agrave; dễ d&agrave;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/206176/samsung-galaxy-note-10-plus-tgdd-22.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy Note 10+ | Thời lượng pin\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-22.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-22.jpg\" title=\"Điện thoại Samsung Galaxy Note 10+ | Thời lượng pin\" /></a></p>\r\n\r\n<p>T&iacute;nh năng sạc ngược kh&ocirc;ng d&acirc;y vẫn xuất hiện tr&ecirc;n Galaxy Note 10+ gi&uacute;p bạn c&oacute; thể biến chiếc m&aacute;y th&agrave;nh một cục&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-khong-day\" target=\"_blank\" title=\"Tham khảo điện thoại có sạc không dây tại Thế Giới Di Động\">sạc kh&ocirc;ng d&acirc;y</a>&nbsp;di động bất cứ l&uacute;c n&agrave;o.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/206176/samsung-galaxy-note-10-plus-tgdd-16.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại Samsung Galaxy Note 10+ | Cấu hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-16.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-tgdd-16.jpg\" title=\"Điện thoại Samsung Galaxy Note 10+ | Cấu hình\" /></a></p>\r\n\r\n<p>Samsung c&ograve;n mang đến cho fan game thủ một chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-choi-game\" target=\"_blank\" title=\"Tham khảo các mẫu điện thoại chơi game tại Thế Giới Di Động\">điện thoại chơi game</a>&nbsp;c&oacute; cấu h&igrave;nh cũng thuộc top đầu bảng hiện nay với con chip&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-chip-exynos-9825-1187140\" target=\"_blank\" title=\"Tìm hiểu chip Exynos 9825 8 nhân 64-bit\">Exynos 9825 8 nh&acirc;n 64-bit</a>&nbsp;kết hợp với 12 GB RAM v&agrave; 256 GB bộ nhớ trong.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/206176/samsung-galaxy-note-10-plus-.jpg\" onclick=\"return false;\"><img alt=\"Điểm Antutu trên Samsung Note 10 Plus\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-.jpg\" title=\"Điểm Antutu trên Samsung Note 10 Plus\" /></a></p>\r\n\r\n<p>Dung lượng pin 4300 mAh thoải m&aacute;i cho bạn sử dụng cả ng&agrave;y m&agrave; kh&ocirc;ng cần phải bận t&acirc;m m&aacute;y sẽ hết pin giữa chừng.</p>\r\n', 20, 'samsung-galaxy-note-10-20.html', '', '', 'https://cdn.tgdd.vn/Products/Images/42/206176/samsung-galaxy-note-10-plus-silver-new-600x600.jpg', '2', 1626005015, NULL, 1, 2),
(21, '#IMOP21', 'OPPO Find X3 Pro 5G', 26990000, NULL, '<p>Màn hình 6.7\", Chip Snapdragon 888</p>\r\n\r\n<p>RAM 12 GB, ROM 256 GB</p>\r\n\r\n<p>Camera sau: Chính 50 MP & Phụ 50 MP, 13 MP, 3 MP</p>\r\n\r\n<p>Camera trước: 32 MP</p>\r\n\r\n<p>Pin 4500 mAh, Sạc 65 W</p>\r\n', '<h3><a href=\"https://www.thegioididong.com/dtdd-oppo\" target=\"_blank\" title=\"Tham khảo các sản phẩm điện thoại OPPO tại Thế Giới Di Động\">OPPO</a>&nbsp;đ&atilde; l&agrave;m khuynh đảo thị trường&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các sản phẩm điện thoại tại Thế Giới Di Động\">smartphone</a>&nbsp;khi cho ra đời chiếc điện thoại&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oppo-find-x3-pro\" target=\"_blank\" title=\"Tham khảo các sản phẩm điện thoại OPPO Find X3 Pro tại Thế Giới Di Động\">OPPO Find X3 Pro 5G</a>. Đ&acirc;y l&agrave; một sản phẩm c&oacute; thiết kế độc đ&aacute;o, sở hữu cụm camera khủng, cấu h&igrave;nh thuộc top đầu trong thế giới Android.</h3>\r\n\r\n<h3>Kết quả của sự s&aacute;ng tạo kh&ocirc;ng ngừng</h3>\r\n\r\n<p>Nếu nh&igrave;n qua mặt lưng của OPPO Find X3 Pro 5G th&igrave; bạn sẽ bất ngờ ngay với cụm camera sau, được tạo h&igrave;nh giống như miệng n&uacute;i lửa, thiết kế n&agrave;y đ&atilde; ngốn rất nhiều thời gian v&agrave; c&ocirc;ng sức của nh&agrave; sản xuất để mang đến cho người d&ugrave;ng sự kh&aacute;c biệt mới lạ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/232190/oppo-find-x3-pro-5g-002.jpg\" onclick=\"return false;\"><img alt=\"OPPO Find X3 Pro 5G | Thiết kế cụm camera tạo hình giống như miệng núi lửa độc đáo\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-5g-002.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-5g-002.jpg\" title=\"OPPO Find X3 Pro 5G | Thiết kế cụm camera tạo hình giống như miệng núi lửa độc đáo\" /></a></p>\r\n\r\n<p>OPPO trang bị cho Find X3 Pro 5G cả hai mặt bằng k&iacute;nh cường lực gi&uacute;p mang lại sự bền bỉ v&agrave; sang trọng, khung viền cấu tạo từ hợp kim cao cấp v&agrave; được gia c&ocirc;ng tỉ mỉ l&agrave;m chiếc điện thoại trở n&ecirc;n mỏng, nhẹ v&agrave; đẹp mắt hơn rất nhiều.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/232190/oppo-find-x3-pro-5g-005.jpg\" onclick=\"return false;\"><img alt=\"OPPO Find X3 Pro 5G | Thiết kế cạnh viền\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-5g-005.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-5g-005.jpg\" title=\"OPPO Find X3 Pro 5G | Thiết kế cạnh viền\" /></a></p>\r\n\r\n<p>Thiết kế m&agrave;n h&igrave;nh tr&agrave;n viền cũng l&agrave;m nổi bật mặt trước của Find X3 Pro 5G, chỉ bằng những quan s&aacute;t đầu ti&ecirc;n ta đ&atilde; c&oacute; thể thấy sự đẳng cấp v&agrave; sang trọng của chiếc điện thoại n&agrave;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/232190/oppo-find-x3-pro-5g-008.jpg\" onclick=\"return false;\"><img alt=\"OPPO Find X3 Pro 5G | Thiết kế màn hình đục lỗ\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-5g-008.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-5g-008.jpg\" title=\"OPPO Find X3 Pro 5G | Thiết kế màn hình đục lỗ\" /></a></p>\r\n\r\n<h3>Mạnh mẽ h&agrave;ng đầu với Snapdragon 888</h3>\r\n\r\n<p>OPPO Find X3 Pro 5G mang hiệu năng h&agrave;ng đầu trong thế giới điện thoại Android khi sở hữu con chip Snapdragon 888 8 nh&acirc;n mới nhất của Qualcomm (05/2021) được sản xuất tr&ecirc;n tiến tr&igrave;nh 5 nm mang lại tốc độ xử l&yacute; nhanh hơn, AI th&ocirc;ng minh hơn v&agrave; tiết kiệm năng lượng hơn so với thế hệ trước.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/232190/oppo-find-x3-pro-4.jpg\" onclick=\"return false;\"><img alt=\"OPPO Find X3 Pro 5G | Sở hữu con chip Snapdragon 888 8 nhân mới nhất của Qualcomm\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-4.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-4.jpg\" title=\"OPPO Find X3 Pro 5G | Sở hữu con chip Snapdragon 888 8 nhân mới nhất của Qualcomm\" /></a></p>\r\n\r\n<p>Snapdragon 888 c&oacute; xung nhịp tối đa l&ecirc;n đến 2.84 GHz, đi k&egrave;m&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-12gb\" target=\"_blank\" title=\"Tham khảo điện thoại có RAM 12 GB tại Thegioididong.com\">12 GB RAM</a>&nbsp;cho m&aacute;y hoạt động mượt m&agrave;, đa nhiệm kh&ocirc;ng giật lag để người d&ugrave;ng cảm nhận trọn vẹn từng khung giờ giải tr&iacute; cao cấp.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/232190/oppo-find-x3-pro-5g-007.jpg\" onclick=\"return false;\"><img alt=\"OPPO Find X3 Pro 5G | Khay sim\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-5g-007.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-5g-007.jpg\" title=\"OPPO Find X3 Pro 5G | Khay sim\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, Find X3 Pro 5G cung cấp đến người d&ugrave;ng dung lượng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-rom-256gb\" target=\"_blank\" title=\"Tham khảo điện thoại có bộ nhớ 256 GB tại Thegioididong.com\">bộ nhớ trong 256 GB</a>, mang lại kh&ocirc;ng gian lớn nhằm thỏa m&atilde;n nhu cầu lưu trữ h&igrave;nh ảnh, th&ocirc;ng tin v&agrave; ứng dụng y&ecirc;u th&iacute;ch một c&aacute;ch thả ga.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/232190/oppo-find-x3-pro-5g-010.jpg\" onclick=\"return false;\"><img alt=\"OPPO Find X3 Pro 5G | Kết nối 5G mang đến thế giới giải trí mượt mà\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-5g-010.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-5g-010.jpg\" title=\"OPPO Find X3 Pro 5G | Kết nối 5G mang đến thế giới giải trí mượt mà\" /></a></p>\r\n\r\n<p>Ngo&agrave;i cung cấp một hiệu năng cực kỳ mạnh mẽ, Snapdragon 888 c&ograve;n sử dụng modem 5G t&iacute;ch hợp gi&uacute;p giảm diện t&iacute;ch cho m&aacute;y mỏng v&agrave; nhẹ hơn. Kết nối 5G mang đến thế giới giải tr&iacute; mượt m&agrave;, đa dạng trải nghiệm đỉnh cao cho người d&ugrave;ng. H&atilde;y c&ugrave;ng tận hưởng những gi&acirc;y ph&uacute;t sống động tr&ecirc;n những tựa game đồ họa cao v&agrave; phim chất lượng m&agrave; kh&ocirc;ng phải chờ đợi.</p>\r\n\r\n<h3>C&ocirc;ng nghệ m&agrave;n h&igrave;nh&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-amoled-la-gi-905766\" target=\"_blank\" title=\"Màn hình AMOLED là gì?\">AMOLED</a></h3>\r\n\r\n<p>OPPO Find X3 Pro 5G sử dụng m&agrave;n h&igrave;nh AMOLED với gam m&agrave;u rộng, sự đa dạng sắc m&agrave;u đem đến những h&igrave;nh ảnh sống động, rực rỡ, khả năng hiển thị sắc n&eacute;t.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/232190/oppo-find-x3-pro-5g-001.jpg\" onclick=\"return false;\"><img alt=\"OPPO Find X3 Pro 5G | Sử dụng màn hình AMOLED với gam màu rộng, kích thước khá lớn với 6.7 inch\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-5g-001.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-5g-001.jpg\" title=\"OPPO Find X3 Pro 5G | Sử dụng màn hình AMOLED với gam màu rộng, kích thước khá lớn với 6.7 inch\" /></a></p>\r\n\r\n<p>M&agrave;n h&igrave;nh c&oacute; k&iacute;ch thước kh&aacute; lớn với 6.7 inch, tần số qu&eacute;t cao 120 Hz cho mọi thao t&aacute;c lướt tay của bạn tr&ecirc;n m&aacute;y uyển chuyển v&agrave; nhẹ nh&agrave;ng.</p>\r\n\r\n<h3>M&aacute;y ảnh xuất sắc từ ngo&agrave;i v&agrave;o trong</h3>\r\n\r\n<p>OPPO Find X3 Pro 5G được ưu &aacute;i bởi d&agrave;n camera v&agrave; hiệu năng khủng, trang bị camera hiển vi cho khả năng chụp gần một chi tiết đến bất ngờ, cả những chi tiết mắt thường kh&oacute; nh&igrave;n thấy. Sở hữu camera ch&iacute;nh độ ph&acirc;n giải 50 MP,&nbsp;camera g&oacute;c rộng&nbsp;50 MP, camera tele 13 MP v&agrave; cuối c&ugrave;ng l&agrave; 3 MP cho&nbsp;camera macro.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/232190/oppo-find-x3-pro-5g-003.jpg\" onclick=\"return false;\"><img alt=\"OPPO Find X3 Pro 5G | Sở hữu dàn camera và hiệu năng khủng, cho ra đời những bức ảnh rực rỡ và lung linh nhất\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-5g-003.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-5g-003.jpg\" title=\"OPPO Find X3 Pro 5G | Sở hữu dàn camera và hiệu năng khủng, cho ra đời những bức ảnh rực rỡ và lung linh nhất\" /></a></p>\r\n\r\n<p>Sản phẩm tập trung kh&aacute; nhiều v&agrave;o phần hiệu năng m&aacute;y ảnh, t&iacute;nh năng Al camera được n&acirc;ng cấp cho ph&eacute;p camera của Find X3 Pro 5G tự động x&aacute;c định v&agrave; c&acirc;n chỉnh t&ocirc;ng m&agrave;u ph&ugrave; hợp với t&ugrave;ng kh&ocirc;ng gian ri&ecirc;ng hứa hẹn sẽ cho ra đời những bức ảnh rực rỡ v&agrave; lung linh nhất.</p>\r\n\r\n<p>Chế độ chụp cảnh đ&ecirc;m si&ecirc;u đẹp đ&atilde; c&oacute; mặt tại Find X3 Pro 5G hỗ trợ th&ecirc;m &aacute;nh s&aacute;ng v&agrave; bắt mọi khung cảnh một c&aacute;ch r&otilde; r&agrave;ng sống động bất kể kh&ocirc;ng gian của đ&ecirc;m tối. Camera si&ecirc;u cận đưa người d&ugrave;ng đến kh&aacute;m ph&aacute; một thế giới chi tiết hơn của những cảnh đẹp lộng lẫy đầy đắm ch&igrave;m.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/232190/oppo-find-x3-pro-7.jpg\" onclick=\"return false;\"><img alt=\"OPPO Find X3 Pro 5G | Cụm 4 camera sau với nhiều tính năng chỉnh sửa ảnh tự động\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-7.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-7.jpg\" title=\"OPPO Find X3 Pro 5G | Cụm 4 camera sau với nhiều tính năng chỉnh sửa ảnh tự động\" /></a></p>\r\n\r\n<p>Bật tung s&aacute;ng tạo của bạn bằng c&aacute;c thước phim ổn định mượt m&agrave; với t&iacute;nh năng&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/che-do-chong-rung-quang-hoc-ois-chup-anh-tren-sm-906416\" target=\"_blank\" title=\"Chế độ chống rung quang học (OIS) chụp ảnh trên smartphone\">chống rung OIS</a>, giờ đ&acirc;y người d&ugrave;ng c&oacute; thể thỏa sức quay video chất lượng cao chuy&ecirc;n nghiệp v&agrave; tr&agrave;n đầy th&uacute; vị ngay tr&ecirc;n smartphone.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/232190/oppo-find-x3-pro-5g-1-1.jpg\" onclick=\"return false;\"><img alt=\"OPPO Find X3 Pro 5G | Ảnh chụp từ camera trước\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-5g-1-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-5g-1-1.jpg\" title=\"OPPO Find X3 Pro 5G | Ảnh chụp từ camera trước\" /></a></p>\r\n\r\n<p>Camera selfie c&oacute; độ ph&acirc;n giải 32 MP cho khả năng chụp ảnh r&otilde; r&agrave;ng v&agrave; sắc n&eacute;t. Hỗ trợ c&aacute;c t&iacute;nh năng l&agrave;m đẹp v&agrave; quay phim chất lượng. Bạn chỉ cần tạo kiểu, c&ograve;n lại bạn h&atilde;y để Find X3 Pro 5G chăm ch&uacute;t cho từng đường n&eacute;t của m&igrave;nh.&nbsp;</p>\r\n\r\n<h3>Trang bị vi&ecirc;n pin th&ocirc;ng minh&nbsp;</h3>\r\n\r\n<p>OPPO Find X3 Pro 5G sử dụng vi&ecirc;n pin đi k&egrave;m nhiều chức năng hỗ trợ để c&oacute; được hiệu suất tốt nhất đến người d&ugrave;ng. Dung lượng pin 4500 mAh kh&aacute; ổn trong thời điểm hiện tại, cung cấp năng lượng phục vụ nhu cầu người d&ugrave;ng thoải m&aacute;i cả ng&agrave;y d&agrave;i.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/232190/oppo-find-x3-pro-5g-006.jpg\" onclick=\"return false;\"><img alt=\"OPPO Find X3 Pro 5G | Dung lượng pin 4500 mAh\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-5g-006.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-5g-006.jpg\" title=\"OPPO Find X3 Pro 5G | Dung lượng pin 4500 mAh\" /></a></p>\r\n\r\n<p>Find X3 Pro 5G hỗ trợ&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo các sản phẩm điện thoại có sạc pin nhanh tại Thế Giới Di Động\">sạc nhanh</a>&nbsp;c&ocirc;ng suất đến 65 W, cho khả năng sạc được 40% chỉ trong v&ograve;ng 10 ph&uacute;t. Củ sạc nhanh khiến biết bao kh&aacute;ch h&agrave;ng y&ecirc;u th&iacute;ch sự giải tr&iacute;, với một vi&ecirc;n pin chất lượng kết hợp với một hiệu năng xuất sắc.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/42/232190/oppo-find-x3-pro-12.jpg\" onclick=\"return false;\"><img alt=\"OPPO Find X3 Pro 5G | Hỗ trợ sạc nhanh công suất đến 65 W\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-12.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-12.jpg\" title=\"OPPO Find X3 Pro 5G | Hỗ trợ sạc nhanh công suất đến 65 W\" /></a></p>\r\n\r\n<p>OPPO lần n&agrave;y đ&atilde; cho ra mắt một sản phẩm cao cấp cả về kiểu d&aacute;ng ấn tượng lẫn hiệu năng tuyệt vời. OPPO Find X3 Pro 5G hứa hẹn đưa t&ecirc;n tuổi OPPO đến nhiều người d&ugrave;ng với thiết kế độc đ&aacute;o, đậm chất ri&ecirc;ng v&agrave; chất lượng phần mềm mạnh mẽ.</p>\r\n', 10, 'oppo-find-x3-pro-5g-21.html', '', '', 'https://cdn.tgdd.vn/Products/Images/42/232190/oppo-find-x3-pro-black-001-1-600x600.jpg', '2', 1626005847, 1626006379, 1, 3);
INSERT INTO `tbl_product` (`product_id`, `product_code`, `product_name`, `product_price`, `old_price`, `product_desc`, `content`, `qty`, `slug`, `creator`, `editor`, `product_thumb`, `product_status`, `add_date`, `update_date`, `cat_id`, `child_id`) VALUES
(22, '#IMMSI22', 'MSI GL65 Leopard', 23240000, 24990000, '<p>15.6\", Full HD</p>\r\n\r\n<p>CPU: Intel Core i7 Comet Lake, 2.60 GHz</p>\r\n\r\n<p>Card: NVIDIA GeForce GTX 1650 4 GB</p>\r\n\r\n<p>Nặng: 2.3 kg, 6-cell Li-ion, 51 Wh</p>\r\n', '<h3><a href=\"https://www.thegioididong.com/laptop/msi-gl65-leopard-10scxk-i7-217vn\" target=\"_blank\" title=\"MSI GL65 Leopard 10SCXK i7 (217VN) \">MSI GL65 Leopard 10SCXK i7 (217VN)</a>&nbsp;sở hữu thiết kế gaming hiện đại v&agrave; sức mạnh vượt trội của bộ vi xử l&yacute; Intel Core i7 d&ograve;ng H sẵn s&agrave;ng đồng h&agrave;nh c&ugrave;ng bạn trong mọi cuộc chiến.&nbsp;</h3>\r\n\r\n<h3>Thiết kế gaming ấn tượng&nbsp;</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm một số laptop đang được bán tại Thegioididong.com\">Laptop</a>&nbsp;sở hữu thiết kế mỏng nhẹ hiện đại nhưng kh&ocirc;ng k&eacute;m phần mạnh mẽ, c&aacute; t&iacute;nh với tone đen - đỏ đặc trưng của d&ograve;ng.&nbsp;</p>\r\n\r\n<p>Nắp lưng v&agrave; chiếu nghỉ tay bằng kim loại tạo cảm gi&aacute;c sang trọng, cứng c&aacute;p hơn m&agrave; vẫn đảm bảo trọng lượng nhẹ cho m&aacute;y. Th&acirc;n m&aacute;y d&agrave;y&nbsp;<strong>27.5 mm</strong>, nặng&nbsp;<strong>2.3 kg</strong>&nbsp;cho bạn dễ d&agrave;ng dịch chuyển hằng ng&agrave;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-2-1.jpg\" onclick=\"return false;\"><img alt=\"MSI GL65 Leopard 10SCXK i7 10750H (217VN) - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-2-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-2-1.jpg\" title=\"MSI GL65 Leopard 10SCXK i7 10750H (217VN) - Thiết kế\" /></a></p>\r\n\r\n<p>M&aacute;y sử dụng&nbsp;<strong>b&agrave;n ph&iacute;m RGB</strong>&nbsp;chuy&ecirc;n dụng cho laptop gaming c&oacute; khả năng chuyển m&agrave;u cho từng ph&iacute;m để người d&ugrave;ng c&oacute; thể tự do điều chỉnh ph&ugrave; hợp với sở th&iacute;ch của m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-7-1.jpg\" onclick=\"return false;\"><img alt=\"MSI GL65 Leopard 10SCXK i7 10750H (217VN) - Bàn phím\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-7-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-7-1.jpg\" title=\"MSI GL65 Leopard 10SCXK i7 10750H (217VN) - Bàn phím\" /></a></p>\r\n\r\n<p>Hệ thống tản nhiệt&nbsp;<strong>Cooler Boost 5</strong>&nbsp;mới nhất của MSI với hệ thống c&aacute;nh quạt Whirlwind cho lưu lượng gi&oacute; cao v&agrave; &ecirc;m. B&ecirc;n cạnh đ&oacute; l&agrave; 4 khe tho&aacute;t kh&iacute; c&ugrave;ng với 7 ống đồng dẫn nhiệt cao cấp gi&uacute;p giảm nhiệt độ cho m&aacute;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-8-1.jpg\" onclick=\"return false;\"><img alt=\"MSI GL65 Leopard 10SCXK i7 10750H (217VN) - Tản nhiệt\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-8-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-8-1.jpg\" title=\"MSI GL65 Leopard 10SCXK i7 10750H (217VN) - Tản nhiệt\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop-msi?g=laptop-gaming\" target=\"_blank\" title=\"Xem thêm một số laptop MSI đang được bán tại Thegioididong.com\">Laptop MSI</a>&nbsp;được trang bị những cổng kết nối th&ocirc;ng dụng hiện nay như USB 3.2, HDMI, LAN (RJ45), Mini DisplayPort v&agrave; USB Type-C gi&uacute;p tiện lợi trong việc trao đổi th&ocirc;ng tin sang thiết bị kh&aacute;c như m&agrave;n h&igrave;nh, m&aacute;y chiếu, chuột, b&agrave;n ph&iacute;m rời,...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-12-1.jpg\" onclick=\"return false;\"><img alt=\"MSI GL65 Leopard 10SCXK i7 10750H (217VN) - Cổng kết nối\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-12-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-12-1.jpg\" title=\"MSI GL65 Leopard 10SCXK i7 10750H (217VN) - Cổng kết nối\" /></a></p>\r\n\r\n<p>Chuẩn kh&ocirc;ng d&acirc;y Bluetooth 5.1 v&agrave; Wi-Fi 6 AX201 cho tốc độ đường truyền nhanh, hạn chế rớt mạng để bạn c&oacute; thể l&agrave;m việc, chơi game online thoải m&aacute;i kể cả ở những nơi nhiều người kết nối.</p>\r\n\r\n<h3>Hiệu năng mạnh mẽ - c&acirc;n mọi tựa game</h3>\r\n\r\n<p>Laptop Lenovo sử dụng chip&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=core-i7\" target=\"_blank\" title=\"Xem thêm một số laptop CPU Core i7 đang được bán tại Thegioididong.com\">Core i7</a>&nbsp;<strong>Comet Lake thế hệ thứ 10 d&ograve;ng H</strong>&nbsp;với&nbsp;<strong>6 nh&acirc;n 12 luồng</strong>&nbsp;mang đến cho bạn hiệu năng vượt trội tới&nbsp;<strong>10%</strong>&nbsp;so với vi xử l&yacute; tiền nhiệm, l&agrave;m việc mạnh mẽ, từ t&aacute;c vụ văn ph&ograve;ng, đồ họa đến chơi game đều được &ldquo;c&acirc;n&rdquo; với xung nhịp cao nhất đạt<strong>&nbsp;5.0 GHz.</strong></p>\r\n\r\n<p>Đi k&egrave;m l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/laptop-8-gb\" target=\"_blank\" title=\"Xem thêm một số laptop RAM 8 GB đang được bán tại Thegioididong.com\">RAM 8 GB</a>&nbsp;DDR4 hỗ trợ đa nhiệm mượt m&agrave;, sử dụng tốt c&aacute;c phần mềm thiết kế như Photoshop, Ai, Pr một c&aacute;ch trơn tru, hạn chế giật lag. M&aacute;y c&ograve;n cho ph&eacute;p n&acirc;ng cấp tối đa l&ecirc;n đến&nbsp;<strong>64 GB RAM</strong>.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-4-1.jpg\" onclick=\"return false;\"><img alt=\"MSI GL65 Leopard 10SCXK i7 10750H (217VN) - Cấu hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-4-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-4-1.jpg\" title=\"MSI GL65 Leopard 10SCXK i7 10750H (217VN) - Cấu hình\" /></a></p>\r\n\r\n<p>Laptop c&ograve;n trang bị cho m&igrave;nh<strong>&nbsp;</strong><a href=\"https://www.thegioididong.com/laptop-card-do-hoa-roi\" target=\"_blank\" title=\"Xem thêm một số laptop có Card đồ họa rời đang được bán tại Thegioididong.com\">card đồ họa rời</a><strong>&nbsp;<a href=\"https://www.thegioididong.com/laptop-geforce-gtx\" target=\"_blank\" title=\"Xem thêm laptop trang bị GeForce GTX đang bán tại thegioididong.com\">NVIDIA GeForce GTX</a>&nbsp;1650 4 GB</strong>&nbsp;mang đến hiệu năng vượt trội để chơi game, l&agrave;m đồ họa hiệu quả hơn. Những h&igrave;nh ảnh trong game cũng được thể hiện sống động, đổ b&oacute;ng, phản chiếu &aacute;nh s&aacute;ng thật hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-5-1.jpg\" onclick=\"return false;\"><img alt=\"MSI GL65 Leopard 10SCXK i7 10750H (217VN) - Card đồ hoạ\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-5-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-5-1.jpg\" title=\"MSI GL65 Leopard 10SCXK i7 10750H (217VN) - Card đồ hoạ\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop?g=ssd-512-gb\" target=\"_blank\" title=\"Xem thêm một số laptop có ổ cứng SSD 512 GB đang được bán tại Thegioididong.com\">Ổ cứng SSD 512 GB</a>&nbsp;<strong>NVMe PCIe</strong>&nbsp;cung cấp kh&ocirc;ng gian lưu trữ rộng v&agrave; tốc độ đọc ghi nhanh gi&uacute;p c&aacute;c thao t&aacute;c diễn ra nhanh ch&oacute;ng nhất. Hỗ trợ khe cắm HDD SATA cho người d&ugrave;ng muốn n&acirc;ng khả năng lưu trữ của m&aacute;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-6-1.jpg\" onclick=\"return false;\"><img alt=\"MSI GL65 Leopard 10SCXK i7 10750H (217VN) - SSD\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-6-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-6-1.jpg\" title=\"MSI GL65 Leopard 10SCXK i7 10750H (217VN) - SSD\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh 144 Hz hiển thị chuyển động mượt m&agrave;</h3>\r\n\r\n<p>Laptop mang đến kh&ocirc;ng gian giải tr&iacute; tuyệt vời với&nbsp;<a href=\"https://www.thegioididong.com/laptop-tren-15-inch\" target=\"_blank\" title=\"Xem thêm một số laptop màn hình trên 15 inch đang được bán tại Thegioididong.com\">m&agrave;n h&igrave;nh 15.6 inch</a>&nbsp; độ ph&acirc;n giải&nbsp;<strong>Full HD (1920 x 1080)&nbsp;</strong>c&ugrave;ng tấm nền IPS&nbsp;sẽ khiến bạn kinh ngạc bởi g&oacute;c nh&igrave;n rộng, khả năng t&aacute;i tạo m&agrave;u sắc trung thực, ph&ugrave; hợp để chỉnh sửa ảnh v&agrave; giải tr&iacute;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-9-1.jpg\" onclick=\"return false;\"><img alt=\"MSI GL65 Leopard 10SCXK i7 10750H (217VN) - Màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-9-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-9-1.jpg\" title=\"MSI GL65 Leopard 10SCXK i7 10750H (217VN) - Màn hình\" /></a></p>\r\n\r\n<p>C&aacute;c game thủ sẽ c&oacute; được trải nghiệm chơi c&aacute;c tựa game tốc độ cực đ&atilde; nhờ c&oacute; tần số qu&eacute;t&nbsp;<strong>144 Hz</strong>&nbsp;giảm hiện tượng giật lag, quan s&aacute;t r&otilde; mọi chuyển động của nh&acirc;n vật. M&agrave;n h&igrave;nh cũng c&oacute; c&ocirc;ng nghệ chống ch&oacute;i&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-man-hinh-chong-choi-anti-glare-1182688\" target=\"_blank\" title=\"Tìm hiểu thêm về công nghệ chống chói Anti Glare\">Anti Glare</a>&nbsp;gi&uacute;p bạn sử dụng tiện lợi ở mọi kh&ocirc;ng gian, kể cả ngo&agrave;i trời hay trong nh&agrave;.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-10-1.jpg\" onclick=\"return false;\"><img alt=\"MSI GL65 Leopard 10SCXK i7 10750H (217VN) - Tần số quét\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-10-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-10-1.jpg\" title=\"MSI GL65 Leopard 10SCXK i7 10750H (217VN) - Tần số quét\" /></a></p>\r\n\r\n<p>C&ocirc;ng nghệ&nbsp;<strong>Realtek High Definition Audio</strong>&nbsp;cho &acirc;m thanh to r&otilde;, trong trẻo, kh&ocirc;ng bị vỡ tiếng d&ugrave; bạn bật &acirc;m lượng lớn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-11-1.jpg\" onclick=\"return false;\"><img alt=\"MSI GL65 Leopard 10SCXK i7 10750H (217VN) - Âm thanh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-11-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-11-1.jpg\" title=\"MSI GL65 Leopard 10SCXK i7 10750H (217VN) - Âm thanh\" /></a></p>\r\n\r\n<p>Laptop MSI GL65 Leopard 10SCXK i7 (217VN) l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=laptop-gaming\" target=\"_blank\" title=\"Xem thêm một số mẫu laptop Gaming đang được bán tại Thegioididong.com\">laptop gaming</a>&nbsp;được thiết kế sang trọng, hiệu năng vượt trội l&agrave; một thiết bị mang lại trải nghiệm chơi game, l&agrave;m đồ họa mượt m&agrave;.</p>\r\n', 1, 'mssi-gl63-leopard-10scxk-i7-10750h-217vn-22.html', '', '', 'https://cdn.tgdd.vn/Products/Images/44/240518/msi-gl65-leopard-10scxk-i7-217vn-600x600.jpg', '2', 1626006653, 1626006681, 2, 8),
(23, '#IMAS23', 'Asus ZenBook UX425EA i5 ', 24190000, NULL, '<p>14&quot;, Full HD</p>\r\n\r\n<p>CPU: Intel Core i5 Tiger Lake, 2.40 GHz</p>\r\n\r\n<p>Card: Intel Iris Xe Graphics</p>\r\n\r\n<p>Nặng: 1.17 kg, 4-cell Li-ion, 67 Wh</p>\r\n', '<h3><a href=\"https://www.thegioididong.com/laptop/asus-zenbook-ux425ea-i5-ki429t\" target=\"_blank\" title=\"Laptop Asus ZenBook UX425EA i5 1135G7/8GB/512GBCáp/Túi/Win10 (KI429T) \">Laptop Asus Zenbook UX425EA (KI429T)&nbsp;</a>sở hữu vẻ ngo&agrave;i tinh tế với độ bền chuẩn qu&acirc;n đội c&ugrave;ng&nbsp;<a href=\"https://www.thegioididong.com/laptop-cpu-intel-gen-11\" target=\"_blank\" title=\"Xem thêm laptop CPU thế hệ 11 đang bán tại thegioididong.com\">CPU Intel thế hệ 11</a>&nbsp;hiện đại đem đến hiệu năng tối ưu v&agrave; vượt trội, xử l&yacute; nhanh gọn mọi t&aacute;c vụ.</h3>\r\n\r\n<h3>D&aacute;ng vẻ thanh lịch, gọn nhẹ với sắc x&aacute;m thời thượng</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop-asus\" target=\"_blank\" title=\"Một số laptop Asus được kinh doanh tại thegioididong.com\">Asus</a>&nbsp;đ&atilde; mang đến thế hệ laptop c&oacute; thiết kế nhỏ gọn với trọng lượng nhẹ chỉ&nbsp;<strong>1.17 kg</strong>&nbsp;v&agrave; mỏng<strong>&nbsp;13.9 mm</strong>, l&agrave; bạn đồng h&agrave;nh l&iacute; tưởng cho bất k&igrave; ai tiện lợi mang đi học tập, l&agrave;m việc, đ&aacute;p ứng tốt mọi nhu cầu ở mọi kh&ocirc;ng gian.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/241721/asus-zenbook-ux425ea-i5-ki429t-2-1.jpg\" onclick=\"return false;\"><img alt=\"Asus ZenBook UX425EA i5 1135G7 (KI429T) - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-2-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-2-1.jpg\" title=\"Asus ZenBook UX425EA i5 1135G7 (KI429T) - Thiết kế\" /></a></p>\r\n\r\n<p>Đặc biệt, phi&ecirc;n bản&nbsp;<a href=\"https://www.thegioididong.com/laptop-asus-zenbook\" target=\"_blank\" title=\"Một số laptop Asus Zenbook được kinh doanh tại thegioididong.com\">Asus Zenbook</a>&nbsp;n&agrave;y đ&atilde; vượt qua h&agrave;ng loạt b&agrave;i kiểm tra khắc khe nhằm đ&aacute;p ứng ti&ecirc;u chuẩn&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tieu-chuan-quan-su-my-mil-std-810g-1304935\" target=\"_blank\" title=\"Tìm hiểu thêm về tiêu chuẩn quân sự Mỹ MIL-STD-810G trên laptop có ý nghĩa gì?\">MIL-STD-810G</a>&nbsp;của qu&acirc;n đội Hoa Kỳ về t&iacute;nh bền bỉ v&agrave; ổn định, trở th&agrave;nh chiếc laptop bền chắc trước những va chạm hằng ng&agrave;y trong qu&aacute; trinh di chuyển.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/241721/asus-zenbook-ux425ea-i5-ki429t-10-1.jpg\" onclick=\"return false;\"><img alt=\"Asus ZenBook UX425EA i5 1135G7 (KI429T) - Độ bền quân đội\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-10-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-10-1.jpg\" title=\"Asus ZenBook UX425EA i5 1135G7 (KI429T) - Độ bền quân đội\" /></a></p>\r\n\r\n<p>B&agrave;n ph&iacute;m được trang bị&nbsp;<strong>đ&egrave;n nền</strong>&nbsp;với thiết kế tr&agrave;n viền mới c&ugrave;ng c&aacute;c ph&iacute;m chức năng ở h&agrave;ng tr&ecirc;n v&agrave; b&ecirc;n phải gi&uacute;p người d&ugrave;ng thuận tiện thực hiện c&aacute;c thao t&aacute;c nhanh như chụp m&agrave;n h&igrave;nh, mở ứng dụng MyAsus,...</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/241721/asus-zenbook-ux425ea-i5-ki429t-8-1.jpg\" onclick=\"return false;\"><img alt=\"Asus ZenBook UX425EA i5 1135G7 (KI429T) - Đèn nền\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-8-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-8-1.jpg\" title=\"Asus ZenBook UX425EA i5 1135G7 (KI429T) - Đèn nền\" /></a></p>\r\n\r\n<p>Đột ph&aacute; hơn c&ugrave;ng&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/numberpad-la-gi-1181174\" target=\"_blank\" title=\"Tìm hiểu thêm về bàn phím ảo NumberPad trên laptop\">c&ocirc;ng nghệ NumberPad 2.0</a>, b&agrave;n di chuột sở hữu chức năng k&eacute;p với v&ugrave;ng số ảo được chiếu s&aacute;ng bằng đ&egrave;n LED t&iacute;ch hợp, tiện lợi cho người d&ugrave;ng trong việc vừa nhập c&aacute;c dữ liệu số vừa điều khiển con trỏ, đẩy nhanh hiệu năng c&ocirc;ng việc.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/241721/asus-zenbook-ux425ea-i5-ki429t-7-1.jpg\" onclick=\"return false;\"><img alt=\"Asus ZenBook UX425EA i5 1135G7 (KI429T) - NumberPad 2.0\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-7-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-7-1.jpg\" title=\"Asus ZenBook UX425EA i5 1135G7 (KI429T) - NumberPad 2.0\" /></a></p>\r\n\r\n<p>Chất lượng &acirc;m thanh được n&acirc;ng cao đ&aacute;ng kể v&agrave; khả năng l&agrave;m m&aacute;t tối ưu nhờ v&agrave;o bản lề&nbsp;<strong>ErgoLift</strong>&nbsp;được chế t&aacute;c tinh tế, ch&iacute;nh x&aacute;c gi&uacute;p tự n&acirc;ng phần sau của laptop l&ecirc;n<strong>&nbsp;2.5 độ</strong>&nbsp;khi mở nắp m&aacute;y, cải thiện vị tr&iacute; g&otilde; ph&iacute;m cho người d&ugrave;ng, tạo cảm gi&aacute;c thoải m&aacute;i khi soạn thảo.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/241721/asus-zenbook-ux425ea-i5-ki429t-6-1.jpg\" onclick=\"return false;\"><img alt=\"Asus ZenBook UX425EA i5 1135G7 (KI429T) - Bản lề ErgoLift\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-6-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-6-1.jpg\" title=\"Asus ZenBook UX425EA i5 1135G7 (KI429T) - Bản lề ErgoLift\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Một số laptop được kinh doanh tại thegioididong.com\">Laptop</a>&nbsp;trở n&ecirc;n linh hoạt với c&aacute;c chuẩn kết nối hiện đại: 2 cổng Thunderbolt 4 USB-C với tốc độ truyền tải l&ecirc;n tới&nbsp;<strong>40 Gbps</strong>, cổng USB 3.2, HDMI c&ugrave;ng đầu đọc thẻ Micro SD hỗ trợ tốt nhu cầu truyền xuất h&igrave;nh ảnh v&agrave; dữ liệu của bạn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/241721/asus-zenbook-ux425ea-i5-ki429t-13.jpg\" onclick=\"return false;\"><img alt=\"Asus ZenBook UX425EA i5 1135G7 (KI429T) - Cổng kết nối\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-13.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-13.jpg\" title=\"Asus ZenBook UX425EA i5 1135G7 (KI429T) - Cổng kết nối\" /></a></p>\r\n\r\n<p>Tốc độ truyền tải dữ liệu vượt trội nhờ chuẩn kết nối kh&ocirc;ng d&acirc;y&nbsp;<strong>Wi-Fi 6 AX201&nbsp;</strong>c&ugrave;ng&nbsp;<strong>Bluetooth 5.0</strong>&nbsp;cho khả năng kết nối kh&ocirc;ng d&acirc;y với c&aacute;c thiết bị ngoại vi kh&aacute;c nhanh ch&oacute;ng, đảm bảo đường truyền ổn định, tr&aacute;nh gi&aacute;n đoạn khi đang l&agrave;m việc hay giải tr&iacute;.</p>\r\n\r\n<h3>T&acirc;n tiến v&agrave; hiện đại c&ugrave;ng chip Intel Gen 11</h3>\r\n\r\n<p>Bộ vi xử l&yacute;&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=core-i5\" target=\"_blank\" title=\"Một số laptop có bộ vi xử ký Intel Core i5 được kinh doanh tại thegioididong.com\">Intel Core i5</a>&nbsp;<strong>1135G7</strong>&nbsp;với tốc độ&nbsp;<strong>2.40 GHz</strong>&nbsp;v&agrave; đạt tối đa đến&nbsp;<strong>4.2 GHz</strong>&nbsp;nhờ&nbsp;<strong>Turbo Boost</strong>&nbsp;đem đến hiệu năng mạnh mẽ cho chiếc laptop Asus n&agrave;y, xử l&yacute; nhanh ch&oacute;ng c&aacute;c t&aacute;c vụ văn ph&ograve;ng như Word, Excel,...</p>\r\n\r\n<p>Đồng thời card đồ họa t&iacute;ch hợp&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-card-do-hoa-tich-hop-intel-iris-xe-graphics-1305192\" target=\"_blank\" title=\"Tìm hiểu về card đồ họa tích hợp Intel Iris Xe Graphics\">Intel Iris Xe Graphics</a>&nbsp;xử l&yacute; tốt&nbsp;chất lượng h&igrave;nh ảnh sắc n&eacute;t l&ecirc;n đến chuẩn&nbsp;<strong>8K</strong>, thỏa m&atilde;n người d&ugrave;ng trong thế giới game hấp dẫn hay đ&aacute;p ứng mọi t&aacute;c vụ chỉnh sửa h&igrave;nh ảnh với c&aacute;c ứng dụng Photoshop, Ai,...</p>\r\n\r\n<p>Sự hỗ trợ của&nbsp;<a href=\"https://www.thegioididong.com/laptop-8-gb\" target=\"_blank\" title=\"Một số laptop sở hữu RAM 8 GB được kinh doanh tại thegioididong.com\">RAM 8 GB</a>&nbsp;<strong>LPDDR4X</strong>&nbsp;với tốc bộ Bus RAM đến&nbsp;<strong>4266 MHz</strong>&nbsp;cho khả năng đa nhiệm tốt nhiều t&aacute;c vụ kh&aacute;c nhau, chuyển đổi mượt m&agrave; giữa c&aacute;c cửa sổ ứng dụng m&agrave; kh&ocirc;ng lo giật lag m&aacute;y.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/241721/asus-zenbook-ux425ea-i5-ki429t-4-1.jpg\" onclick=\"return false;\"><img alt=\"Asus ZenBook UX425EA i5 1135G7 (KI429T) - Cấu hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-4-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-4-1.jpg\" title=\"Asus ZenBook UX425EA i5 1135G7 (KI429T) - Cấu hình\" /></a></p>\r\n\r\n<p>Kh&ocirc;ng gian bộ nhớ rộng lớn với&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=ssd-512-gb\" target=\"_blank\" title=\"Một số laptop sở hữu SSD 512 GB được kinh doanh tại thegioididong.com\">SSD 512 GB</a>&nbsp;<strong>NVMe PCle</strong>&nbsp;cho bạn lưu trữ nhiều dữ liệu, h&igrave;nh ảnh hay download phần mềm, đồng thời tiết kiệm tối đa thời gian khởi động m&aacute;y v&agrave; vận h&agrave;nh phần mềm.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/241721/asus-zenbook-ux425ea-i5-ki429t-5-1.jpg\" onclick=\"return false;\"><img alt=\"Asus ZenBook UX425EA i5 1135G7 (KI429T) - SSD\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-5-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-5-1.jpg\" title=\"Asus ZenBook UX425EA i5 1135G7 (KI429T) - SSD\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh NanoEdge với h&igrave;nh ảnh sắc n&eacute;t c&ugrave;ng độ s&aacute;ng đ&aacute;ng kinh ngạc</h3>\r\n\r\n<p>Chiếc Asus n&agrave;y sở hữu m&agrave;n h&igrave;nh&nbsp;<a href=\"https://www.thegioididong.com/laptop-14-inch\" target=\"_blank\" title=\"Một số laptop có màn hình 14 inch được kinh doanh tại thegioididong.com\">14 inch</a>&nbsp;viền mỏng&nbsp;<strong>100% sRGB&nbsp;</strong>với độ ph&acirc;n giải Full HD (1920 x 1080) cho bạn tận hưởng những giờ ph&uacute;t giải tr&iacute; v&agrave; l&agrave;m việc c&ugrave;ng khung ảnh r&otilde; n&eacute;t, m&agrave;u sắc ch&acirc;n thật nhưng vẫn tiết kiệm điện năng tối đa nhờ c&ocirc;ng nghệ<strong>&nbsp;LED Backlit</strong>.</p>\r\n\r\n<p><strong>Tấm nền IPS&nbsp;</strong>đem đến g&oacute;c nh&igrave;n rộng l&ecirc;n đến&nbsp;<strong>178 độ&nbsp;</strong>v&agrave;&nbsp;độ s&aacute;ng m&agrave;n h&igrave;nh&nbsp;<strong>400 nits</strong>&nbsp;cho ph&eacute;p người d&ugrave;ng sử dụng laptop ở nơi c&oacute; cường độ &aacute;nh s&aacute;ng cao với tầm nh&igrave;n bao qu&aacute;t hơn.</p>\r\n\r\n<p>C&ocirc;ng nghệ chống ch&oacute;i&nbsp;<a href=\"https://www.thegioididong.com/laptop-man-hinh-chong-choi\" target=\"_blank\" title=\"Một số laptop sở hữu màn hình chống chói Anti Glare được kinh doanh tại thegioididong.com\">Anti Glare</a>&nbsp;gi&uacute;p loại bỏ b&oacute;ng gương mang đến sự thoải m&aacute;i khi l&agrave;m việc hay giải tr&iacute; trong bất kỳ kh&ocirc;ng gian n&agrave;o m&agrave; kh&ocirc;ng lo mỏi mắt hay l&oacute;a mắt.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/241721/asus-zenbook-ux425ea-i5-ki429t-11.jpg\" onclick=\"return false;\"><img alt=\"Asus ZenBook UX425EA i5 1135G7 (KI429T) - Hình ảnh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-11.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-11.jpg\" title=\"Asus ZenBook UX425EA i5 1135G7 (KI429T) - Hình ảnh\" /></a></p>\r\n\r\n<p>Dễ d&agrave;ng đăng nhập m&agrave; kh&ocirc;ng cần nhập mật khẩu với&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/camera-ir-tren-laptop-la-gi-1269288\" target=\"_blank\" title=\"Tìm hiểu thêm về Camera IR trên laptop\">camera hồng ngoại (IR)&nbsp;</a>t&iacute;ch hợp tr&ecirc;n m&aacute;y cho ph&eacute;p người d&ugrave;ng mở kh&oacute;a bằng khu&ocirc;n mặt nhanh ch&oacute;ng với&nbsp;<strong>Windows Hello</strong>.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/241721/asus-zenbook-ux425ea-i5-ki429t-9-1.jpg\" onclick=\"return false;\"><img alt=\"Asus ZenBook UX425EA i5 1135G7 (KI429T) - Camera IR\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-9-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-9-1.jpg\" title=\"Asus ZenBook UX425EA i5 1135G7 (KI429T) - Camera IR\" /></a></p>\r\n\r\n<p>Chất lượng &acirc;m thanh được chứng nhận bởi&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/audio-by-harman-la-gi-958471\" target=\"_blank\" title=\"Tìm hiểu thêm về công nghệ âm thanh Harman/Kardon\">Harman Kardon</a>&nbsp;cho người d&ugrave;ng thưởng thức những giai điệu trong trẻo, đem đến kh&ocirc;ng gian thư gi&atilde;n c&ugrave;ng &acirc;m nhạc v&agrave; phim ảnh.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/241721/asus-zenbook-ux425ea-i5-ki429t-12.jpg\" onclick=\"return false;\"><img alt=\"Asus ZenBook UX425EA i5 1135G7 (KI429T) - Hình ảnh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-12.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-12.jpg\" title=\"Asus ZenBook UX425EA i5 1135G7 (KI429T) - Hình ảnh\" /></a></p>\r\n\r\n<p>Laptop Asus Zenbook UX425EA (KI429T) l&agrave; phi&ecirc;n bản laptop&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=mong-nhe-thoi-trang\" target=\"_blank\" title=\"Một số laptop mỏng nhẹ được kinh doanh tại thegioididong.com\">mỏng nhẹ</a>, thiết kế thanh lịch c&ugrave;ng hiệu năng vượt trội cho mọi t&aacute;c vụ l&agrave; sự lựa chọn ph&ugrave; hợp cho hầu hết tất cả mọi người.</p>\r\n', 2, 'asus-zenbook-ux425ea-i5-1135g7-kl429t-23.html', '', '', 'https://cdn.tgdd.vn/Products/Images/44/241721/asus-zenbook-ux425ea-i5-ki429t-600x600.jpg', '2', 1626006863, NULL, 2, 6),
(24, '#IMMB24', 'MacBook Air 2017 ', 19900000, NULL, '<p>13.3&quot;, Full HD</p>\r\n\r\n<p>CPU: Intel Core i5 Broadwell, 1.80 GHz</p>\r\n\r\n<p>Card: Intel HD Graphics 6000</p>\r\n\r\n<p>Nặng: 1.35 Kg, Khoảng 12 giờ</p>\r\n', '<h2><a href=\"https://www.thegioididong.com/laptop/apple-macbook-air-mqd32sa-a-i5-5350u\" target=\"_blank\" title=\"Xem chi tiết về sản phẩm MacBook Air 2017 i5 128 GB\" type=\"Xem chi tiết về sản phẩm MacBook Air 2017 i5 128 GB\">MacBook Air 2017 i5 128GB</a>&nbsp;l&agrave; mẫu&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=hoc-tap-van-phong\" target=\"_blank\" title=\"Xem thêm các mẫu laptop văn phòng đang kinh doanh\" type=\"Xem thêm các mẫu laptop văn phòng đang kinh doanh\">laptop văn ph&ograve;ng</a>, c&oacute; thiết kế si&ecirc;u mỏng v&agrave; nhẹ, vỏ nh&ocirc;m nguy&ecirc;n khối sang trọng. M&aacute;y c&oacute; hiệu năng ổn định, thời lượng pin cực l&acirc;u 12 giờ, ph&ugrave; hợp cho hầu hết c&aacute;c nhu cầu l&agrave;m việc v&agrave; giải tr&iacute;.&nbsp;</h2>\r\n\r\n<h3>Thiết kế si&ecirc;u mỏng v&agrave; nhẹ&nbsp;</h3>\r\n\r\n<p>Macbook Air 2017 mang những đặc trưng thiết kế của d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook-air\" target=\"_blank\" title=\"Xem thêm các mẫu MacBook Air khác\" type=\"Xem thêm các mẫu MacBook Air khác\">MacBook Air</a>&nbsp;với trọng lượng v&agrave; độ d&agrave;y của&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Tìm hiểu các dòng laptop khác tại Thegioididong\" type=\"Tìm hiểu các dòng laptop khác tại Thegioididong\">laptop&nbsp;</a>lần lượt l&agrave;&nbsp;<strong>1.7 cm</strong>&nbsp;v&agrave;&nbsp;<strong>1.35 kg</strong>&nbsp;rất tiện lợi v&agrave; dễ d&agrave;ng gi&uacute;p người d&ugrave;ng kh&ocirc;ng cảm thấy bất tiện khi mang tr&ecirc;n vai thường xuy&ecirc;n khi đi học hay đi l&agrave;m.&nbsp;</p>\r\n\r\n<p>Đ&acirc;y cũng l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\" title=\"Xem những mẫu MacBook khác\" type=\"Xem những mẫu MacBook khác\">MacBook ch&iacute;nh h&atilde;ng</a>&nbsp;c&oacute; gi&aacute; rẻ nhất hiện tại, ph&ugrave; hợp với mọi người ti&ecirc;u d&ugrave;ng.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-4.jpg\" onclick=\"return false;\"><img alt=\"Hình mô tả thiết kế Laptop MacBook Air 2017 i5 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-4.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-4.jpg\" title=\"Hình mô tả thiết kế Laptop MacBook Air 2017 i5 128GB\" /></a></p>\r\n\r\n<h3>Hiệu năng mượt m&agrave;</h3>\r\n\r\n<p>Macbook Air MQD32SA/A i5 5350U c&oacute; bộ xử l&yacute; Intel&nbsp;<strong>Core i5</strong>&nbsp;Broadwell hai nh&acirc;n xung nhịp 1.8 GHz, bộ nhớ&nbsp;<strong>RAM 8 GB</strong>&nbsp;thoải m&aacute;i trong việc duyệt web, chơi game, sử dụng mượt m&agrave; c&aacute;c thao t&aacute;c trong phần mềm&nbsp;<a href=\"https://www.thegioididong.com/phan-mem-microsoft-office\" target=\"_blank\" title=\"Tham khảo phần mềm Office tại Thế giới di động\">Office</a>, Photoshop, AI&hellip;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-1-1.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng mượt mà của laptop Macbook Air MQD32SA/A i5 5350U\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-1-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-1-1.jpg\" title=\"Hiệu năng mượt mà của laptop Macbook Air MQD32SA/A i5 5350U\" /></a></p>\r\n\r\n<p>Ổ cứng lưu trữ&nbsp;<strong><a href=\"https://www.thegioididong.com/laptop-apple-macbook-o-cung-ssd\" target=\"_blank\" title=\"Xem thêm Laptop MacBook có SSD đang bán tại Thegioididong.com\">SSD</a>&nbsp;128 GB</strong>&nbsp;cho tốc độ cực nhanh, thời gian để bạn khởi động m&aacute;y chỉ khoảng 10-15 gi&acirc;y, dung lượng 128 GB kh&ocirc;ng qu&aacute; dư giả, nhưng vẫn đủ để lưu trữ c&aacute;c t&agrave;i liệu, h&igrave;nh ảnh, ứng dụng thường d&ugrave;ng.</p>\r\n\r\n<h3>Thời lượng pin cực l&acirc;u</h3>\r\n\r\n<p>Macbook Air MQD32SA/A i5 5350U cho thời lượng sử dụng l&ecirc;n đến&nbsp;<strong>12 tiếng</strong>&nbsp;sau một lần sạc đầy khi sử dụng soạn thảo v&agrave; lướt web th&ocirc;ng thường.&nbsp;</p>\r\n\r\n<p>Với c&aacute;c t&aacute;c vụ nặng hơn như Photoshop, lướt web từ 10-20 tab, sử dụng c&aacute;c ứng dụng nhắn tin, gọi video khi l&agrave;m việc th&igrave; chiếc m&aacute;y vẫn trụ được khoảng&nbsp;<strong>5-7 tiếng li&ecirc;n tục</strong>.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u5.jpg\" onclick=\"return false;\"><img alt=\"Laptop Macbook Air MQD32SA/A i5 5350U\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u5.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u5.jpg\" title=\"Laptop Macbook Air MQD32SA/A i5 5350U\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh đ&aacute;p ứng nhu cầu sử dụng h&agrave;ng ng&agrave;y</h3>\r\n\r\n<p>Macbook Air MQD32SA/A i5 5350U c&oacute; m&agrave;n h&igrave;nh rộng&nbsp;<strong>13.3 inch</strong>, độ ph&acirc;n giải l&agrave;&nbsp;<strong>1440x900px</strong>.&nbsp;</p>\r\n\r\n<p>Mặc d&ugrave; chưa được n&acirc;ng cấp l&ecirc;n m&agrave;n h&igrave;nh Retina si&ecirc;u n&eacute;t như c&aacute;c d&ograve;ng kh&aacute;c, m&agrave;n h&igrave;nh MacBook Air 2017 vẫn cho ra&nbsp;<strong>m&agrave;u sắc trung thực</strong>, v&agrave; độ&nbsp;<strong>tương phản kh&aacute; tốt</strong>&nbsp;nhờ tấm nền LED Backlit chất lượng cao.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-manhinh.jpg\" onclick=\"return false;\"><img alt=\"Màn hình của laptop Macbook Air MQD32SA/A i5 5350U\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-manhinh.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-manhinh.jpg\" title=\"Màn hình của laptop Macbook Air MQD32SA/A i5 5350U\" /></a></p>\r\n\r\n<h3>Vẫn giữ đầy đủ c&aacute;c cổng kết nối cần thiết</h3>\r\n\r\n<p>Macbook Air MQD32SA/A i5 5350U l&agrave; d&ograve;ng MacBook duy nhất ở hiện tại c&ograve;n trang bị 2 cổng&nbsp;<strong>USB 3.0</strong>,&nbsp;<strong>SD card</strong>&nbsp;gi&uacute;p kh&aacute;ch h&agrave;ng thuận tiện hơn trong việc gắn k&egrave;m phụ kiện như chuột, b&agrave;n ph&iacute;m hay chia sẻ dữ liệu qua USB, thẻ nhớ m&aacute;y ảnh&hellip;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-400-cong-ket-noi.jpg\" onclick=\"return false;\"><img alt=\"Khe cắm của laptop Macbook Air MQD32SA/A i5 5350U\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-400-cong-ket-noi.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-400-cong-ket-noi.jpg\" title=\"Khe cắm của laptop Macbook Air MQD32SA/A i5 5350U\" /></a></p>\r\n\r\n<h3>B&agrave;n r&ecirc; cảm ứng mượt m&agrave;, b&agrave;n ph&iacute;m tối ưu sử dụng</h3>\r\n\r\n<p>Macbook Air MQD32SA/A i5 5350U với b&agrave;n r&ecirc; c&oacute; k&iacute;ch thước&nbsp;<strong>lớn&nbsp;</strong>v&agrave; bề mặt&nbsp;<strong>trơn&nbsp;</strong>nhẵn dễ thao t&aacute;c. Trackpad n&agrave;y c&ograve;n hỗ trợ nhiều thao t&aacute;c sử dụng tiện lợi như: Cuộn, thu ph&oacute;ng, chuyển qua lại giữa c&aacute;c ứng dụng, mở khay ứng dụng nhanh ch&oacute;ng...</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cu-chi-thong-minh-voi-trackpad-tren-mac-os-848490\" target=\"_blank\" title=\"Tổng hợp các cử chỉ thông minh với Trackpad trên MacBook\" type=\"Tổng hợp các cử chỉ thông minh với Trackpad trên MacBook\">Tổng hợp c&aacute;c cử chỉ th&ocirc;ng minh với Trackpad tr&ecirc;n MacBook</a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-400-track-pad.jpg\" onclick=\"return false;\"><img alt=\"Bàn di chuột lớn trên MacBook Air 2017\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-400-track-pad.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-400-track-pad.jpg\" title=\"Bàn di chuột lớn trên MacBook Air 2017\" /></a></p>\r\n\r\n<p>B&agrave;n ph&iacute;m tr&ecirc;n MacBook Air 2017 cũng được trang bị&nbsp;<strong>đ&egrave;n nền</strong>&nbsp;gi&uacute;p bạn dễ d&agrave;ng sử dụng m&aacute;y trong b&oacute;ng tối.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-bac-7-2-org.jpg\" onclick=\"return false;\"><img alt=\"Bàn phím của laptop Macbook Air MQD32SA/A i5 5350U\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-bac-7-2-org.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-bac-7-2-org.jpg\" title=\"Bàn phím của laptop Macbook Air MQD32SA/A i5 5350U\" /></a></p>\r\n', 22, 'macbook-air-2017-mqd32sa-a-24.html', '', '', 'https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-600x600.jpg', '2', 1626007116, NULL, 2, 5);

